				    NO MAXVALUE
				    NO MINVALUE
				    CACHE 1;
				ALTER SEQUENCE core_translation_user_id_seq OWNED BY ###core_translation.translation_id;
				SELECT pg_catalog.setval('core_translation_translation_id_seq', 1, false);

