INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (104, 35, 1, 'Bánh đúc', '', 'banh-duc', '<p>B&aacute;nh đ&uacute;c is a term used to refer to two different varieties of Vietnamese b&aacute;nh, or cake.</p>', '<h2>Northern Vietnamese version</h2>\r\n<p style="text-align: justify;">In northern Vietnam, b&aacute;nh đ&uacute;c is a cake made from either non-glutinous rice flour or corn flour. It is white in color and has a soft texture and mild flavour.photo It is typically garnished with savory ingredients such as ground pork, t&ocirc;m chấy (grilled ground shrimp),photo fried onions, sesame seeds, salt, peanuts, lime juice, and soy sauce or fish sauce.photo Although it may be eaten on its own, it may also be served hot, accompanied by steamed meat or mushrooms.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cdd066d361_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A bowl of northern Vietnamese b&aacute;nh đ&uacute;c thịt (b&aacute;nh đ&uacute;c with ground pork and fish sauce)</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">B&aacute;nh đ&uacute;c is available at small stalls and is eaten throughout the day.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Southern_Vietnamese_version" class="mw-headline">Southern Vietnamese version</span></h2>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A plate of southern Vietnamese b&aacute;nh đ&uacute;c colored with Pandanus amaryllifolius leaf extract, and topped with sweetened coconut milk and sesame seeds</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">In southern Vietnam, b&aacute;nh đ&uacute;c is a dessert made from non-glutinous rice flour. It takes the form of gelatinous blocks that are often colored green by the addition of Pandanus amaryllifolius leaf extract. It is cooked by boiling the ingredients and allowing them to cool, solidifying into a jelly-like sheetphoto that is then cut into blocks.</p>', 'Wikipedia', '', '17:45:31', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (105, 35, 1, 'Bánh đúc', '', 'banh-duc', '<p>B&aacute;nh đ&uacute;c is a term used to refer to two different varieties of Vietnamese b&aacute;nh, or cake.</p>', '<h2>Northern Vietnamese version</h2>\r\n<p style="text-align: justify;">In northern Vietnam, b&aacute;nh đ&uacute;c is a cake made from either non-glutinous rice flour or corn flour. It is white in color and has a soft texture and mild flavour.photo It is typically garnished with savory ingredients such as ground pork, t&ocirc;m chấy (grilled ground shrimp),photo fried onions, sesame seeds, salt, peanuts, lime juice, and soy sauce or fish sauce.photo Although it may be eaten on its own, it may also be served hot, accompanied by steamed meat or mushrooms.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cdd066d361_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A bowl of northern Vietnamese b&aacute;nh đ&uacute;c thịt (b&aacute;nh đ&uacute;c with ground pork and fish sauce)</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">B&aacute;nh đ&uacute;c is available at small stalls and is eaten throughout the day.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Southern_Vietnamese_version" class="mw-headline">Southern Vietnamese version</span></h2>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A plate of southern Vietnamese b&aacute;nh đ&uacute;c colored with Pandanus amaryllifolius leaf extract, and topped with sweetened coconut milk and sesame seeds</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">In southern Vietnam, b&aacute;nh đ&uacute;c is a dessert made from non-glutinous rice flour. It takes the form of gelatinous blocks that are often colored green by the addition of Pandanus amaryllifolius leaf extract. It is cooked by boiling the ingredients and allowing them to cool, solidifying into a jelly-like sheetphoto that is then cut into blocks.</p>', 'Wikipedia', '', '17:46:35', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (106, 36, 17, 'Bánh đúc', '', 'banh-duc', '<p style="text-align: justify;">B&aacute;nh đ&uacute;c l&agrave; một loại b&aacute;nh của Việt Nam, thường được l&agrave;m bằng bột gạo (tại miền Bắc v&agrave; miền Trung) hoặc bột năng (miền Nam) với một số gia vị. B&aacute;nh được l&agrave;m th&agrave;nh tấm to, khi ăn th&igrave; cắt nhỏ th&agrave;nh miếng t&ugrave;y th&iacute;ch.</p>', '<p style="text-align: justify;">L&agrave; m&oacute;n ăn d&acirc;n d&atilde; thịnh h&agrave;nh khắp ba miền, b&aacute;nh đ&uacute;c ăn gi&ograve;n, m&aacute;t, mịn, no bụng m&agrave; lại dễ ti&ecirc;u dễ l&agrave;m v&agrave; gi&aacute; th&agrave;nh cũng rất rẻ. Kh&ocirc;ng chỉ được ăn như một thức qu&agrave; qu&ecirc;, bữa ăn s&aacute;ng m&agrave; điển h&igrave;nh l&agrave; b&aacute;nh đ&uacute;c chấm tương, b&aacute;nh đ&uacute;c cũng c&oacute; thể ăn k&egrave;m với canh ri&ecirc;u cua, rau thơm, mắm t&ocirc;m, mật ong, mật m&iacute;a, mứt tr&aacute;i c&acirc;y v&agrave; thậm ch&iacute; cả c&aacute; kho, thịt kho t&ugrave;y th&iacute;ch. Từ loại b&aacute;nh đ&uacute;c thuần t&uacute;y chỉ được bằng bột gạo pha với nước v&ocirc;i trong ngai ng&aacute;i m&ugrave;i v&ocirc;i, b&aacute;nh đ&uacute;c cũng đ&atilde; c&oacute; nhiều biến tấu như b&aacute;nh đ&uacute;c cẩm thạch, b&aacute;nh đ&uacute;c lạc, b&aacute;nh đ&uacute;c dừa, b&aacute;nh đ&uacute;c ng&ocirc; v.v.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cdd066d361_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>B&aacute;nh đ&uacute;c thịt kiểu miền Bắc</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">B&aacute;nh đ&uacute;c is available at small stalls and is eaten throughout the day.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Quy_tr.C3.ACnh_th.E1.BB.B1c_hi.E1.BB.87n" class="mw-headline">Quy tr&igrave;nh thực hiện</span></h2>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>B&aacute;nh đ&uacute;c kiểu miền Nam với nước cốt dừa, m&egrave;, nhuộm m&agrave;u bằng l&aacute; dứa thơm</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">Quy tr&igrave;nh thực hiện b&aacute;nh đ&uacute;c, d&ugrave; với nhiều biến thể, về cơ bản bao gồm 3 c&ocirc;ng đoạn: l&agrave;m nước ng&acirc;m gạo, chuẩn bị bột v&agrave; đun b&aacute;nh. Dưới đ&acirc;y chỉ tr&igrave;nh b&agrave;y m&oacute;n b&aacute;nh đ&uacute;c bột gạo, l&agrave; m&oacute;n cơ bản v&agrave; phổ biến nhất trong họ b&aacute;nh đ&uacute;c.<br /><br />Nước ng&acirc;m gạo được l&agrave;m bằng nước v&ocirc;i trong hoặc nước tro. V&ocirc;i t&ocirc;i (loại v&ocirc;i mềm nhuyễn để trộn vữa x&acirc;y nh&agrave;, v&agrave; c&aacute;c cụ ở Việt Nam ng&agrave;y xưa thường d&ugrave;ng ăn trầu) được pha với nước, thường với tỷ lệ 1 l&iacute;t nước 10g v&ocirc;i. Khuấy đều để lắng v&agrave; chắt lấy nước v&ocirc;i trong. Với nước tro, người ta thường lấy tro bếp, loại tro đun củi hoặc l&aacute; c&acirc;y kh&ocirc;ng lẫn tạp chất của c&aacute;c loại nhi&ecirc;n liệu kh&aacute;c, s&agrave;ng xảy thật kỹ v&agrave; h&ograve;a v&agrave;o nước ng&acirc;m gạo. Một số nơi cầu kỳ c&ograve;n sử dụng c&aacute;c loại c&agrave;nh xoan, c&agrave;nh bưởi, l&aacute; si, d&acirc;y tầm gửi v.v. để đốt th&agrave;nh tro v&agrave; lấy tro đ&oacute; h&ograve;a nước.<br /><br />Chọn loại gạo ngon, t&ugrave;y loại gạo sẽ quyết định chất lượng b&aacute;nh (độ dai, gi&ograve;n, nở; m&agrave;u sắc; hương vị). Gạo được vo, đ&atilde;i sạch v&agrave; ng&acirc;m trong nước v&ocirc;i trong hoặc nước tro đ&atilde; n&oacute;i ở tr&ecirc;n trong một thời hạn nhất định c&oacute; thể mười tiếng đồng hồ hoặc v&agrave;i ng&agrave;y (t&ugrave;y địa phương, t&ugrave;y loại gạo) cho đến khi tay b&oacute;p hạt gạo thấy bở tơi. Sau đ&oacute; gạo được đem xay lẫn với nước cho ra bột nước. Bột lại xay đi xay lại v&agrave;i lần cho đến khi thật nhuyễn mịn v&agrave; sau đ&oacute; c&ograve;n được lược qua r&acirc;y cho sạch tạp chất.<br /><br />Để tạo độ dai gi&ograve;n của b&aacute;nh c&oacute; khi người ta c&ograve;n d&ugrave;ng cả h&agrave;n the h&ograve;a lẫn v&agrave;o bột d&ugrave; rằng đ&acirc;y l&agrave; phụ gia kh&ocirc;ng đảm bảo vệ sinh an to&agrave;n thực phẩm.<br /><br />Nồi được l&aacute;ng một ch&uacute;t mỡ nước để bột kh&ocirc;ng d&iacute;nh đ&aacute;y, đổ bột v&agrave;o v&agrave; bắc l&ecirc;n bếp đun nhỏ lửa, khuấy thật đều tay v&agrave; li&ecirc;n tục sao cho bột kh&ocirc;ng v&oacute;n, kh&ocirc;ng kh&ecirc;, kh&ocirc;ng s&aacute;t nồi, cho đến khi bột trong nồi đặc sệt lại, trở n&ecirc;n trong mượt, hớt một đũa bột l&ecirc;n thấy bột chảy xuống kh&ocirc;ng d&iacute;nh đũa, th&igrave; tắt bếp. Rất thường thấy b&aacute;nh đ&uacute;c bột gạo n&agrave;y c&ograve;n kết hợp với lạc nh&acirc;n luộc ch&iacute;n b&oacute;c vỏ v&agrave; cơm dừa xắt vụn. Khi đ&oacute; lạc v&agrave; dừa được th&ecirc;m v&agrave;o nồi bột trước thời điểm nhấc nồi xuống.<br /><br />D&ugrave;ng dầu ăn, nước mỡ lợn hoặc ch&uacute;t &oacute;c lợn t&aacute;n nhuyễn thoa đều l&ecirc;n l&ograve;ng một c&aacute;i đĩa to, hoặc c&aacute;c khu&ocirc;n b&aacute;nh. Đổ bột v&agrave;o với độ d&agrave;y khoảng 3-5cm. Để nguội th&agrave;nh phẩm sẽ đ&ocirc;ng cứng lại v&agrave; c&oacute; thể xắt miếng để ăn k&egrave;m c&aacute;c thực phẩm kh&aacute;c (tương, mật, c&aacute; kho, canh cua, đậu phụ r&aacute;n, thịt lợn luộc v.v.).</p>', 'Wikipedia', '', '17:49:50', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (107, 35, 2, 'Bánh đúc', '', 'banh-duc', '<p>B&aacute;nh đ&uacute;c is a term used to refer to two different varieties of Vietnamese b&aacute;nh, or cake.</p>', '<h2>Northern Vietnamese version</h2>\r\n<p style="text-align: justify;">In northern Vietnam, b&aacute;nh đ&uacute;c is a cake made from either non-glutinous rice flour or corn flour. It is white in color and has a soft texture and mild flavour.photo It is typically garnished with savory ingredients such as ground pork, t&ocirc;m chấy (grilled ground shrimp),photo fried onions, sesame seeds, salt, peanuts, lime juice, and soy sauce or fish sauce.photo Although it may be eaten on its own, it may also be served hot, accompanied by steamed meat or mushrooms.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cdd066d361_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A bowl of northern Vietnamese b&aacute;nh đ&uacute;c thịt (b&aacute;nh đ&uacute;c with ground pork and fish sauce)</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">B&aacute;nh đ&uacute;c is available at small stalls and is eaten throughout the day.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Southern_Vietnamese_version" class="mw-headline">Southern Vietnamese version</span></h2>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A plate of southern Vietnamese b&aacute;nh đ&uacute;c colored with Pandanus amaryllifolius leaf extract, and topped with sweetened coconut milk and sesame seeds</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">In southern Vietnam, b&aacute;nh đ&uacute;c is a dessert made from non-glutinous rice flour. It takes the form of gelatinous blocks that are often colored green by the addition of Pandanus amaryllifolius leaf extract. It is cooked by boiling the ingredients and allowing them to cool, solidifying into a jelly-like sheetphoto that is then cut into blocks.</p>', 'Wikipedia', '', '17:51:45', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (108, 35, 2, 'Bánh đúc', '', 'banh-duc', '<p>B&aacute;nh đ&uacute;c is a term used to refer to two different varieties of Vietnamese b&aacute;nh, or cake.</p>', '<h2>Northern Vietnamese version</h2>\r\n<p style="text-align: justify;">In northern Vietnam, b&aacute;nh đ&uacute;c is a cake made from either non-glutinous rice flour or corn flour. It is white in color and has a soft texture and mild flavour.photo It is typically garnished with savory ingredients such as ground pork, t&ocirc;m chấy (grilled ground shrimp),photo fried onions, sesame seeds, salt, peanuts, lime juice, and soy sauce or fish sauce.photo Although it may be eaten on its own, it may also be served hot, accompanied by steamed meat or mushrooms.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cdd066d361_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A bowl of northern Vietnamese b&aacute;nh đ&uacute;c thịt (b&aacute;nh đ&uacute;c with ground pork and fish sauce)</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">B&aacute;nh đ&uacute;c is available at small stalls and is eaten throughout the day.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Southern_Vietnamese_version" class="mw-headline">Southern Vietnamese version</span></h2>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A plate of southern Vietnamese b&aacute;nh đ&uacute;c colored with Pandanus amaryllifolius leaf extract, and topped with sweetened coconut milk and sesame seeds</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">In southern Vietnam, b&aacute;nh đ&uacute;c is a dessert made from non-glutinous rice flour. It takes the form of gelatinous blocks that are often colored green by the addition of Pandanus amaryllifolius leaf extract. It is cooked by boiling the ingredients and allowing them to cool, solidifying into a jelly-like sheetphoto that is then cut into blocks.</p>', 'Wikipedia', '', '17:51:45', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (109, 37, 6, 'Bến Thành Market', '', 'ben-thanh-market', '<p style="text-align: justify;">Ben Thanh Market (from Vietnamese Ben meaning "wharf", and Quy Thanh meaning "turtle citadel") is a big marketplace in the downtown area of Ho Chi Minh City, Vietnam, in District 1. The market is one of the earliest surviving structures in Saigon and today is considered one of symbols of Ho Chi Minh City, popular with tourists seeking local handicrafts, textiles, &aacute;o d&agrave;i, and souvenirs, as well as local cuisine.</p>', '<h2 style="text-align: justify;"><span id="History" class="mw-headline">History</span></h2>\r\n<p style="text-align: justify;">The market developed from informal markets created by early 17th century street vendors gathering together near the Saigon River. The market was formally established by the French colonial powers after taking over the Gia Dinh citadel in 1859 (see Citadel of Saigon). This market was destroyed by fire in 1870 and rebuilt to become Saigon''s largest market. In 1912 the market was moved to a new building and called the New Ben Thanh Market to distinguish over its predecessor. The building was renovated in 1985.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce02254cc8_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Bến Th&agrave;nh Market</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2 style="text-align: justify;"><span id="Transport" class="mw-headline">Transport</span></h2>\r\n<p style="text-align: justify;">Apart from being a major hub for the network of city buses serving Ho Chi Minh City, Ben Thanh Market is planned to be a hub for several lines of the planned Ho Chi Minh City Metro; Line 1, already under construction, would connect Ben Thanh with Suoi Tien Park and Long Binh in District 9. Other lines would connect Ben Thanh with Tham Luong in District 12, and with Cholon and Binh Tan District.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce021481af_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Ben Thanh Market</em></p>', 'Wikipedia', '', '17:58:41', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (110, 38, 12, 'Mid-Autumn Festival', '', 'mid-autumn-festival', '<p>The Mid-Autumn Festival, also known as the Moon Festival, Zhongqiu Festival, or in Chinese, Zhongqiujie, or in Vietnamese "Tết Trung Thu", is a popular harvest festival celebrated by Chinese and Vietnamese people, dating back over 3,000 years to moon worship in China''s Shang Dynasty.</p>', '<p>The Mid-Autumn Festival is held on the 15th day of the eighth month in the Chinese calendar, which is usually around late September or early October in the Gregorian calendar. It is a date that parallels the autumnal equinox of the solar calendar, when the moon is supposedly at its fullest and roundest. The traditional food of this festival is the mooncake, of which there are many different varieties.</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce033ae1f6_medium.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Mid-Autumn Festival at the Botanical Garden, Montreal</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p>The Mid-Autumn Festival is one of the few most important holidays in the Chinese calendar, the others being Chinese New Year and Winter Solstice, and is a legal holiday in several countries. Farmers celebrate the end of the summer harvesting season on this date. Traditionally on this day, Chinese family members and friends will gather to admire the bright mid-autumn harvest moon, and eat moon cakes and pomelos under the moon together.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce032b90af_medium.JPG" alt="" /></p>\r\n<p style="text-align: center;"><em>Mid-Autumn Festival celebrations in Victoria Park, Hong Kong</em></p>', 'Wikipedia', '', '18:00:14', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (111, 39, 16, 'Chợ Bến Thành', '', 'cho-ben-thanh', '<p style="text-align: justify;">Chợ Bến Th&agrave;nh l&agrave; một trong những địa điểm ti&ecirc;u biểu của Th&agrave;nh phố Hồ Ch&iacute; Minh. Trong nhiều trường hợp, h&igrave;nh ảnh khu cửa nam ng&ocirc;i chợ n&agrave;y được xem l&agrave; biểu tượng của th&agrave;nh phố.</p>', '<h2><span id="Ngu.E1.BB.93n_g.E1.BB.91c_v.C3.A0_xu.E1.BA.A5t_x.E1.BB.A9_t.C3.AAn_g.E1.BB.8Di" class="mw-headline">Nguồn gốc v&agrave; xuất xứ t&ecirc;n gọi</span></h2>\r\n<p style="text-align: justify;">Nguy&ecirc;n thủy, chợ Bến Th&agrave;nh đ&atilde; c&oacute; từ trước khi người Ph&aacute;p x&acirc;m chiếm Gia Định. Ban đầu, vị tr&iacute; của chợ nằm b&ecirc;n bờ s&ocirc;ng Bến Ngh&eacute;, cạnh một bến s&ocirc;ng gần th&agrave;nh Gia Định (bấy giờ l&agrave; th&agrave;nh Quy, c&ograve;n gọi l&agrave; th&agrave;nh B&aacute;t Qu&aacute;i). Bến n&agrave;y d&ugrave;ng để cho h&agrave;nh kh&aacute;ch v&atilde;ng lai v&agrave; qu&acirc;n nh&acirc;n v&agrave;o th&agrave;nh, v&igrave; vậy mới c&oacute; t&ecirc;n gọi l&agrave; Bến Th&agrave;nh, v&agrave; khu chợ cũng c&oacute; t&ecirc;n gọi l&agrave; chợ Bến Th&agrave;nh.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce02254cc8_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;">Cổng ch&iacute;nh v&agrave;o dịp Tết 2003</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Ch.E1.BB.A3_M.E1.BB.9Bi" class="mw-headline">Chợ Mới</span></h2>\r\n<p style="text-align: justify;">Khu vực x&acirc;y chợ, vốn l&agrave; một c&aacute;i ao s&igrave;nh lầy cũ, gọi l&agrave; ao Bồ Rệt (Marais Boresse), được người Ph&aacute;p cho lấp đi. Khu&ocirc;n vi&ecirc;n chợ quy hoạch bốn mặt bởi bốn con đường. Mặt tiền l&agrave; Place Cuniac, t&ecirc;n đặt theo vi&ecirc;n X&atilde; T&acirc;y (Ủy vi&ecirc;n Hội đồng) Cuniac, người đ&atilde; đề ra c&ocirc;ng việc lấp ao. Người Việt th&igrave; quen gọi đ&oacute; l&agrave; B&ugrave;ng binh Chợ Bến Th&agrave;nh cho d&ugrave; t&ecirc;n ch&iacute;nh thức đ&atilde; từng đổi l&agrave; "C&ocirc;ng trường Cộng H&ograve;a", "C&ocirc;ng trường Di&ecirc;n Hồng", rồi "Quảng trường Qu&aacute;ch Thị Trang". Mặt bắc chợ l&agrave; Rue d''Espagne, ph&iacute;a đ&ocirc;ng l&agrave; rue Vi&eacute;not, v&agrave; ph&iacute;a t&acirc;y l&agrave; rue Schroeder. Năm 1955 thời Đệ nhất Cộng h&ograve;a Việt Nam, bốn con đường n&agrave;y đổi t&ecirc;n th&agrave;nh đường L&ecirc; Th&aacute;nh T&ocirc;n, đường Phan Bội Ch&acirc;u v&agrave; đường Phan Ch&acirc;u Trinh.<br /><br />Ng&ocirc;i chợ mới do h&atilde;ng thầu Brossard et Maupin khởi c&ocirc;ng x&acirc;y dựng từ năm 1912 đến cuối th&aacute;ng 3 năm 1914 th&igrave; ho&agrave;n tất. Lễ ăn mừng chợ kh&aacute;nh th&agrave;nh được b&aacute;o ch&iacute; thời đ&oacute; gọi l&agrave; "T&acirc;n Vương Hội", do được diễn ra trong ba ng&agrave;y 28, 29 v&agrave; 30 th&aacute;ng 3 năm 1914, với ph&aacute;o b&ocirc;ng, xe hoa v&agrave; hơn 100.000 người tham dự, kể cả từ c&aacute;c tỉnh đổ về.<br /><br />Khu chợ mới n&agrave;y vẫn mang t&ecirc;n gọi Bến Th&agrave;nh; tuy nhi&ecirc;n cho đến trước năm 1975 t&ecirc;n gọi chợ Bến Th&agrave;nh n&agrave;y thường chỉ hiện diện trong s&aacute;ch vở, c&ograve;n người d&acirc;n th&igrave; thường gọi l&agrave; chợ S&agrave;i G&ograve;n hay chợ Mới, để ph&acirc;n biệt chợ Cũ tại điểm cũ, vốn chỉ c&ograve;n lại gian h&agrave;ng thịt. Phần c&ograve;n lại bị ph&aacute; đi v&agrave; được người Ph&aacute;p x&acirc;y dựng th&agrave;nh cơ quan Ng&acirc;n khố. Hai con đường b&ecirc;n h&ocirc;ng chợ m&atilde;i đến năm 1940 c&ograve;n l&agrave; bến xe đ&ograve; miền Đ&ocirc;ng v&agrave; miền T&acirc;y. Về sau, bến xe n&agrave;y mới được dời đi chỗ kh&aacute;c.<br /><br />Chợ Bến Th&agrave;nh hoạt động li&ecirc;n tục trong 70 năm. Từ ng&agrave;y 1 th&aacute;ng 7 đến 15 th&aacute;ng 8 năm 1985, chợ Bến Th&agrave;nh được cải tạo v&agrave; sữa chữa lớn.<br /><br />&nbsp;&nbsp;&nbsp; * Địa chỉ: Cửa Nam (nằm giữa c&aacute;c đường Phan Bội Ch&acirc;u - Phan Chu Trinh - L&ecirc; Th&aacute;nh T&ocirc;n - C&ocirc;ng trường Qu&aacute;ch Thị Trang) - Phường Bến Th&agrave;nh - Quận 1<br />&nbsp;&nbsp;&nbsp; * Điện thoại: +84.8.8292096<br />&nbsp;&nbsp;&nbsp; * Diện t&iacute;ch: 13.056 m&sup2;<br />&nbsp;&nbsp;&nbsp; * Ng&agrave;nh h&agrave;ng kinh doanh chủ yếu: Quần &aacute;o, vải sợi, giầy d&eacute;p, thời trang, h&agrave;ng thủ c&ocirc;ng mỹ nghệ, thực phẩm tươi sống, tr&aacute;i c&acirc;y, hoa tươi.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce021481af_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Chợ Bến Th&agrave;nh<br /></em></p>', 'Wikipedia', '', '18:01:51', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (112, 40, 24, 'Tết Trung thu', '', 'tet-trung-thu', '<p>Tết Trung Thu theo &acirc;m lịch l&agrave; ng&agrave;y rằm th&aacute;ng 8 hằng năm. Đ&acirc;y l&agrave; ng&agrave;y tết của trẻ em, c&ograve;n được gọi l&agrave; "Tết tr&ocirc;ng Trăng" hay Tết Thiếu nhi, Tết Nhi đồng.</p>', '<p>Trẻ em rất mong đợi được đ&oacute;n tết n&agrave;y v&igrave; thường được người lớn tặng đồ chơi, thường l&agrave; đ&egrave;n &ocirc;ng sao, mặt nạ, đ&egrave;n k&eacute;o qu&acirc;n, s&uacute;ng phun nước... rồi b&aacute;nh nướng, b&aacute;nh dẻo. V&agrave;o ng&agrave;y tết n&agrave;y, người ta tổ chức b&agrave;y cỗ, tr&ocirc;ng trăng. Thời điểm trăng l&ecirc;n cao, trẻ em sẽ vừa m&uacute;a h&aacute;t vừa ngắm trăng ph&aacute; cỗ. Ở một số nơi người ta c&ograve;n tổ chức m&uacute;a l&acirc;n, m&uacute;a sư tử, m&uacute;a rồng để c&aacute;c em vui chơi thoả th&iacute;ch.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce110b10d9_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>B&aacute;nh nướng ng&agrave;y tết Trung thu</em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p>Tết Trung Thu c&oacute; nguồn gốc văn minh n&ocirc;ng nghiệp của d&acirc;n tộc Việt. Thời điểm n&agrave;y kh&iacute; trời m&aacute;t mẻ, m&ugrave;a m&agrave;ng đang chờ thu hoạch. N&ocirc;ng d&acirc;n nghỉ ngơi v&agrave; vui chơi sau một vụ m&ugrave;a. Sau n&agrave;y người Trung Hoa tiếp nhận từ văn h&oacute;a Việt v&agrave; tiếp đ&oacute; phổ biến th&agrave;nh ng&agrave;y lễ truyền thống của d&acirc;n tộc Đ&ocirc;ng &Aacute; như H&agrave;n Quốc, Nhật Bản.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce10ebf455_crop.JPG" alt="" /></p>\r\n<p style="text-align: center;"><em>C&aacute;c loại đ&egrave;n lồng</em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p>Theo phong tục người Việt, v&agrave;o dịp Tết Trung Thu, cha mẹ b&agrave;y cỗ cho c&aacute;c con để mừng trung thu, mua v&agrave; l&agrave;m đủ thứ lồng đ&egrave;n thắp bằng nến để treo trong nh&agrave; v&agrave; để c&aacute;c con rước đ&egrave;n. Cỗ mừng trung thu gồm b&aacute;nh Trung Thu, kẹo, m&iacute;a, bưởi v&agrave; c&aacute;c thứ hoa quả kh&aacute;c nữa. Đ&acirc;y l&agrave; dịp để cha mẹ t&ugrave;y theo khả năng kinh tế gia đ&igrave;nh thể hiện t&igrave;nh thương y&ecirc;u con c&aacute;i một c&aacute;ch cụ thể. V&igrave; thế, t&igrave;nh y&ecirc;u gia đ&igrave;nh lại c&agrave;ng khắng kh&iacute;t th&ecirc;m. Cũng trong dịp n&agrave;y người ta mua b&aacute;nh trung thu, tr&agrave;, rượu để c&uacute;ng tổ ti&ecirc;n, biếu &ocirc;ng b&agrave;, cha mẹ, thầy c&ocirc;, bạn b&egrave;, họ h&agrave;ng v&agrave; c&aacute;c &acirc;n nh&acirc;n kh&aacute;c.</p>', 'Wikipedia', '', '18:03:29', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (113, 41, 8, 'Cơm tấm', '', 'com-tam', '<p style="text-align: justify;">Cơm tấm in Vietnamese is cooked rice from fractured rice grains. Tấm refers to the broken rice grains while cơm refers to cooked rice.<br /><br /></p>', '<p style="text-align: justify;">It is usually served with grilled pork (either ribs or shredded) plus a Vietnamese dish called b&igrave; (thinly shredded pork mixed with cooked and thinly shredded pork skin) over broken rice. The rice and meat are served with various greens and pickled vegetables, along with a prawn paste cake, trứng hấp (baked egg), and grilled prawns. Typically restaurants will serve this popular combination rice plate with a small bowl of nước chấm, as well as a small bowl of soup broth (canh) with garlic chives (to cleanse the throat).</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce1fb72ae2_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Cơm tấm with grilled pork, shredded pork, pork skin, fried egg, pork meatloaf, pickled carrot, and sliced cucumber</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Broken_Rice" class="mw-headline">Broken Rice</span></h2>\r\n<p style="text-align: justify;">Some rice grains break in the transport and processing of the rice from the field to the pot. Machinery is available to separate the broken grains from the whole grains. Broken rice may or may not have lower fiber and nutrient content, but generally has a similar energy content to intact rice.<br /><br />The broken varieties are often less expensive, so are preferred by poorer consumers or used as raw material (such as in beer brewing). ue to the different size and shape of the grains, broken rice has a different texture from "unbroken" rice. Some chefs and consumers may prefer the qualities of broken rice for certain dishes.</p>', 'Wikipedia', '', '18:07:28', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (114, 42, 19, 'Cơm tấm', '', 'com-tam', '<p style="text-align: justify;">Cơm tấm l&agrave; m&oacute;n đặc sản của miền Nam Việt Nam, n&oacute; l&agrave; một trong những m&oacute;n ăn s&aacute;ng được ưa chuộng nhất của người miền Nam, nhất l&agrave; S&agrave;i G&ograve;n. Hiện nay loại cơm l&agrave;m từ hột gạo bể n&agrave;y đ&atilde; c&oacute; mặt ở một số nơi thuộc miền Trung, miền Bắc v&agrave; hải ngoại.</p>', '<p style="text-align: justify;">Dĩa cơm tấm với sườn, b&igrave;, chả, trứng thường được d&ugrave;ng l&agrave;m qu&agrave; s&aacute;ng, nhưng nay cơm tấm đ&atilde; c&oacute; ở một số qu&aacute;n ăn trưa, chiều hoặc tối với nhiều loại thức ăn k&egrave;m.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce1fb72ae2_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Một dĩa cơm tấm với đầy đủ sườn, b&igrave;, chả v&agrave; trứng ốp-la</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Th.C3.A0nh_ph.E1.BA.A7n" class="mw-headline">Th&agrave;nh phần</span></h2>\r\n<p style="text-align: justify;"><strong>- Cơm</strong><br /><br />Cơm tấm được nấu từ hạt gạo tấm, tức hạt gạo bị bể. Gạo n&agrave;y xưa l&agrave; loại gạo thứ phẩm, rớt v&atilde;i sau khi s&agrave;ng, thường d&ugrave;ng cho g&agrave; ăn hoặc cho người ăn l&uacute;c qu&aacute; t&uacute;ng thiếu. Nay th&igrave; cơm tấm đ&atilde; th&agrave;nh m&oacute;n ăn quen thuộc do đ&oacute; gạo tấm cũng được n&acirc;ng gi&aacute; cao hơn.</p>\r\n<p style="text-align: justify;"><br />- <strong>Nước mắm</strong><br /><br />Ăn cơm tấm phải c&oacute; nước mắm ngọt, l&agrave; nước mắm pha với nước lọc v&agrave; th&ecirc;m đường. T&ugrave;y theo c&aacute;ch chế biến v&agrave; khẩu vị của người ăn, nước mắm c&oacute; thể ngọt nhiều hoặc ngọt &iacute;t (mặn), c&oacute; thể th&ecirc;m chanh.</p>\r\n<p style="text-align: justify;"><br />- <strong>M&oacute;n ăn k&egrave;m</strong><br /><br />Cơm tấm c&oacute; thể ăn k&egrave;m với nhiều thứ, nhưng nhiều nhất l&agrave; Sườn nướng, chả trứng, trứng ốp-la v&agrave; b&igrave;.</p>\r\n<p style="text-align: justify;"><br />- <strong>Sườn, b&igrave;, chả, trứng</strong><br /><br />Một dĩa cơm tấm c&oacute; thể gồm cả sườn, b&igrave;, chả, trứng hoặc kh&ocirc;ng gồm đầy đủ c&aacute;c m&oacute;n tr&ecirc;n, nhưng đ&acirc;y l&agrave; 4 thức ăn th&ocirc;ng dụng nhất v&agrave; người ta thường xếp t&ecirc;n ch&uacute;ng b&ecirc;n nhau tr&ecirc;n c&aacute;c biển hiệu cơm tấm:<br /><br />&nbsp;&nbsp;&nbsp; * Sườn: Sườn ăn với cơm tấm l&agrave; loại sườn heo được tẩm ướp gia vị chua ngọt, sau đ&oacute; đem nướng. Thường c&aacute;c qu&aacute;n cơm tấm nướng sườn ngay trước cửa tiệm, kh&oacute;i hương bốc ra mang theo m&ugrave;i vị đặc trưng của sườn nướng v&agrave; nhiều người nhận ra ngay l&agrave; họ vừa đi ngang qua qu&aacute;n cơm tấm.<br />&nbsp;&nbsp;&nbsp; * Chả: Gọi l&agrave; chả hoặc chả trứng, được l&agrave;m từ trứng, cua, thịt xay, nấm m&egrave;o v&agrave; b&uacute;n t&agrave;u. Chả trứng được chưng sẵn th&agrave;nh một c&aacute;i b&aacute;nh h&igrave;nh tr&ograve;n hoặc chữ nhật, khi ăn xắt l&aacute;t.<br />&nbsp;&nbsp;&nbsp; * Trứng: Trứng ở đ&acirc;y thường l&agrave; trứng g&agrave; ốp la.<br />&nbsp;&nbsp;&nbsp; * B&igrave;: l&agrave; hỗn hợp nhiều thứ, thường gồm thịt heo cắt sợi, da heo cắt sợi trộn với th&iacute;nh v&agrave; gia vị.<br /><br />- <strong>Mỡ h&agrave;nh</strong><br /><br />Mỡ h&agrave;nh cũng rất quan trọng trong dĩa cơm tấm. N&oacute; l&agrave; hỗn hợp lỏng được l&agrave;m từ h&agrave;nh l&aacute;, phi dầu hoặc mỡ, đ&ocirc;i khi trộn với t&oacute;p mỡ chi&ecirc;n. Mỡ h&agrave;nh gi&uacute;p cơm tấm c&oacute; độ b&eacute;o đặc trưng, tuy nhi&ecirc;n một số người kh&ocirc;ng ăn v&igrave; nhiều l&yacute; do.</p>\r\n<p style="text-align: justify;"><br />- <strong>Đồ chua</strong><br /><br />Đồ chua gi&uacute;p cơm tấm c&oacute; vị chua ngọt. Thường l&agrave;m từ đu đủ, c&agrave; rốt, củ cải, dưa leo, đ&ocirc;i khi l&agrave; c&agrave; chua.</p>\r\n<p style="text-align: justify;"><br />- <strong>C&aacute;c thức kh&aacute;c</strong><br /><br />Đ&ocirc;i khi cơm tấm được ăn với thịt kho, đậu hủ nhồi thịt, c&aacute; chi&ecirc;n, g&agrave;, rau, đồ x&agrave;o... giống như cơm gạo nguy&ecirc;n hột. Kiểu ăn n&agrave;y thường thấy ở c&aacute;c qu&aacute;n cơm tấm đ&ecirc;m.</p>', 'Wikipedia', '', '18:12:07', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (115, 42, 19, 'Cơm tấm', '', 'com-tam', '<p style="text-align: justify;">Cơm tấm l&agrave; m&oacute;n đặc sản của miền Nam Việt Nam, n&oacute; l&agrave; một trong những m&oacute;n ăn s&aacute;ng được ưa chuộng nhất của người miền Nam, nhất l&agrave; S&agrave;i G&ograve;n. Hiện nay loại cơm l&agrave;m từ hột gạo bể n&agrave;y đ&atilde; c&oacute; mặt ở một số nơi thuộc miền Trung, miền Bắc v&agrave; hải ngoại.</p>', '<p style="text-align: justify;">Dĩa cơm tấm với sườn, b&igrave;, chả, trứng thường được d&ugrave;ng l&agrave;m qu&agrave; s&aacute;ng, nhưng nay cơm tấm đ&atilde; c&oacute; ở một số qu&aacute;n ăn trưa, chiều hoặc tối với nhiều loại thức ăn k&egrave;m.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce1fb72ae2_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Một dĩa cơm tấm với đầy đủ sườn, b&igrave;, chả v&agrave; trứng ốp-la</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Th.C3.A0nh_ph.E1.BA.A7n" class="mw-headline">Th&agrave;nh phần</span></h2>\r\n<p style="text-align: justify;"><strong>- Cơm</strong><br /><br />Cơm tấm được nấu từ hạt gạo tấm, tức hạt gạo bị bể. Gạo n&agrave;y xưa l&agrave; loại gạo thứ phẩm, rớt v&atilde;i sau khi s&agrave;ng, thường d&ugrave;ng cho g&agrave; ăn hoặc cho người ăn l&uacute;c qu&aacute; t&uacute;ng thiếu. Nay th&igrave; cơm tấm đ&atilde; th&agrave;nh m&oacute;n ăn quen thuộc do đ&oacute; gạo tấm cũng được n&acirc;ng gi&aacute; cao hơn.</p>\r\n<p style="text-align: justify;"><br />- <strong>Nước mắm</strong><br /><br />Ăn cơm tấm phải c&oacute; nước mắm ngọt, l&agrave; nước mắm pha với nước lọc v&agrave; th&ecirc;m đường. T&ugrave;y theo c&aacute;ch chế biến v&agrave; khẩu vị của người ăn, nước mắm c&oacute; thể ngọt nhiều hoặc ngọt &iacute;t (mặn), c&oacute; thể th&ecirc;m chanh.</p>\r\n<p style="text-align: justify;"><br />- <strong>M&oacute;n ăn k&egrave;m</strong><br /><br />Cơm tấm c&oacute; thể ăn k&egrave;m với nhiều thứ, nhưng nhiều nhất l&agrave; Sườn nướng, chả trứng, trứng ốp-la v&agrave; b&igrave;.</p>\r\n<p style="text-align: justify;"><br />- <strong>Sườn, b&igrave;, chả, trứng</strong><br /><br />Một dĩa cơm tấm c&oacute; thể gồm cả sườn, b&igrave;, chả, trứng hoặc kh&ocirc;ng gồm đầy đủ c&aacute;c m&oacute;n tr&ecirc;n, nhưng đ&acirc;y l&agrave; 4 thức ăn th&ocirc;ng dụng nhất v&agrave; người ta thường xếp t&ecirc;n ch&uacute;ng b&ecirc;n nhau tr&ecirc;n c&aacute;c biển hiệu cơm tấm:<br /><br />&nbsp;&nbsp;&nbsp; * Sườn: Sườn ăn với cơm tấm l&agrave; loại sườn heo được tẩm ướp gia vị chua ngọt, sau đ&oacute; đem nướng. Thường c&aacute;c qu&aacute;n cơm tấm nướng sườn ngay trước cửa tiệm, kh&oacute;i hương bốc ra mang theo m&ugrave;i vị đặc trưng của sườn nướng v&agrave; nhiều người nhận ra ngay l&agrave; họ vừa đi ngang qua qu&aacute;n cơm tấm.<br />&nbsp;&nbsp;&nbsp; * Chả: Gọi l&agrave; chả hoặc chả trứng, được l&agrave;m từ trứng, cua, thịt xay, nấm m&egrave;o v&agrave; b&uacute;n t&agrave;u. Chả trứng được chưng sẵn th&agrave;nh một c&aacute;i b&aacute;nh h&igrave;nh tr&ograve;n hoặc chữ nhật, khi ăn xắt l&aacute;t.<br />&nbsp;&nbsp;&nbsp; * Trứng: Trứng ở đ&acirc;y thường l&agrave; trứng g&agrave; ốp la.<br />&nbsp;&nbsp;&nbsp; * B&igrave;: l&agrave; hỗn hợp nhiều thứ, thường gồm thịt heo cắt sợi, da heo cắt sợi trộn với th&iacute;nh v&agrave; gia vị.<br /><br />- <strong>Mỡ h&agrave;nh</strong><br /><br />Mỡ h&agrave;nh cũng rất quan trọng trong dĩa cơm tấm. N&oacute; l&agrave; hỗn hợp lỏng được l&agrave;m từ h&agrave;nh l&aacute;, phi dầu hoặc mỡ, đ&ocirc;i khi trộn với t&oacute;p mỡ chi&ecirc;n. Mỡ h&agrave;nh gi&uacute;p cơm tấm c&oacute; độ b&eacute;o đặc trưng, tuy nhi&ecirc;n một số người kh&ocirc;ng ăn v&igrave; nhiều l&yacute; do.</p>\r\n<p style="text-align: justify;"><br />- <strong>Đồ chua</strong><br /><br />Đồ chua gi&uacute;p cơm tấm c&oacute; vị chua ngọt. Thường l&agrave;m từ đu đủ, c&agrave; rốt, củ cải, dưa leo, đ&ocirc;i khi l&agrave; c&agrave; chua.</p>\r\n<p style="text-align: justify;"><br />- <strong>C&aacute;c thức kh&aacute;c</strong><br /><br />Đ&ocirc;i khi cơm tấm được ăn với thịt kho, đậu hủ nhồi thịt, c&aacute; chi&ecirc;n, g&agrave;, rau, đồ x&agrave;o... giống như cơm gạo nguy&ecirc;n hột. Kiểu ăn n&agrave;y thường thấy ở c&aacute;c qu&aacute;n cơm tấm đ&ecirc;m.</p>', 'Wikipedia', '', '18:16:39', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (116, 39, 16, 'Chợ Bến Thành', '', 'cho-ben-thanh', '<p style="text-align: justify;">Chợ Bến Th&agrave;nh l&agrave; một trong những địa điểm ti&ecirc;u biểu của Th&agrave;nh phố Hồ Ch&iacute; Minh. Trong nhiều trường hợp, h&igrave;nh ảnh khu cửa nam ng&ocirc;i chợ n&agrave;y được xem l&agrave; biểu tượng của th&agrave;nh phố.</p>', '<h2><span id="Ngu.E1.BB.93n_g.E1.BB.91c_v.C3.A0_xu.E1.BA.A5t_x.E1.BB.A9_t.C3.AAn_g.E1.BB.8Di" class="mw-headline">Nguồn gốc v&agrave; xuất xứ t&ecirc;n gọi</span></h2>\r\n<p style="text-align: justify;">Nguy&ecirc;n thủy, chợ Bến Th&agrave;nh đ&atilde; c&oacute; từ trước khi người Ph&aacute;p x&acirc;m chiếm Gia Định. Ban đầu, vị tr&iacute; của chợ nằm b&ecirc;n bờ s&ocirc;ng Bến Ngh&eacute;, cạnh một bến s&ocirc;ng gần th&agrave;nh Gia Định (bấy giờ l&agrave; th&agrave;nh Quy, c&ograve;n gọi l&agrave; th&agrave;nh B&aacute;t Qu&aacute;i). Bến n&agrave;y d&ugrave;ng để cho h&agrave;nh kh&aacute;ch v&atilde;ng lai v&agrave; qu&acirc;n nh&acirc;n v&agrave;o th&agrave;nh, v&igrave; vậy mới c&oacute; t&ecirc;n gọi l&agrave; Bến Th&agrave;nh, v&agrave; khu chợ cũng c&oacute; t&ecirc;n gọi l&agrave; chợ Bến Th&agrave;nh.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce02254cc8_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;">Cổng ch&iacute;nh v&agrave;o dịp Tết 2003</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Ch.E1.BB.A3_M.E1.BB.9Bi" class="mw-headline">Chợ Mới</span></h2>\r\n<p style="text-align: justify;">Khu vực x&acirc;y chợ, vốn l&agrave; một c&aacute;i ao s&igrave;nh lầy cũ, gọi l&agrave; ao Bồ Rệt (Marais Boresse), được người Ph&aacute;p cho lấp đi. Khu&ocirc;n vi&ecirc;n chợ quy hoạch bốn mặt bởi bốn con đường. Mặt tiền l&agrave; Place Cuniac, t&ecirc;n đặt theo vi&ecirc;n X&atilde; T&acirc;y (Ủy vi&ecirc;n Hội đồng) Cuniac, người đ&atilde; đề ra c&ocirc;ng việc lấp ao. Người Việt th&igrave; quen gọi đ&oacute; l&agrave; B&ugrave;ng binh Chợ Bến Th&agrave;nh cho d&ugrave; t&ecirc;n ch&iacute;nh thức đ&atilde; từng đổi l&agrave; "C&ocirc;ng trường Cộng H&ograve;a", "C&ocirc;ng trường Di&ecirc;n Hồng", rồi "Quảng trường Qu&aacute;ch Thị Trang". Mặt bắc chợ l&agrave; Rue d''Espagne, ph&iacute;a đ&ocirc;ng l&agrave; rue Vi&eacute;not, v&agrave; ph&iacute;a t&acirc;y l&agrave; rue Schroeder. Năm 1955 thời Đệ nhất Cộng h&ograve;a Việt Nam, bốn con đường n&agrave;y đổi t&ecirc;n th&agrave;nh đường L&ecirc; Th&aacute;nh T&ocirc;n, đường Phan Bội Ch&acirc;u v&agrave; đường Phan Ch&acirc;u Trinh.<br /><br />Ng&ocirc;i chợ mới do h&atilde;ng thầu Brossard et Maupin khởi c&ocirc;ng x&acirc;y dựng từ năm 1912 đến cuối th&aacute;ng 3 năm 1914 th&igrave; ho&agrave;n tất. Lễ ăn mừng chợ kh&aacute;nh th&agrave;nh được b&aacute;o ch&iacute; thời đ&oacute; gọi l&agrave; "T&acirc;n Vương Hội", do được diễn ra trong ba ng&agrave;y 28, 29 v&agrave; 30 th&aacute;ng 3 năm 1914, với ph&aacute;o b&ocirc;ng, xe hoa v&agrave; hơn 100.000 người tham dự, kể cả từ c&aacute;c tỉnh đổ về.<br /><br />Khu chợ mới n&agrave;y vẫn mang t&ecirc;n gọi Bến Th&agrave;nh; tuy nhi&ecirc;n cho đến trước năm 1975 t&ecirc;n gọi chợ Bến Th&agrave;nh n&agrave;y thường chỉ hiện diện trong s&aacute;ch vở, c&ograve;n người d&acirc;n th&igrave; thường gọi l&agrave; chợ S&agrave;i G&ograve;n hay chợ Mới, để ph&acirc;n biệt chợ Cũ tại điểm cũ, vốn chỉ c&ograve;n lại gian h&agrave;ng thịt. Phần c&ograve;n lại bị ph&aacute; đi v&agrave; được người Ph&aacute;p x&acirc;y dựng th&agrave;nh cơ quan Ng&acirc;n khố. Hai con đường b&ecirc;n h&ocirc;ng chợ m&atilde;i đến năm 1940 c&ograve;n l&agrave; bến xe đ&ograve; miền Đ&ocirc;ng v&agrave; miền T&acirc;y. Về sau, bến xe n&agrave;y mới được dời đi chỗ kh&aacute;c.<br /><br />Chợ Bến Th&agrave;nh hoạt động li&ecirc;n tục trong 70 năm. Từ ng&agrave;y 1 th&aacute;ng 7 đến 15 th&aacute;ng 8 năm 1985, chợ Bến Th&agrave;nh được cải tạo v&agrave; sữa chữa lớn.<br /><br />&nbsp;&nbsp;&nbsp; * Địa chỉ: Cửa Nam (nằm giữa c&aacute;c đường Phan Bội Ch&acirc;u - Phan Chu Trinh - L&ecirc; Th&aacute;nh T&ocirc;n - C&ocirc;ng trường Qu&aacute;ch Thị Trang) - Phường Bến Th&agrave;nh - Quận 1<br />&nbsp;&nbsp;&nbsp; * Điện thoại: +84.8.8292096<br />&nbsp;&nbsp;&nbsp; * Diện t&iacute;ch: 13.056 m&sup2;<br />&nbsp;&nbsp;&nbsp; * Ng&agrave;nh h&agrave;ng kinh doanh chủ yếu: Quần &aacute;o, vải sợi, giầy d&eacute;p, thời trang, h&agrave;ng thủ c&ocirc;ng mỹ nghệ, thực phẩm tươi sống, tr&aacute;i c&acirc;y, hoa tươi.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce021481af_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Chợ Bến Th&agrave;nh<br /></em></p>', 'Wikipedia', '', '18:17:33', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (117, 35, 2, 'Bánh đúc', '', 'banh-duc', '<p>B&aacute;nh đ&uacute;c is a term used to refer to two different varieties of Vietnamese b&aacute;nh, or cake.</p>', '<h2>Northern Vietnamese version</h2>\r\n<p style="text-align: justify;">In northern Vietnam, b&aacute;nh đ&uacute;c is a cake made from either non-glutinous rice flour or corn flour. It is white in color and has a soft texture and mild flavour.photo It is typically garnished with savory ingredients such as ground pork, t&ocirc;m chấy (grilled ground shrimp),photo fried onions, sesame seeds, salt, peanuts, lime juice, and soy sauce or fish sauce.photo Although it may be eaten on its own, it may also be served hot, accompanied by steamed meat or mushrooms.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cdd066d361_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A bowl of northern Vietnamese b&aacute;nh đ&uacute;c thịt (b&aacute;nh đ&uacute;c with ground pork and fish sauce)</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">B&aacute;nh đ&uacute;c is available at small stalls and is eaten throughout the day.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Southern_Vietnamese_version" class="mw-headline">Southern Vietnamese version</span></h2>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A plate of southern Vietnamese b&aacute;nh đ&uacute;c colored with Pandanus amaryllifolius leaf extract, and topped with sweetened coconut milk and sesame seeds</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">In southern Vietnam, b&aacute;nh đ&uacute;c is a dessert made from non-glutinous rice flour. It takes the form of gelatinous blocks that are often colored green by the addition of Pandanus amaryllifolius leaf extract. It is cooked by boiling the ingredients and allowing them to cool, solidifying into a jelly-like sheetphoto that is then cut into blocks.</p>', 'Wikipedia', '', '18:20:23', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (118, 35, 2, 'Bánh đúc', '', 'banh-duc', '<p>B&aacute;nh đ&uacute;c is a term used to refer to two different varieties of Vietnamese b&aacute;nh, or cake.</p>', '<h2>Northern Vietnamese version</h2>\r\n<p style="text-align: justify;">In northern Vietnam, b&aacute;nh đ&uacute;c is a cake made from either non-glutinous rice flour or corn flour. It is white in color and has a soft texture and mild flavour.photo It is typically garnished with savory ingredients such as ground pork, t&ocirc;m chấy (grilled ground shrimp),photo fried onions, sesame seeds, salt, peanuts, lime juice, and soy sauce or fish sauce.photo Although it may be eaten on its own, it may also be served hot, accompanied by steamed meat or mushrooms.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cdd066d361_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A bowl of northern Vietnamese b&aacute;nh đ&uacute;c thịt (b&aacute;nh đ&uacute;c with ground pork and fish sauce)</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">B&aacute;nh đ&uacute;c is available at small stalls and is eaten throughout the day.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Southern_Vietnamese_version" class="mw-headline">Southern Vietnamese version</span></h2>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A plate of southern Vietnamese b&aacute;nh đ&uacute;c colored with Pandanus amaryllifolius leaf extract, and topped with sweetened coconut milk and sesame seeds</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">In southern Vietnam, b&aacute;nh đ&uacute;c is a dessert made from non-glutinous rice flour. It takes the form of gelatinous blocks that are often colored green by the addition of Pandanus amaryllifolius leaf extract. It is cooked by boiling the ingredients and allowing them to cool, solidifying into a jelly-like sheetphoto that is then cut into blocks.</p>', 'Wikipedia', '', '18:20:23', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (119, 38, 12, 'Mid-Autumn Festival', '', 'mid-autumn-festival', '<p>The Mid-Autumn Festival, also known as the Moon Festival, Zhongqiu Festival, or in Chinese, Zhongqiujie, or in Vietnamese "Tết Trung Thu", is a popular harvest festival celebrated by Chinese and Vietnamese people, dating back over 3,000 years to moon worship in China''s Shang Dynasty.</p>', '<p>The Mid-Autumn Festival is held on the 15th day of the eighth month in the Chinese calendar, which is usually around late September or early October in the Gregorian calendar. It is a date that parallels the autumnal equinox of the solar calendar, when the moon is supposedly at its fullest and roundest. The traditional food of this festival is the mooncake, of which there are many different varieties.</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce033ae1f6_medium.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Mid-Autumn Festival at the Botanical Garden, Montreal</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p>The Mid-Autumn Festival is one of the few most important holidays in the Chinese calendar, the others being Chinese New Year and Winter Solstice, and is a legal holiday in several countries. Farmers celebrate the end of the summer harvesting season on this date. Traditionally on this day, Chinese family members and friends will gather to admire the bright mid-autumn harvest moon, and eat moon cakes and pomelos under the moon together.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce032b90af_medium.JPG" alt="" /></p>\r\n<p style="text-align: center;"><em>Mid-Autumn Festival celebrations in Victoria Park, Hong Kong</em></p>', 'Wikipedia', '', '18:23:15', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (120, 40, 24, 'Tết Trung thu', '', 'tet-trung-thu', '<p>Tết Trung Thu theo &acirc;m lịch l&agrave; ng&agrave;y rằm th&aacute;ng 8 hằng năm. Đ&acirc;y l&agrave; ng&agrave;y tết của trẻ em, c&ograve;n được gọi l&agrave; "Tết tr&ocirc;ng Trăng" hay Tết Thiếu nhi, Tết Nhi đồng.</p>', '<p>Trẻ em rất mong đợi được đ&oacute;n tết n&agrave;y v&igrave; thường được người lớn tặng đồ chơi, thường l&agrave; đ&egrave;n &ocirc;ng sao, mặt nạ, đ&egrave;n k&eacute;o qu&acirc;n, s&uacute;ng phun nước... rồi b&aacute;nh nướng, b&aacute;nh dẻo. V&agrave;o ng&agrave;y tết n&agrave;y, người ta tổ chức b&agrave;y cỗ, tr&ocirc;ng trăng. Thời điểm trăng l&ecirc;n cao, trẻ em sẽ vừa m&uacute;a h&aacute;t vừa ngắm trăng ph&aacute; cỗ. Ở một số nơi người ta c&ograve;n tổ chức m&uacute;a l&acirc;n, m&uacute;a sư tử, m&uacute;a rồng để c&aacute;c em vui chơi thoả th&iacute;ch.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce110b10d9_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>B&aacute;nh nướng ng&agrave;y tết Trung thu</em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p>Tết Trung Thu c&oacute; nguồn gốc văn minh n&ocirc;ng nghiệp của d&acirc;n tộc Việt. Thời điểm n&agrave;y kh&iacute; trời m&aacute;t mẻ, m&ugrave;a m&agrave;ng đang chờ thu hoạch. N&ocirc;ng d&acirc;n nghỉ ngơi v&agrave; vui chơi sau một vụ m&ugrave;a. Sau n&agrave;y người Trung Hoa tiếp nhận từ văn h&oacute;a Việt v&agrave; tiếp đ&oacute; phổ biến th&agrave;nh ng&agrave;y lễ truyền thống của d&acirc;n tộc Đ&ocirc;ng &Aacute; như H&agrave;n Quốc, Nhật Bản.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce10ebf455_crop.JPG" alt="" /></p>\r\n<p style="text-align: center;"><em>C&aacute;c loại đ&egrave;n lồng</em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p>Theo phong tục người Việt, v&agrave;o dịp Tết Trung Thu, cha mẹ b&agrave;y cỗ cho c&aacute;c con để mừng trung thu, mua v&agrave; l&agrave;m đủ thứ lồng đ&egrave;n thắp bằng nến để treo trong nh&agrave; v&agrave; để c&aacute;c con rước đ&egrave;n. Cỗ mừng trung thu gồm b&aacute;nh Trung Thu, kẹo, m&iacute;a, bưởi v&agrave; c&aacute;c thứ hoa quả kh&aacute;c nữa. Đ&acirc;y l&agrave; dịp để cha mẹ t&ugrave;y theo khả năng kinh tế gia đ&igrave;nh thể hiện t&igrave;nh thương y&ecirc;u con c&aacute;i một c&aacute;ch cụ thể. V&igrave; thế, t&igrave;nh y&ecirc;u gia đ&igrave;nh lại c&agrave;ng khắng kh&iacute;t th&ecirc;m. Cũng trong dịp n&agrave;y người ta mua b&aacute;nh trung thu, tr&agrave;, rượu để c&uacute;ng tổ ti&ecirc;n, biếu &ocirc;ng b&agrave;, cha mẹ, thầy c&ocirc;, bạn b&egrave;, họ h&agrave;ng v&agrave; c&aacute;c &acirc;n nh&acirc;n kh&aacute;c.</p>', 'Wikipedia', '', '18:23:35', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (121, 37, 6, 'Bến Thành Market', '', 'ben-thanh-market', '<p style="text-align: justify;">Ben Thanh Market (from Vietnamese Ben meaning "wharf", and Quy Thanh meaning "turtle citadel") is a big marketplace in the downtown area of Ho Chi Minh City, Vietnam, in District 1. The market is one of the earliest surviving structures in Saigon and today is considered one of symbols of Ho Chi Minh City, popular with tourists seeking local handicrafts, textiles, &aacute;o d&agrave;i, and souvenirs, as well as local cuisine.</p>', '<h2 style="text-align: justify;"><span id="History" class="mw-headline">History</span></h2>\r\n<p style="text-align: justify;">The market developed from informal markets created by early 17th century street vendors gathering together near the Saigon River. The market was formally established by the French colonial powers after taking over the Gia Dinh citadel in 1859 (see Citadel of Saigon). This market was destroyed by fire in 1870 and rebuilt to become Saigon''s largest market. In 1912 the market was moved to a new building and called the New Ben Thanh Market to distinguish over its predecessor. The building was renovated in 1985.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce02254cc8_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Bến Th&agrave;nh Market</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2 style="text-align: justify;"><span id="Transport" class="mw-headline">Transport</span></h2>\r\n<p style="text-align: justify;">Apart from being a major hub for the network of city buses serving Ho Chi Minh City, Ben Thanh Market is planned to be a hub for several lines of the planned Ho Chi Minh City Metro; Line 1, already under construction, would connect Ben Thanh with Suoi Tien Park and Long Binh in District 9. Other lines would connect Ben Thanh with Tham Luong in District 12, and with Cholon and Binh Tan District.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce021481af_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Ben Thanh Market</em></p>', 'Wikipedia', '', '18:24:10', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (122, 39, 16, 'Chợ Bến Thành', '', 'cho-ben-thanh', '<p style="text-align: justify;">Chợ Bến Th&agrave;nh l&agrave; một trong những địa điểm ti&ecirc;u biểu của Th&agrave;nh phố Hồ Ch&iacute; Minh. Trong nhiều trường hợp, h&igrave;nh ảnh khu cửa nam ng&ocirc;i chợ n&agrave;y được xem l&agrave; biểu tượng của th&agrave;nh phố.</p>', '<h2><span id="Ngu.E1.BB.93n_g.E1.BB.91c_v.C3.A0_xu.E1.BA.A5t_x.E1.BB.A9_t.C3.AAn_g.E1.BB.8Di" class="mw-headline">Nguồn gốc v&agrave; xuất xứ t&ecirc;n gọi</span></h2>\r\n<p style="text-align: justify;">Nguy&ecirc;n thủy, chợ Bến Th&agrave;nh đ&atilde; c&oacute; từ trước khi người Ph&aacute;p x&acirc;m chiếm Gia Định. Ban đầu, vị tr&iacute; của chợ nằm b&ecirc;n bờ s&ocirc;ng Bến Ngh&eacute;, cạnh một bến s&ocirc;ng gần th&agrave;nh Gia Định (bấy giờ l&agrave; th&agrave;nh Quy, c&ograve;n gọi l&agrave; th&agrave;nh B&aacute;t Qu&aacute;i). Bến n&agrave;y d&ugrave;ng để cho h&agrave;nh kh&aacute;ch v&atilde;ng lai v&agrave; qu&acirc;n nh&acirc;n v&agrave;o th&agrave;nh, v&igrave; vậy mới c&oacute; t&ecirc;n gọi l&agrave; Bến Th&agrave;nh, v&agrave; khu chợ cũng c&oacute; t&ecirc;n gọi l&agrave; chợ Bến Th&agrave;nh.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce02254cc8_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;">Cổng ch&iacute;nh v&agrave;o dịp Tết 2003</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Ch.E1.BB.A3_M.E1.BB.9Bi" class="mw-headline">Chợ Mới</span></h2>\r\n<p style="text-align: justify;">Khu vực x&acirc;y chợ, vốn l&agrave; một c&aacute;i ao s&igrave;nh lầy cũ, gọi l&agrave; ao Bồ Rệt (Marais Boresse), được người Ph&aacute;p cho lấp đi. Khu&ocirc;n vi&ecirc;n chợ quy hoạch bốn mặt bởi bốn con đường. Mặt tiền l&agrave; Place Cuniac, t&ecirc;n đặt theo vi&ecirc;n X&atilde; T&acirc;y (Ủy vi&ecirc;n Hội đồng) Cuniac, người đ&atilde; đề ra c&ocirc;ng việc lấp ao. Người Việt th&igrave; quen gọi đ&oacute; l&agrave; B&ugrave;ng binh Chợ Bến Th&agrave;nh cho d&ugrave; t&ecirc;n ch&iacute;nh thức đ&atilde; từng đổi l&agrave; "C&ocirc;ng trường Cộng H&ograve;a", "C&ocirc;ng trường Di&ecirc;n Hồng", rồi "Quảng trường Qu&aacute;ch Thị Trang". Mặt bắc chợ l&agrave; Rue d''Espagne, ph&iacute;a đ&ocirc;ng l&agrave; rue Vi&eacute;not, v&agrave; ph&iacute;a t&acirc;y l&agrave; rue Schroeder. Năm 1955 thời Đệ nhất Cộng h&ograve;a Việt Nam, bốn con đường n&agrave;y đổi t&ecirc;n th&agrave;nh đường L&ecirc; Th&aacute;nh T&ocirc;n, đường Phan Bội Ch&acirc;u v&agrave; đường Phan Ch&acirc;u Trinh.<br /><br />Ng&ocirc;i chợ mới do h&atilde;ng thầu Brossard et Maupin khởi c&ocirc;ng x&acirc;y dựng từ năm 1912 đến cuối th&aacute;ng 3 năm 1914 th&igrave; ho&agrave;n tất. Lễ ăn mừng chợ kh&aacute;nh th&agrave;nh được b&aacute;o ch&iacute; thời đ&oacute; gọi l&agrave; "T&acirc;n Vương Hội", do được diễn ra trong ba ng&agrave;y 28, 29 v&agrave; 30 th&aacute;ng 3 năm 1914, với ph&aacute;o b&ocirc;ng, xe hoa v&agrave; hơn 100.000 người tham dự, kể cả từ c&aacute;c tỉnh đổ về.<br /><br />Khu chợ mới n&agrave;y vẫn mang t&ecirc;n gọi Bến Th&agrave;nh; tuy nhi&ecirc;n cho đến trước năm 1975 t&ecirc;n gọi chợ Bến Th&agrave;nh n&agrave;y thường chỉ hiện diện trong s&aacute;ch vở, c&ograve;n người d&acirc;n th&igrave; thường gọi l&agrave; chợ S&agrave;i G&ograve;n hay chợ Mới, để ph&acirc;n biệt chợ Cũ tại điểm cũ, vốn chỉ c&ograve;n lại gian h&agrave;ng thịt. Phần c&ograve;n lại bị ph&aacute; đi v&agrave; được người Ph&aacute;p x&acirc;y dựng th&agrave;nh cơ quan Ng&acirc;n khố. Hai con đường b&ecirc;n h&ocirc;ng chợ m&atilde;i đến năm 1940 c&ograve;n l&agrave; bến xe đ&ograve; miền Đ&ocirc;ng v&agrave; miền T&acirc;y. Về sau, bến xe n&agrave;y mới được dời đi chỗ kh&aacute;c.<br /><br />Chợ Bến Th&agrave;nh hoạt động li&ecirc;n tục trong 70 năm. Từ ng&agrave;y 1 th&aacute;ng 7 đến 15 th&aacute;ng 8 năm 1985, chợ Bến Th&agrave;nh được cải tạo v&agrave; sữa chữa lớn.<br /><br />&nbsp;&nbsp;&nbsp; * Địa chỉ: Cửa Nam (nằm giữa c&aacute;c đường Phan Bội Ch&acirc;u - Phan Chu Trinh - L&ecirc; Th&aacute;nh T&ocirc;n - C&ocirc;ng trường Qu&aacute;ch Thị Trang) - Phường Bến Th&agrave;nh - Quận 1<br />&nbsp;&nbsp;&nbsp; * Điện thoại: +84.8.8292096<br />&nbsp;&nbsp;&nbsp; * Diện t&iacute;ch: 13.056 m&sup2;<br />&nbsp;&nbsp;&nbsp; * Ng&agrave;nh h&agrave;ng kinh doanh chủ yếu: Quần &aacute;o, vải sợi, giầy d&eacute;p, thời trang, h&agrave;ng thủ c&ocirc;ng mỹ nghệ, thực phẩm tươi sống, tr&aacute;i c&acirc;y, hoa tươi.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce021481af_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Chợ Bến Th&agrave;nh<br /></em></p>', 'Wikipedia', '', '18:24:41', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (123, 3, 4, 'Hoan Kiem Lake', '', 'hoan-kiem-lake', '<p style="text-align: justify;"><strong>Hoan Kiem Lake</strong> (Vietnamese: Hồ Ho&agrave;n Kiếm, meaning "Lake of the Returned Sword" or "Lake of the Restored Sword", also known as Hồ Gươm - Sword Lake) is a lake in the historical center of Hanoi, the capital city of Vietnam. The lake is one of the major scenic spots in the city and serves as a focal point for its public life.</p>', '<h2>History</h2>\r\n<p style="text-align: justify;">According to the legend, emperor L&ecirc; Lợi handed a magic sword called Heaven''s Will which brought him victory in his revolt against the Chinese Ming Dynasty back to the Golden Turtle God (Kim Qui) in the lake and hence gave it its present name (the lake was formerly known as "Luc Thuy" meaning "Green Water"). The Tortoise Tower (Thap R&ugrave;a) standing on a small island near the center of lake is linked to the legend.</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7c880db619c_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Turtle Tower</em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: justify;">Large soft-shell turtles, either of the species Rafetus swinhoei or a separate species named Rafetus leloii in honor of the emperor, have been sighted in the lake. The species is critically endangered and the number of individuals in the lake is unclear.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7c880ed46f3_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>The Huc Bridge</em></p>\r\n<p style="text-align: justify;"><br />Near the northern shore of the lake lies Jade Island on which the Ngoc Son Temple (Jade Mountain Temple) stands. The temple was erected in the 18th century. It honors the 13-century military leader Tran Hung Dao who distinguished himself in the fight against the Yuan Dynasty, Van Xuong, a scholar, and Nguyen Van Sieu, a Confucian master and famous writer in charge of repairs made to the temple in 1864. Jade Island is connected to the shore by the wooden red-painted Huc Bridge (The Huc, meaning Morning Sunlight Bridge).</p>', 'Wikipedia', '', '18:25:43', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (124, 4, 14, 'Hồ Hoàn Kiếm', '', 'ho-hoan-kiem', '<p>Hồ Ho&agrave;n Kiếm hay hồ Gươm l&agrave; một hồ nước ngọt nằm giữa thủ đ&ocirc; H&agrave; Nội. T&ecirc;n hồ cũng được đặt cho một quận của H&agrave; Nội, quận Ho&agrave;n Kiếm.</p>', '<h2><span style="font-weight: normal; font-size: 10px;">C&aacute;ch đ&acirc;y khoảng 6 thế kỷ, hồ Gươm gồm hai phần chạy d&agrave;i từ phố H&agrave;ng Đ&agrave;o, qua Hai B&agrave; Trưng, L&yacute; Thường Kiệt tới phố H&agrave;ng Chuối, th&ocirc;ng với s&ocirc;ng Hồng. Nước hồ quanh năm xanh biếc n&ecirc;n hồ Gươm cũng được gọi l&agrave; hồ Lục Thuỷ.</span></h2>\r\n<p style="text-align: justify;"><br />Tương truyền v&agrave;o thế kỷ 15 hồ được đổi t&ecirc;n th&agrave;nh hồ Ho&agrave;n Kiếm, gắn liền với truyền thuyết trả gươm thần cho R&ugrave;a V&agrave;ng, ghi lại thắng lợi của cuộc chiến đấu 10 năm của nh&acirc;n d&acirc;n Việt Nam chống lại qu&acirc;n Minh (1417-1427) dưới sự l&atilde;nh đạo của L&ecirc; Lợi. Truyền thuyết kể rằng khi L&ecirc; Lợi khởi nghĩa ở Lam Sơn (Thanh Ho&aacute;) c&oacute; m&ograve; được một lưỡi gươm, sau đ&oacute; lại nhặt được một c&aacute;i chu&ocirc;i ở ruộng c&agrave;y, gh&eacute;p lại th&agrave;nh thanh gươm, đặt t&ecirc;n l&agrave; Thuận Thi&ecirc;n. Gươm b&aacute;u n&agrave;y đ&atilde; theo L&ecirc; Lợi trong suốt thời gian kh&aacute;ng chiến chống giặc Minh. Khi l&ecirc;n ng&ocirc;i về đ&oacute;ng đ&ocirc; ở Thăng Long, trong một lần nh&agrave; vua đi chơi thuyền tr&ecirc;n hồ Lục Thuỷ, bỗng một con r&ugrave;a xuất hiện. L&ecirc; Th&aacute;i Tổ r&uacute;t gươm khỏi vỏ, giơ gươm ra th&igrave; gươm bay về ph&iacute;a con r&ugrave;a. R&ugrave;a ngậm gươm, lặn xuống đ&aacute;y hồ, v&agrave; từ đ&oacute; hồ Lục Thuỷ c&oacute; t&ecirc;n gọi mới l&agrave; hồ Ho&agrave;n Kiếm (trả gươm) hay hồ Gươm.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7c880db619c_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Hồ Ho&agrave;n Kiếm với Th&aacute;p R&ugrave;a</em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<h2><span style="font-weight: normal; font-size: 10px;">Hồ Ho&agrave;n Kiếm được du kh&aacute;ch cho l&agrave; một thắng cảnh của H&agrave; Nội. Quanh hồ trồng nhiều loại hoa v&agrave; c&acirc;y cảnh. Giữa hồ c&oacute; th&aacute;p R&ugrave;a, cạnh hồ c&oacute; đền Ngọc Sơn. Xung quanh hồ c&ograve;n c&oacute; những di t&iacute;ch lịch sử kh&aacute;c như tượng vua L&ecirc; Th&aacute;i Tổ, cầu Th&ecirc; H&uacute;c, th&aacute;p B&uacute;t, đền B&agrave; Kiệu,... b&ecirc;n cạnh những c&ocirc;ng tr&igrave;nh kiến tr&uacute;c hiện đại. To&agrave; nh&agrave; Bưu điện với th&aacute;p đồng hồ cổ k&iacute;nh in b&oacute;ng hồ Gươm đ&atilde; đi v&agrave;o l&ograve;ng nhiều người d&acirc;n H&agrave; Nội.</span></h2>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7c8c8a03f71_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Cầu Th&ecirc; H&uacute;c dẫn v&agrave;o Đền Ngọc Sơn</em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: justify;">Người d&acirc;n H&agrave; Nội sống ở khu vực quanh hồ c&oacute; th&oacute;i quen ra đ&acirc;y tập thể dục v&agrave;o s&aacute;ng sớm, đặc biệt l&agrave; v&agrave;o m&ugrave;a h&egrave;. Họ gọi c&aacute;c khu phố nằm quanh hồ l&agrave; Bờ Hồ.<br /><br />Th&aacute;p B&uacute;t (hay l&agrave; b&uacute;t th&aacute;p) nằm ở cạnh hồ, đối với đ&agrave;i nghi&ecirc;n nằm ở bờ hồ. Mỗi ng&agrave;y, b&oacute;ng của Th&aacute;p b&uacute;t ngả xuống chấm mực trong đ&agrave;i nghi&ecirc;n, tạo th&agrave;nh một biểu tượng rất đẹp cho học vấn: "Th&aacute;p B&uacute;t - đ&agrave;i nghi&ecirc;n - đề thơ l&ecirc;n trời xanh"<br /><br />Chu vi s&aacute;t bờ hồ l&agrave; khoảng 1750m, c&ograve;n chu vi đường xe cơ giới đi v&ograve;ng quanh hồ l&agrave; khoảng 2000m.</p>', 'Wikipedia', '', '18:26:00', 1, 'admin');


