INSERT INTO multimedia_set (set_id, slug, title, description, created_date, updated_date, created_user_id, created_user_name, num_views, num_comments, is_active, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large) VALUES (1, 'ha-long-bay', 'Ha Long Bay', '<p>Ha Long Bay (literally: "Descending Dragon Bay"; Vietnamese: Vnh H Long) is a UNESCO World Heritage Site, and a popular travel destination, located in Qung Ninh province, Vietnam. Administratively, the bay belongs to Ha Long City, Cm Ph town, and part of Van Don district. The bay features thousands of limestone karsts and isles in various sizes and shapes. Ha Long Bay is a center of a larger zone which includes B&aacute;i T Long bay to the northeast, and C&aacute;t B&agrave; islands to the southwest. These larger zones share similar geological, geographical, geomorphological, climate, and cultural characters.</p>', '12:04:20', NULL, 1, 'admin', 0, 0, 1, 'http://localhost/tomatocms/upload/multimedia/admin/2010/08/4c7c8d501df49_square.jpg', 'http://localhost/tomatocms/upload/multimedia/admin/2010/08/4c7c8d501df49_thumbnail.jpg', 'http://localhost/tomatocms/upload/multimedia/admin/2010/08/4c7c8d501df49_small.jpg', 'http://localhost/tomatocms/upload/multimedia/admin/2010/08/4c7c8d501df49_crop.jpg', 'http://localhost/tomatocms/upload/multimedia/admin/2010/08/4c7c8d501df49_medium.jpg', 'http://localhost/tomatocms/upload/multimedia/admin/2010/08/4c7c8d501df49_large.jpg');
INSERT INTO multimedia_set (set_id, slug, title, description, created_date, updated_date, created_user_id, created_user_name, num_views, num_comments, is_active, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large) VALUES (2, 'sapa', 'Sapa', '<p>Sa Pa District is located in Lao Cai Province, north-west Vietnam, and 350 km north-west of Hanoi, close to the border with China. The Hoang Lien Son range of mountains dominates the district, which is at the eastern extremity of the Himalayas. This range includes Vietnam''s highest mountain, Fan Si Pan, at a height of 3142m above sea level. The town of Sa Pa lies at an altitude of about 1600 m. The climate is moderate and rainy in summer (May&mdash;August), and foggy and cold with occasional snowfalls in winter.</p>', '13:26:26', NULL, 1, 'admin', 0, 0, 1, 'http://localhost/tomatocms/upload/multimedia/admin/2010/08/4c7ca022b732a_square.jpg', 'http://localhost/tomatocms/upload/multimedia/admin/2010/08/4c7ca022b732a_thumbnail.jpg', 'http://localhost/tomatocms/upload/multimedia/admin/2010/08/4c7ca022b732a_small.jpg', 'http://localhost/tomatocms/upload/multimedia/admin/2010/08/4c7ca022b732a_crop.jpg', 'http://localhost/tomatocms/upload/multimedia/admin/2010/08/4c7ca022b732a_medium.jpg', 'http://localhost/tomatocms/upload/multimedia/admin/2010/08/4c7ca022b732a_large.jpg');


