INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (1, 1, 'Hanoi', '', 'hanoi', '<p>Hanoi estimated population nearly 6.5 million (2009),is the capital and second-largest city of Vietnam. October 2010 will officially mark 1000 years of the establishment of the city.</p>', '<h2>Climate</h2>\r\n<p>Hanoi features a warm humid subtropical climate (K&ouml;ppen Cwa) with plentiful precipitation. The city experiences the typical climate of northern Vietnam, where summers are hot and humid, and winters are, by national standards, relatively cool and dry. Summers, lasting from May to September, are hot and humid, receiving the majority of the annual 1,680 millimetres (66.1 in) of rainfall. The winters are short, relatively dry, and mild, while spring can bring light rains.</p>\r\n<h2>Landmarks</h2>\r\n<p>As the capital of Vietnam for almost a thousand years, Hanoi is considered to be one of cultural centres of Vietnam, where most of Vietnamese dynasties had left behind their imprint. Even though some relics have not survived through wars and time, the city still has many interesting cultural and historic monuments for visitors and residents alike. Even when the nation''s capital moved to Hu under the Nguyn Dynasty in 1802, the city of Hanoi continued to flourish, especially after the French took control in 1888 and modeled the city''s architecture to their tastes, lending an important aesthetic to the city''s rich stylistic heritage. The city boasts more than 1,000 years of history, and that of the past few hundred years has been well preserved.</p>\r\n<p>Hanoi hosts more cultural sites than any city in Vietnam, including over 600 pagodas and temples.</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7c7ca8ec1d0_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Turtle Tower in Hoan Kiem Lake, in central Hanoi</em></p>\r\n<h2>Cuisine</h2>\r\n<p>Hanoi has rich food traditions and many of Vietnam''s most famous dishes, such as ph, ch c&aacute;, b&aacute;nh cun and cm are thought to come from Hanoi. Perhaps most widely known is Ph, a simple rice noodle soup often eaten as a breakfast dish in the home or at streetside cafes, but also served in restaurants as a meal. Two varieties dominate the Hanoi scene: Ph B&ograve;, containing beef, and Ph G&agrave;, containing chicken.</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7c7d05e3ebf_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>B&aacute;nh cun Thanh Tri dish</em></p>\r\n<p>Hanoi has been selected as one of the top 10 cities for food in the world by Shermans Travel.[13] Vietnam''s national dish Ph has been also named as one of the Top5 streetfood in the world by globalpost.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c7ca8ec1d0_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c7ca8ec1d0_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c7ca8ec1d0_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c7ca8ec1d0_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c7ca8ec1d0_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c7ca8ec1d0_large.jpg', 'active', 8, '10:57:03', 1, 'admin', '14:40:02', 1, 'admin', 1, 'admin', '10:57:11', 0, 0, 0, 0, NULL, 1, 'en_US');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (2, 13, 'H Ni', '', 'ha-noi', '<p>H&agrave; Ni l&agrave; th &ocirc; ca Vit Nam l&agrave; th&agrave;nh ph c&oacute; din t&iacute;ch ln nht v&agrave; &ocirc;ng d&acirc;n ng th hai sau Th&agrave;nh ph H Ch&iacute; Minh.</p>', '<h2>Kh&iacute; hu</h2>\r\n<p>Kh&iacute; hu H&agrave; Ni ti&ecirc;u biu cho v&ugrave;ng Bc B vi c im ca kh&iacute; hu nhit i gi&oacute; m&ugrave;a m, m&ugrave;a h&egrave; n&oacute;ng, ma nhiu v&agrave; m&ugrave;a &ocirc;ng lnh, &iacute;t ma. Thuc v&ugrave;ng nhit i, th&agrave;nh ph quanh nm tip nhn lng bc x Mt Tri rt di d&agrave;o v&agrave; c&oacute; nhit  cao. V&agrave; do t&aacute;c ng ca bin, H&agrave; Ni c&oacute;  m v&agrave; lng ma kh&aacute; ln, trung b&igrave;nh 114 ng&agrave;y ma mt nm.</p>\r\n<p>Mt c im r&otilde; n&eacute;t ca kh&iacute; hu H&agrave; Ni l&agrave; s thay i v&agrave; kh&aacute;c bit ca hai m&ugrave;a n&oacute;ng, lnh. M&ugrave;a n&oacute;ng k&eacute;o d&agrave;i t th&aacute;ng 5 ti th&aacute;ng 9, k&egrave;m theo ma nhiu, nhit  trung b&igrave;nh 29,2 &deg;C. T th&aacute;ng 11 ti th&aacute;ng 3 nm sau l&agrave; kh&iacute; hu ca m&ugrave;a &ocirc;ng vi nhit  trung b&igrave;nh 15,2 &deg;C. C&ugrave;ng vi hai thi k chuyn tip v&agrave;o th&aacute;ng 4 v&agrave; th&aacute;ng 10, th&agrave;nh ph c&oacute;  bn m&ugrave;a xu&acirc;n, h, thu v&agrave; &ocirc;ng.</p>\r\n<h2>C&aacute;c c&ocirc;ng tr&igrave;nh kin tr&uacute;c</h2>\r\n<p>Trc khi tr th&agrave;nh mt trung t&acirc;m ch&iacute;nh tr &ndash; v&agrave;o th k 5 vi triu i nh&agrave; Tin L&yacute; &ndash; H&agrave; Ni &atilde; l&agrave; mt trung t&acirc;m ca Pht gi&aacute;o vi c&aacute;c thin ph&aacute;i danh ting. Theo vn bia, t gia th k 6, ch&ugrave;a Trn Quc c x&acirc;y dng tr&ecirc;n b&atilde;i Y&ecirc;n Hoa ngo&agrave;i s&ocirc;ng Hng, ti 1615 do b&atilde;i s&ocirc;ng b l, ch&ugrave;a mi di v a im hin nay. n th k 11, vi s xut hin ca kinh &ocirc; Thng Long, khi Pht gi&aacute;o tr th&agrave;nh quc gi&aacute;o, c&aacute;c ch&ugrave;a chin, thin vin, s tng mi ph&aacute;t trin thc s mnh m. Trong nhiu th k, H&agrave; Ni tip tc x&acirc;y dng c&aacute;c ng&ocirc;i ch&ugrave;a, trong &oacute; mt s vn tn ti ti ng&agrave;y nay. C&oacute; th k ti c&aacute;c ng&ocirc;i ch&ugrave;a ni ting nh ch&ugrave;a Mt Ct x&acirc;y ln u nm 1049, ch&ugrave;a L&aacute;ng t th k 12, ch&ugrave;a Qu&aacute;n S, ch&ugrave;a Kim Li&ecirc;n xut hin v&agrave;o th k 17. Tuy vy, hu ht c&aacute;c ng&ocirc;i ch&ugrave;a trong ni &ocirc; ng&agrave;y nay u c x&acirc;y dng li v&agrave;o th k 19. Nhng triu i L&yacute;, Trn, L&ecirc;  li rt &iacute;t du t&iacute;ch.</p>\r\n<p>V&ugrave;ng ngoi th&agrave;nh H&agrave; Ni cng c&oacute; nhiu ng&ocirc;i ch&ugrave;a ni ting. Thuc x&atilde; Hng Sn, huyn M c, mt qun th vn h&oacute;a&ndash;t&ocirc;n gi&aacute;o gm h&agrave;ng chc ng&ocirc;i ch&ugrave;a th Pht, c&aacute;c ng&ocirc;i n th thn, c&aacute;c ng&ocirc;i &igrave;nh th t&iacute;n ngng n&ocirc;ng nghip... thng c gi chung l&agrave; Ch&ugrave;a Hng. T l&acirc;u, di t&iacute;ch n&agrave;y &atilde; tr th&agrave;nh mt a im du lch quan trng, c bit vi l hi Ch&ugrave;a Hng c t chc v&agrave;o mi m&ugrave;a xu&acirc;n. C&aacute;ch trung t&acirc;m H&agrave; Ni khong 20 km v ph&iacute;a T&acirc;y Nam, Ch&ugrave;a Thy nm tr&ecirc;n a phn ca x&atilde; S&agrave;i Sn, huyn Quc Oai. Ng&ocirc;i ch&ugrave;a, thng gn lin vi t&ecirc;n tui v thin s T o Hnh, l&agrave; mt di sn kin tr&uacute;c, vn h&oacute;a, lch s gi&aacute; tr. Ging nh Ch&ugrave;a Hng, l hi Ch&ugrave;a Thy cng c t chc v&agrave;o m&ugrave;a xu&acirc;n, u th&aacute;ng 3 h&agrave;ng nm</p>\r\n<p>C&ugrave;ng vi c&aacute;c ng&ocirc;i ch&ugrave;a, H&agrave; Ni c&ograve;n c&oacute; kh&ocirc;ng &iacute;t n th o L&atilde;o, o Khng hay c&aacute;c thn bo h nh Thng Long t trn, Vn Miu-Quc T Gi&aacute;m, n Ngc Sn.. Trong khu ph c c&ograve;n nhiu ng&ocirc;i &igrave;nh vn c nhng ngi d&acirc;n thng xuy&ecirc;n ti lui ti b&agrave;y t l&ograve;ng th&agrave;nh k&iacute;nh. C c gi&aacute;o theo ch&acirc;n nhng ngi ch&acirc;u &Acirc;u v&agrave;o Vit Nam, gi&uacute;p H&agrave; Ni c&oacute; c Nh&agrave; th Ln, Nh&agrave; th Ca Bc, Nh&agrave; th H&agrave;m Long</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7c807626d2f_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Ch&ugrave;a Mt Ct, mt c&ocirc;ng tr&igrave;nh c hin nm trong khu ph Ph&aacute;p</em></p>\r\n<h2>m thc</h2>\r\n<p>L&agrave; trung t&acirc;m vn h&oacute;a ca c min Bc t nhiu th k, ti H&agrave; Ni c&oacute; th t&igrave;m thy v&agrave; thng thc nhng m&oacute;n n ca nhiu v&ugrave;ng t kh&aacute;c, nhng m thc th&agrave;nh ph cng n&oacute; nhng n&eacute;t ri&ecirc;ng bit. Cm l&agrave;ng V&ograve;ng c nhng ngi d&acirc;n ca ng&ocirc;i l&agrave;ng c&ugrave;ng t&ecirc;n thuc qun Cu Giy l&agrave;m c trng bi m&ugrave;i thm v&agrave; m&agrave;u sc. Cm l&agrave;m t ging np v&agrave;ng gt khi c&ograve;n non, g&oacute;i trong nhng t&agrave;u l&aacute; sen m&agrave;u ngc thch v&agrave; c nhng ngi b&aacute;n h&agrave;ng rao b&aacute;n ngay t s&aacute;ng sm. Tuy ph bin nht l&agrave; cm ti, nhng m&oacute;n n n&agrave;y c&ograve;n c ch bin th&agrave;nh m&oacute;n ch cm. &acirc;y cng l&agrave; mt m&oacute;n qu&agrave; c d&ugrave;ng trong c&aacute;c dp vui</p>\r\n<p>Thanh Tr&igrave;, mt v&ugrave;ng ngoi &ocirc; kh&aacute;c, ni ting vi m&oacute;n b&aacute;nh cun. B&aacute;nh c l&agrave;m t go gi&eacute; c&aacute;nh, t&aacute;m thm, tr&aacute;ng mng nh t giy. Nhng ph n v&ugrave;ng Thanh Tr&igrave; cho b&aacute;nh v&agrave;o th&uacute;ng, i tr&ecirc;n u v&agrave; i rao khp c&aacute;c ng&otilde; ph ca H&agrave; Ni. B&aacute;nh xp trong th&uacute;ng, tng lp gi nhau tr&ecirc;n nhng t&agrave;u l&aacute; chui. Khi n, b&aacute;nh c b&oacute;c tng lp ri cun li, b&agrave;y tr&ecirc;n nhng chic a. M&oacute;n b&aacute;nh cun Thanh Tr&igrave; c n c&ugrave;ng vi loi nc mm pha theo c&ocirc;ng thc c bit ca ngi Thanh Tr&igrave;, u ph r&aacute;n n&oacute;ng, ch qu. Ng&agrave;y nay, b&aacute;nh c&ograve;n c n vi tht ba ch quay gi&ograve;n</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7c80781db03_crop.JPG" alt="" /></p>\r\n<p style="text-align: center;"><em>Mt b&aacute;t ph b&ograve; ch&iacute;n n c&ugrave;ng vi quy.</em></p>\r\n<p>Mt m&oacute;n n kh&aacute;c ca H&agrave; Ni, tuy xut hin cha l&acirc;u nhng &atilde; ni ting, l&agrave; ch c&aacute; L&atilde; Vng. V&agrave;o thi Ph&aacute;p thuc, gia &igrave;nh h o&agrave;n ph H&agrave;ng Sn, ng&agrave;y nay l&agrave; ph Ch C&aacute;, &atilde; to n&ecirc;n mt m&oacute;n n m&agrave; danh ting ca n&oacute; l&agrave;m thay i c t&ecirc;n con ph. Ch c l&agrave;m t tht c&aacute; lng &ndash; hoc c&aacute; qu, c&aacute; nheo nhng s k&eacute;m ngon hn &ndash; th&aacute;i mng p vi nc ring, ngh, m, ht ti&ecirc;u, nc mm ri kp v&agrave;o cp tre nng tr&ecirc;n l&ograve; than ngay tr&ecirc;n b&agrave;n n ca thc kh&aacute;ch. Ch c&aacute; L&atilde; Vng phi n n&oacute;ng, k&egrave;m vi b&aacute;nh a nng hay b&uacute;n ri, lc rang, rau m&ugrave;i, h&uacute;ng l&aacute;ng, th&igrave; l&agrave;, h&agrave;nh c ti ch nh, chm vi mm t&ocirc;m.</p>\r\n<p>Ph l&agrave; m&oacute;n n rt ph bin  Vit Nam, nhng ph H&agrave; Ni c&oacute; nhng c&aacute;ch ch bin c trng ri&ecirc;ng. Ph H&agrave; Ni mang v ngt ca xng b&ograve;, tht va ch&iacute;n n   vn do m&agrave; kh&ocirc;ng dai, m&agrave;u nc ph trong, b&aacute;nh ph mng v&agrave; mm. Sau khi c trn qua nc n&oacute;ng, b&aacute;nh ph c d&agrave;n u trong b&aacute;t, b&ecirc;n tr&ecirc;n l&agrave; nhng l&aacute;t tht mng c&ugrave;ng h&agrave;nh hoa, rau thm. C&ugrave;ng vi thi gian, nhiu m&oacute;n ph mi xut hin vi nhng c&aacute;ch ch bin kh&aacute;c nhau, nh ph x&agrave;o, ph r&aacute;n...</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c807626d2f_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c807626d2f_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c807626d2f_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c807626d2f_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c807626d2f_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c807626d2f_large.jpg', 'active', 5, '11:10:25', 1, 'admin', '11:27:23', 1, 'admin', 1, 'admin', '11:10:35', 0, 0, 0, 0, NULL, 0, 'vi_VN');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (3, 4, 'Hoan Kiem Lake', '', 'hoan-kiem-lake', '<p style="text-align: justify;"><strong>Hoan Kiem Lake</strong> (Vietnamese: H Ho&agrave;n Kim, meaning "Lake of the Returned Sword" or "Lake of the Restored Sword", also known as H Gm - Sword Lake) is a lake in the historical center of Hanoi, the capital city of Vietnam. The lake is one of the major scenic spots in the city and serves as a focal point for its public life.</p>', '<h2>History</h2>\r\n<p style="text-align: justify;">According to the legend, emperor L&ecirc; Li handed a magic sword called Heaven''s Will which brought him victory in his revolt against the Chinese Ming Dynasty back to the Golden Turtle God (Kim Qui) in the lake and hence gave it its present name (the lake was formerly known as "Luc Thuy" meaning "Green Water"). The Tortoise Tower (Thap R&ugrave;a) standing on a small island near the center of lake is linked to the legend.</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7c880db619c_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Turtle Tower</em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: justify;">Large soft-shell turtles, either of the species Rafetus swinhoei or a separate species named Rafetus leloii in honor of the emperor, have been sighted in the lake. The species is critically endangered and the number of individuals in the lake is unclear.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7c880ed46f3_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>The Huc Bridge</em></p>\r\n<p style="text-align: justify;"><br />Near the northern shore of the lake lies Jade Island on which the Ngoc Son Temple (Jade Mountain Temple) stands. The temple was erected in the 18th century. It honors the 13-century military leader Tran Hung Dao who distinguished himself in the fight against the Yuan Dynasty, Van Xuong, a scholar, and Nguyen Van Sieu, a Confucian master and famous writer in charge of repairs made to the temple in 1864. Jade Island is connected to the shore by the wooden red-painted Huc Bridge (The Huc, meaning Morning Sunlight Bridge).</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c881016a8d_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c881016a8d_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c881016a8d_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c881016a8d_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c881016a8d_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c881016a8d_large.jpg', 'active', 2, '11:44:51', 1, 'admin', '18:25:43', 1, 'admin', 1, 'admin', '11:44:59', 0, 0, 0, 0, NULL, 0, 'en_US');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (4, 14, 'H Hon Kim', '', 'ho-hoan-kiem', '<p>H Ho&agrave;n Kim hay h Gm l&agrave; mt h nc ngt nm gia th &ocirc; H&agrave; Ni. T&ecirc;n h cng c t cho mt qun ca H&agrave; Ni, qun Ho&agrave;n Kim.</p>', '<h2><span style="font-weight: normal; font-size: 10px;">C&aacute;ch &acirc;y khong 6 th k, h Gm gm hai phn chy d&agrave;i t ph H&agrave;ng &agrave;o, qua Hai B&agrave; Trng, L&yacute; Thng Kit ti ph H&agrave;ng Chui, th&ocirc;ng vi s&ocirc;ng Hng. Nc h quanh nm xanh bic n&ecirc;n h Gm cng c gi l&agrave; h Lc Thu.</span></h2>\r\n<p style="text-align: justify;"><br />Tng truyn v&agrave;o th k 15 h c i t&ecirc;n th&agrave;nh h Ho&agrave;n Kim, gn lin vi truyn thuyt tr gm thn cho R&ugrave;a V&agrave;ng, ghi li thng li ca cuc chin u 10 nm ca nh&acirc;n d&acirc;n Vit Nam chng li qu&acirc;n Minh (1417-1427) di s l&atilde;nh o ca L&ecirc; Li. Truyn thuyt k rng khi L&ecirc; Li khi ngha  Lam Sn (Thanh Ho&aacute;) c&oacute; m&ograve; c mt li gm, sau &oacute; li nht c mt c&aacute;i chu&ocirc;i  rung c&agrave;y, gh&eacute;p li th&agrave;nh thanh gm, t t&ecirc;n l&agrave; Thun Thi&ecirc;n. Gm b&aacute;u n&agrave;y &atilde; theo L&ecirc; Li trong sut thi gian kh&aacute;ng chin chng gic Minh. Khi l&ecirc;n ng&ocirc;i v &oacute;ng &ocirc;  Thng Long, trong mt ln nh&agrave; vua i chi thuyn tr&ecirc;n h Lc Thu, bng mt con r&ugrave;a xut hin. L&ecirc; Th&aacute;i T r&uacute;t gm khi v, gi gm ra th&igrave; gm bay v ph&iacute;a con r&ugrave;a. R&ugrave;a ngm gm, ln xung &aacute;y h, v&agrave; t &oacute; h Lc Thu c&oacute; t&ecirc;n gi mi l&agrave; h Ho&agrave;n Kim (tr gm) hay h Gm.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7c880db619c_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>H Ho&agrave;n Kim vi Th&aacute;p R&ugrave;a</em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<h2><span style="font-weight: normal; font-size: 10px;">H Ho&agrave;n Kim c du kh&aacute;ch cho l&agrave; mt thng cnh ca H&agrave; Ni. Quanh h trng nhiu loi hoa v&agrave; c&acirc;y cnh. Gia h c&oacute; th&aacute;p R&ugrave;a, cnh h c&oacute; n Ngc Sn. Xung quanh h c&ograve;n c&oacute; nhng di t&iacute;ch lch s kh&aacute;c nh tng vua L&ecirc; Th&aacute;i T, cu Th&ecirc; H&uacute;c, th&aacute;p B&uacute;t, n B&agrave; Kiu,... b&ecirc;n cnh nhng c&ocirc;ng tr&igrave;nh kin tr&uacute;c hin i. To&agrave; nh&agrave; Bu in vi th&aacute;p ng h c k&iacute;nh in b&oacute;ng h Gm &atilde; i v&agrave;o l&ograve;ng nhiu ngi d&acirc;n H&agrave; Ni.</span></h2>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7c8c8a03f71_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Cu Th&ecirc; H&uacute;c dn v&agrave;o n Ngc Sn</em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: justify;">Ngi d&acirc;n H&agrave; Ni sng  khu vc quanh h c&oacute; th&oacute;i quen ra &acirc;y tp th dc v&agrave;o s&aacute;ng sm, c bit l&agrave; v&agrave;o m&ugrave;a h&egrave;. H gi c&aacute;c khu ph nm quanh h l&agrave; B H.<br /><br />Th&aacute;p B&uacute;t (hay l&agrave; b&uacute;t th&aacute;p) nm  cnh h, i vi &agrave;i nghi&ecirc;n nm  b h. Mi ng&agrave;y, b&oacute;ng ca Th&aacute;p b&uacute;t ng xung chm mc trong &agrave;i nghi&ecirc;n, to th&agrave;nh mt biu tng rt p cho hc vn: "Th&aacute;p B&uacute;t - &agrave;i nghi&ecirc;n -  th l&ecirc;n tri xanh"<br /><br />Chu vi s&aacute;t b h l&agrave; khong 1750m, c&ograve;n chu vi ng xe c gii i v&ograve;ng quanh h l&agrave; khong 2000m.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c881016a8d_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c881016a8d_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c881016a8d_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c881016a8d_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c881016a8d_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c881016a8d_large.jpg', 'active', 3, '11:52:33', 1, 'admin', '18:26:00', 1, 'admin', 1, 'admin', '11:54:59', 0, 0, 0, 0, NULL, 0, 'vi_VN');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (5, 1, 'Sa Pa', '', 'sa-pa', '<p>Sapa is a frontier town and capital of Sa Pa District in the L&agrave;o Cai province in northwest Vietnam. It is one of the main market towns in the area, where many ethnic minority groups such as H''mong, Dao and Tay live.</p>', '<p>Sa Pa District is located in Lao Cai Province, north-west Vietnam, and 350 km north-west of Hanoi, close to the border with China. The Hoang Lien Son range of mountains dominates the district, which is at the eastern extremity of the Himalayas. This range includes Vietnam''s highest mountain, Fan Si Pan, at a height of 3142m above sea level. The town of Sa Pa lies at an altitude of about 1600 m. The climate is moderate and rainy in summer (May&mdash;August), and foggy and cold with occasional snowfalls in winter.</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7c9d36eeb3f_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Sa Pa town</em></p>\r\n<p>Sa Pa is a quiet mountain town and home to a great diversity of ethnic minority peoples. The total population of 36,000 consists mostly of minority groups. Besides the Kinh (Viet) people (15%) there are mainly 5 ethnic groups in Sapa: Hmong 52%, Dao 25%, Tay 5%, Giay 2% and a small number of Xa Pho. Approximately 7,000 live in Sapa, the other 36,000 being scattered in small communes throughout the district.</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7c9de6e9139_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>View on the mountains from downtown Sapa</em></p>\r\n<p>Most of the ethnic minority people work their land on sloping terraces since the vast majority of the land is mountainous. Their staple foods are rice and corn. Rice, by its very nature of being a labour intensive crop, makes the daily fight for survival paramount. The unique climate in Sapa has a major influence on the ethnic minorities who live in the area. With sub-tropical summers, temperate winters and 160 days of mist annually, the influence on agricultural yields and health related issues are significant.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c9d36eeb3f_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c9d36eeb3f_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c9d36eeb3f_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c9d36eeb3f_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c9d36eeb3f_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c9d36eeb3f_large.jpg', 'active', 3, '13:16:01', 1, 'admin', NULL, NULL, NULL, 1, 'admin', '13:16:08', 0, 0, 0, 0, NULL, 1, 'en_US');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (6, 13, 'Sa Pa', '', 'sa-pa', '<p>Sa Pa l&agrave; mt th trn v&agrave; cng l&agrave; mt khu ngh m&aacute;t ni ting thuc huyn Sa Pa, tnh L&agrave;o Cai, Vit Nam.</p>', '<p>Nm  ph&iacute;a T&acirc;y Bc ca Vit Nam, th trn Sa Pa   cao 1.600 m&eacute;t so vi mc nc bin, c&aacute;ch th&agrave;nh ph L&agrave;o Cai 38 km v&agrave; 376 km t&iacute;nh t H&agrave; Ni. Ngo&agrave;i con ng ch&iacute;nh t th&agrave;nh ph L&agrave;o Cai,  ti Sa Pa c&ograve;n mt tuyn giao th&ocirc;ng kh&aacute;c, quc l 4D ni t x&atilde; B&igrave;nh L, Lai Ch&acirc;u. Mc d&ugrave; phn ln c d&acirc;n huyn Sa Pa l&agrave; nhng ngi d&acirc;n tc thiu s, nhng th trn li tp trung ch yu nhng ngi Kinh sinh sng bng n&ocirc;ng nghip v&agrave; dch v du lch.</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7c9f51795a3_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Rung bc thang</em></p>\r\n<p>Sa Pa c&oacute; kh&iacute; hu mang sc th&aacute;i &ocirc;n i v&agrave; cn nhit i, kh&ocirc;ng kh&iacute; m&aacute;t m quanh nm. Thi tit  th trn mt ng&agrave;y c&oacute;  bn m&ugrave;a: bui s&aacute;ng l&agrave; tit tri m&ugrave;a xu&acirc;n, bui tra tit tri nh v&agrave;o h, thng c&oacute; nng nh, kh&iacute; hu du m&aacute;t, bui chiu m&acirc;y v&agrave; sng ri xung to cm gi&aacute;c l&agrave;nh lnh nh tri thu v&agrave; ban &ecirc;m l&agrave; c&aacute;i r&eacute;t ca m&ugrave;a &ocirc;ng. Nhit  kh&ocirc;ng kh&iacute; trung b&igrave;nh nm ca Sa Pa l&agrave; 15 &deg;C. M&ugrave;a h&egrave;, th trn kh&ocirc;ng phi chu c&aacute;i nng gay gt nh v&ugrave;ng ng bng ven bin, khong 13 &deg;C &ndash; 15 &deg;C v&agrave;o ban &ecirc;m v&agrave; 20 &deg;C &ndash; 25 &deg;C v&agrave;o ban ng&agrave;y. M&ugrave;a &ocirc;ng thng c&oacute; m&acirc;y m&ugrave; bao ph v&agrave; lnh, nhit  c&oacute; khi xung di 0 &deg;C, &ocirc;i khi c&oacute; tuyt ri. Lng ma trung b&igrave;nh h&agrave;ng nm  &acirc;y khong t 1.800 n 2.200 mm, tp trung nhiu nht v&agrave;o khong thi gian t th&aacute;ng 5 ti th&aacute;ng 8</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7c9ee47d19c_medium.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Nh&agrave; th &aacute; ti Sa Pa</em></p>\r\n<p>Th trn Sa Pa l&agrave; mt trong nhng ni him hoi ca Vit Nam c&oacute; tuyt. Trong khong thi gian t 1971 ti 2008, 14 ln tuyt ri ti Sa Pa. Ln tuyt ri mnh nht v&agrave;o ng&agrave;y 13 th&aacute;ng 2 nm 1968, li&ecirc;n tc t 3 gi s&aacute;ng n 14 gi c&ugrave;ng ng&agrave;y, d&agrave;y ti 20 cm.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c9f51795a3_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c9f51795a3_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c9f51795a3_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c9f51795a3_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c9f51795a3_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7c9f51795a3_large.jpg', 'active', 3, '13:21:29', 1, 'admin', NULL, NULL, NULL, 1, 'admin', '13:21:39', 0, 0, 0, 0, NULL, 0, 'vi_VN');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (7, 5, 'Perfume River', '', 'perfume-river', '<p style="text-align: justify;"><strong>The Perfume River</strong> (Vietnamese: S&ocirc;ng Hng or Hng Giang; H&aacute;n n&ocirc;m: ) is a river that crosses the city of Hu, in the central Vietnamese province of Tha Thi&ecirc;n Hu.</p>', '<h2 style="text-align: justify;">Etymology</h2>\r\n<p style="text-align: justify;">In the autumn, flowers from orchards upriver from Hu fall into the water, giving the river a perfume-like aroma.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca04e32d9f_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Hng River in Hu</em></p>\r\n<h2 style="text-align: justify;">Source and flow</h2>\r\n<p style="text-align: justify;">The Perfume River has two sources, both of which begin in the D&atilde;y Trng Sn mountain range and meet at Bang Lang Fork. The Ta Trach (Left Tributary) originates in the Truong Dong mountains and flows northwest towards Bang Lang Fork. The shorter Huu Trach (Right Tributary) flows past the Tuan ferry landing towards Bang Lang Fork.</p>\r\n<p style="text-align: justify;">The river then flows in south-north direction past the temples of Hon Chen and Ngoc Tran and then turns north-west, meandering through the Nguyet Bieu and Luong Quan plains. Continuing on, the Huong River then flows to the north-east to Hu city, only an echo of Truong Son mountain, and passes the resting place of Nguyen emperors. The river of green water continues its itinerary, passing Hen Islet and various villages, crossing the Sinh junction, which is known as the capital of ancient Chau Hoa, before emptying into the Tam Giang Lagoon.</p>\r\n<p style="text-align: justify;">From Bang Lang to Thuan An estuary, the Perfume River is 30 km long and runs very slowly (as the river level is not much above sea level). The Perfume River darkens as it winds along the foot of Ngoc Tran Mountain - home of the Jade Cup Temple - where there is a very deep abyss.</p>\r\n<p style="text-align: justify;">The river snakes through the villages of Kim Long, Nguyet Bieu, Vy Da, Dong Ba, Gia Hoi, the Dinh market, Nam Pho, Bao Vinh, and through Hu.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2 style="text-align: justify;">Life around the river</h2>\r\n<p style="text-align: justify;">The locals who live beside the river in Hu are often very poor and wash their clothes and bathe in the water. Also, they earn money by dredging the river bed for sand. This sand is collected and then sold to cement manufacturers for a tiny price.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2 style="text-align: justify;"><span id="Surrounding_area" class="mw-headline">Surrounding area</span></h2>\r\n<p style="text-align: justify;">The 105m Ngu Binh Mountain has a striking and symmetric figure. On both sides of the Even Mount (Bang Son) are 2 small mounts called Ta Bat Son (Left Mount) and Huu Bat Son (Right Mount). After observing that Bang Son resembled a screen, the Nguyn Dynasty decided to build up Hu which became known as their "Forbidden Purple City." Emperor Gia Long approved the design of geomancers which chose this mount as a front altar of the imposing and solid defending wall system, and renamed it Ngu Binh.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca10576afd_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca10576afd_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca10576afd_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca10576afd_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca10576afd_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca10576afd_large.jpg', 'active', 2, '13:28:27', 1, 'admin', '13:38:31', 1, 'admin', 1, 'admin', '13:28:52', 0, 0, 0, 0, NULL, 0, 'en_US');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (8, 15, 'Sng Hng', '', 'song-huong', '<p>S&ocirc;ng Hng hay Hng Giang l&agrave; con s&ocirc;ng chy qua th&agrave;nh ph Hu  tnh Tha Thi&ecirc;n-Hu, min Trung Vit Nam.</p>', '<h2><span style="font-weight: normal; font-size: 10px;">S&ocirc;ng Hng c&oacute; hai ngun ch&iacute;nh v&agrave; u bt ngun t d&atilde;y n&uacute;i Trng Sn. D&ograve;ng ch&iacute;nh ca T Trch d&agrave;i khong 67 km, bt ngun t d&atilde;y Trng Sn &ocirc;ng, ven khu vc vn quc gia Bch M&atilde; chy theo hng t&acirc;y bc vi 55 th&aacute;c nc h&ugrave;ng v, qua th trn Nam &ocirc;ng ri sau &oacute; hp lu vi d&ograve;ng Hu Trch ti ng&atilde; ba Bng L&atilde;ng (khong 3 km v ph&iacute;a bc khu vc lng Minh Mng). Hu Trch d&agrave;i khong 60 km l&agrave; nh&aacute;nh ph, chy theo hng bc, qua 14 th&aacute;c nguy him v&agrave; vt qua ph&agrave; Tun  ti ng&atilde; ba Bng L&atilde;ng, ni hai d&ograve;ng n&agrave;y gp nhau v&agrave; to n&ecirc;n s&ocirc;ng Hng.</span></h2>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca04e32d9f_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>S&ocirc;ng Hng</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">T Bng L&atilde;ng n ca s&ocirc;ng Thun An, s&ocirc;ng Hng d&agrave;i 33 km v&agrave; chy rt chm (bi v&igrave; mc nc s&ocirc;ng kh&ocirc;ng cao hn my so vi mc nc bin). Khi chy quanh dc ch&acirc;n n&uacute;i Ngc Trn, sc nc s&ocirc;ng Hng xanh hn &ndash; &acirc;y l&agrave; a im in H&ograve;n Ch&eacute;n. Ti &acirc;y c&oacute; mt vc rt s&acirc;u.</p>\r\n<p style="text-align: justify;">S&ocirc;ng Hng rt p khi chi&ecirc;m ngng n&oacute; t ngun v&agrave; khi n&oacute; chy quanh c&aacute;c ch&acirc;n n&uacute;i, xuy&ecirc;n qua c&aacute;c c&aacute;nh rng rm v&agrave; mang theo hng thm ca h thc vt nhit i. Con s&ocirc;ng chy chm qua nhng l&agrave;ng mc xanh ti v&agrave; r&acirc;m m&aacute;t nh Kim Long, Nguyt Biu, V D, &ocirc;ng Ba, Gia Hi, ch Dinh, Nam Ph, Bao Vinh v&agrave; ho&agrave; ln v&agrave;o vi hng thm ca hoa c Hu... Con s&ocirc;ng vi sc xanh lung linh, trong tro nh mt vi&ecirc;n ngc di &aacute;nh mt tri. N&oacute; tng l&agrave; ngun cm x&uacute;c ca bao th h du kh&aacute;ch khi h i thuyn dc theo s&ocirc;ng Hng  nh&igrave;n ngm phong cnh n&ecirc;n th v&agrave; lng nghe nhng iu ca Hu truyn thng trong &ecirc;m tnh mch.</p>\r\n<p style="text-align: justify;">C&aacute;c c&ocirc;ng tr&igrave;nh kin tr&uacute;c hai b&ecirc;n b s&ocirc;ng gm th&agrave;nh qu&aacute;ch, th t, vn tc, ch&ugrave;a chin, th&aacute;p v&agrave; n &agrave;i... &aacute;nh phn chiu ca ch&uacute;ng tr&ecirc;n d&ograve;ng nc khin con s&ocirc;ng thm ch&iacute; c&ograve;n mang th&ecirc;m nhiu cht th v&agrave; t&iacute;nh nhc. Nhiu ngi lu&ocirc;n gn lin s thanh b&igrave;nh, thanh lch v&agrave; cnh vt lng l ca Hu vi d&ograve;ng S&ocirc;ng Hng.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">N&uacute;i Ng B&igrave;nh (c&ograve;n gi l&agrave; Bng sn) cao 105 m&eacute;t c&oacute; h&igrave;nh d&aacute;ng c&acirc;n xng.  hai b&ecirc;n Bng Sn l&agrave; hai ngn n&uacute;i nh t&ecirc;n l&agrave; T Bt Sn v&agrave; Hu Bt Sn. C&oacute; &yacute; kin cho rng sau khi quan s&aacute;t thy Bng Sn tr&ocirc;ng ging nh mt tm b&igrave;nh phong, nh&agrave; Nguyn &atilde; quyt nh chn Hu l&agrave;m ni x&acirc;y dng Kinh th&agrave;nh. Vua Gia Long &atilde; ng &yacute; vi nhng thy a l&yacute; chn ngn n&uacute;i &oacute; l&agrave;m &aacute;n th ph&iacute;a trc ca ca h thng tng bao chc chn c&oacute; chc nng bo v th&agrave;nh, v&agrave; i t&ecirc;n n&oacute; l&agrave; Ng B&igrave;nh.</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca2d953f6b_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Ho&agrave;ng h&ocirc;n tr&ecirc;n s&ocirc;ng Hng</em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: justify;">C&ugrave;ng vi s&ocirc;ng Hng, n&uacute;i Ng B&igrave;nh l&agrave; mt thng cnh thi&ecirc;n nhi&ecirc;n  Hu. T l&acirc;u, n&uacute;i Ng v&agrave; s&ocirc;ng Hng &atilde; c coi l&agrave; nhng biu tng ca Hu, Hu thng c gi l&agrave; &ldquo;V&ugrave;ng t ca s&ocirc;ng Hng v&agrave; n&uacute;i Ng&rdquo;.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca10576afd_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca10576afd_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca10576afd_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca10576afd_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca10576afd_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca10576afd_large.jpg', 'active', 3, '13:36:43', 1, 'admin', '13:42:52', 1, 'admin', 1, 'admin', '13:36:59', 0, 0, 0, 0, NULL, 0, 'vi_VN');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (9, 4, 'One Pillar Pagoda', '', 'one-pillar-pagoda', '<p>The One Pillar Pagoda (Vietnamese: Ch&ugrave;a Mt Ct, formally Di&ecirc;n Hu t  or Li&ecirc;n Hoa &agrave;i ) is a historic Buddhist temple in Hanoi, the capital of Vietnam. It is regarded alongside the Perfume Temple, as one of Vietnam''s two most iconic temples.</p>', '<h2 style="text-align: justify;"><span id="History" class="mw-headline">History</span></h2>\r\n<p style="text-align: justify;">The temple was built by Emperor L&yacute; Th&aacute;i T&ocirc;ng, who ruled from 1028 to 1054. According to the court records, L&yacute; Th&aacute;i T&ocirc;ng was childless and dreamt that he met the bodhisattva Avalokiteshvara, who handed him a baby son while seated on a lotus flower. L&yacute; Th&aacute;i T&ocirc;ng then married a peasant girl that he had met and she bore him a son. The emperor constructed the temple in gratitude for this in 1049, having been told by a monk named Thin Tu to build the temple, by erecting a pillar in the middle of a lotus pond, similar to the one he saw in the dream.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca50b95de3_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>One Pillar Pagoda, Hanoi</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">The temple was located in what was then the T&acirc;y Cm Garden in Thch Bo, Vnh Thun district in the capital Thng Long (now known as Hanoi). Before the pagoda was opened, prayers were held for the longevity of the monarch. During the L&yacute; Dynasty era, the temple was the site of an annual royal ceremony on the occasion of Vesak, the birthday of Gautama Buddha. A Buddha-bathing ceremony was held annually by the monarch, and it attracted monks and laymen alike to the ceremony. The monarch would then free a bird, which was followed by the people.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca50c97cd8_crop.JPG" alt="" /></p>\r\n<p style="text-align: center;"><em>Steps leading up to the pagoda</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">The temple was renovated in 1105 by Emperor L&yacute; Nh&acirc;n T&ocirc;ng and a bell was cast and an installation was attempted in 1109. However, the bell, which was regarded as one of the four major capital works of Vietnam at the time, was much too large and heavy, and could not be installed. Since it could not be tolled while left on the ground, it was moved into the countryside and deposited in farmland adjacent to Nht Tr Temple. This land was widely inhabited by turtles, so the bell came to be known as Q<strong>uy in chung</strong>, which means Bell of the Turtle Farmland. At the start of the 15th century, Vietnam was invaded and occupied by the Ming Dynasty. In 1426, the future Emperor L&ecirc; Li attacked and dispersed the Chinese forces, and while the Ming were in retreat and low on weapons, their commanding general ordered that the bell be smelted, so that the copper could be used for manufacturing weaponry.</p>\r\n<p style="text-align: justify;">The temple is built of wood on a single stone pillar 1.25 m in diameter, and it is designed to resemble a lotus blossom, which is a Buddhist symbol of purity, since a lotus blossoms in a muddy pond. In 1954, the French Union forces destroyed the pagoda before withdrawing from Vietnam after the First Indochina War, It was rebuilt afterwards.</p>\r\n<p style="text-align: justify;">A replica was built in Th c in Saigon in the late 1950s and early 1960s.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca602211b7_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca602211b7_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca602211b7_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca602211b7_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca602211b7_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca602211b7_large.jpg', 'active', 2, '13:46:37', 1, 'admin', '13:49:43', 1, 'admin', 1, 'admin', '13:48:37', 0, 0, 0, 0, NULL, 0, 'en_US');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (10, 14, 'Cha Mt Ct', '', 'chua-mot-cot', '<p>Ch&ugrave;a Mt Ct hay Ch&ugrave;a Mt (gi theo H&aacute;n-Vit l&agrave; Nht Tr th&aacute;p ), c&ograve;n c&oacute; t&ecirc;n kh&aacute;c l&agrave; Di&ecirc;n Hu t () hoc Li&ecirc;n Hoa &agrave;i (, "&agrave;i hoa sen"), l&agrave; mt ng&ocirc;i ch&ugrave;a nm gia l&ograve;ng th &ocirc; H&agrave; Ni. &acirc;y l&agrave; ng&ocirc;i ch&ugrave;a c&oacute; kin tr&uacute;c c &aacute;o  Vit Nam.</p>', '<h2><span id="L.E1.BB.8Bch_s.E1.BB.AD" class="mw-headline">Lch s</span></h2>\r\n<p style="text-align: justify;">Ch&ugrave;a Di&ecirc;n Hu c vua L&yacute; Th&aacute;i T&ocirc;ng cho khi c&ocirc;ng x&acirc;y dng v&agrave;o m&ugrave;a &ocirc;ng th&aacute;ng mi (&acirc;m lch) nm K Su 1049, ni&ecirc;n hiu Thi&ecirc;n Cm Th&aacute;nh V&otilde; th nht.</p>\r\n<p style="text-align: justify;">Nhng theo cun H&agrave; Ni-di t&iacute;ch lch s v&agrave; danh thng, nh&oacute;m c&aacute;c nh&agrave; s hc inh Xu&acirc;n L&acirc;m, Do&atilde;n oan Trinh, Dng Trung Quc, Nguyn Quang &Acirc;n, Nguyn Thanh Mai, &agrave;m T&aacute;i Hng tin h&agrave;nh nghi&ecirc;n cu vn bia dng ti ch&ugrave;a nm Cnh Tr th 3 (1665), i vua L&ecirc; Huyn T&ocirc;ng, do T Khu L&ecirc; Tt t khc ghi, th&igrave; thy rng: ti v tr&iacute; ch&ugrave;a Mt Ct ng&agrave;y nay, v&agrave;o thi nh&agrave; ng (nm H&agrave;m Thng th nht) mt ct &aacute; tr&ecirc;n c&oacute; ng&ocirc;i lu ngc (vi tng Pht Quan &Acirc;m  trong) &atilde; c dng gia mt h nc vu&ocirc;ng. Vua L&yacute; Th&aacute;i T&ocirc;ng thng n cu nguyn, c ho&agrave;ng t ni d&otilde;i, lin tu sa li th&agrave;nh ch&ugrave;a, x&acirc;y th&ecirc;m mt ng&ocirc;i ch&ugrave;a b&ecirc;n cnh ch&ugrave;a Mt Ct (c&aacute;ch 10 m v ph&iacute;a T&acirc;y Nam) v&agrave; t t&ecirc;n c qun th ch&ugrave;a n&agrave;y l&agrave; Di&ecirc;n Hu t (vi ngha l&agrave; "ph&uacute;c l&agrave;nh d&agrave;i l&acirc;u").</p>\r\n<p style="text-align: justify;">Nm 1954, qu&acirc;n i Vin chinh Ph&aacute;p trc khi r&uacute;t khi H&agrave; Ni &atilde; cho t m&igrave;m ph&aacute; ch&ugrave;a Mt Ct. B&aacute;o Tia s&aacute;ng ng&agrave;y 10-9-1954 a tin "..., ch&ugrave;a Mt Ct di t&iacute;ch lit hng ca H&agrave; Th&agrave;nh &atilde; sp  sau mt ting long tri l t..." Sau khi tip qun th &ocirc;, B Vn h&oacute;a Vit Nam D&acirc;n ch Cng h&ograve;a &atilde; tin h&agrave;nh tr&ugrave;ng tu ln ch&ugrave;a Mt Ct v&agrave; ch&ugrave;a Di&ecirc;n Hu, x&acirc;y dng li ch&ugrave;a Mt Ct theo kin tr&uacute;c c.</p>\r\n<p style="text-align: justify;">Ch&ugrave;a Mt Ct ch c&oacute; mt gian nm tr&ecirc;n mt ct &aacute;  gia h Linh Chiu nh c&oacute; trng hoa sen. Truyn thuyt k li rng, ch&ugrave;a c x&acirc;y dng theo gic m ca vua L&yacute; Th&aacute;i T&ocirc;ng (1028-1054) v&agrave; theo gi &yacute; thit k ca nh&agrave; s Thin Tu. V&agrave;o nm 1049, vua &atilde; m thy c Pht b&agrave; Quan &Acirc;m ngi tr&ecirc;n t&ograve;a sen dt vua l&ecirc;n to&agrave;. Khi tnh dy, nh&agrave; vua k chuyn &oacute; li vi b&agrave;y t&ocirc;i v&agrave; c nh&agrave; s Thin Tu khuy&ecirc;n dng ch&ugrave;a, dng ct &aacute; nh trong chi&ecirc;m bao, l&agrave;m to&agrave; sen ca Pht b&agrave; Quan &Acirc;m t tr&ecirc;n ct nh &atilde; thy trong mng v&agrave; cho c&aacute;c nh&agrave; s i v&ograve;ng xung quanh tng kinh cu k&eacute;o d&agrave;i s ph&ugrave; h, v&igrave; th ch&ugrave;a mang t&ecirc;n Di&ecirc;n Hu.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca50b95de3_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;">Ch&ugrave;a Mt Ct nh&igrave;n t ph&iacute;a sau</p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">Hng nm c n ng&agrave;y 8 th&aacute;ng 4 &Acirc;m lch, vua li ti ch&ugrave;a l&agrave;m l tm Pht. C&aacute;c nh&agrave; s v&agrave; nh&acirc;n d&acirc;n khp Kinh th&agrave;nh Thng Long c&ugrave;ng d l. Sau l tm Pht l&agrave; l ph&oacute;ng sinh, vua ng tr&ecirc;n mt &agrave;i cao trc ch&ugrave;a th mt con chim bay i, ri nh&acirc;n d&acirc;n c&ugrave;ng tung chim bay theo trong ting reo vui ca mt ng&agrave;y hi ln.</p>\r\n<p style="text-align: justify;">n nm 1105, vua L&yacute; Nh&acirc;n T&ocirc;ng cho sa ng&ocirc;i ch&ugrave;a v&agrave; cho dng trc s&acirc;n hai th&aacute;p lp s trng. Nm 1108, Nguy&ecirc;n phi  Lan sai b&agrave;y t&ocirc;i &uacute;c mt c&aacute;i chu&ocirc;ng rt to, nng den mt vn hai ngh&igrave;n c&acirc;n, t t&ecirc;n l&agrave; "Gi&aacute;c th chung" (Qu chu&ocirc;ng thc tnh ngi i). &acirc;y c xem l&agrave; mt trong t i kh&iacute; - bn c&ocirc;ng tr&igrave;nh ln ca Vit Nam thi &oacute; - l&agrave;: th&aacute;p B&aacute;o Thi&ecirc;n, chu&ocirc;ng Quy in, vc Ph Minh v&agrave; tng Qunh L&acirc;m. "Gi&aacute;c th chung" &uacute;c xong nng qu&aacute; kh&ocirc;ng treo l&ecirc;n c,  di mt t th&igrave; &aacute;nh kh&ocirc;ng k&ecirc;u. Ngi ta &agrave;nh b chu&ocirc;ng xung mt tha rung s&acirc;u b&ecirc;n ch&ugrave;a Nht Tr, rung n&agrave;y c&oacute; nhiu r&ugrave;a, do &oacute; c&oacute; t&ecirc;n la Quy in chu&ocirc;ng (chu&ocirc;ng rung r&ugrave;a). n th k 15, gic Minh x&acirc;m lc, chim th&agrave;nh &ocirc;ng Quan (H&agrave; Ni). Nm 1426 L&ecirc; Li em ngha qu&acirc;n Lam Sn ra &aacute;nh, v&acirc;y th&agrave;nh rt gp. Qu&acirc;n Minh thiu thn v kh&iacute; n dc, tng Minh l&agrave; Vng Th&ocirc;ng b&egrave;n sai ngi em ph&aacute; chu&ocirc;ng Quy in ly ng. Qu&acirc;n Minh thua trn, nhng chu&ocirc;ng Quy in th&igrave; kh&ocirc;ng c&ograve;n na.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca50c97cd8_crop.JPG" alt="" /></p>\r\n<p style="text-align: center;">To&agrave;n cnh ch&ugrave;a Mt Ct</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">Vn bia th&aacute;p S&ugrave;ng thin di&ecirc;n linh (ch&ugrave;a i, H&agrave; Nam), nm 1121 vit: "Do l&ograve;ng s&ugrave;ng k&iacute;nh c Pht v&agrave; dc l&ograve;ng m o nh&acirc;n qu (o Pht) n&ecirc;n hng v vn T&acirc;y Cm ni danh (Ng&ocirc;i vn  ph&iacute;a t&acirc;y cm th&agrave;nh Thng Long i L&yacute;) m&agrave; x&acirc;y ng&ocirc;i ch&ugrave;a s&aacute;ng Di&ecirc;n Hu theo du vt ch  c, c&ugrave;ng vi &yacute; mu mi ca nh&agrave; vua (&yacute; n&oacute;i: theo du vt l li x&acirc;y dng ch&ugrave;a i L&yacute; Th&aacute;nh T&ocirc;ng, c&oacute; th&ecirc;m &yacute; mi ca L&yacute; Nh&acirc;n T&ocirc;ng m&agrave; cha li ch&ugrave;a p hn trc)".</p>\r\n<p style="text-align: justify;">n thi nh&agrave; Trn, ch&ugrave;a &atilde; kh&ocirc;ng phi l&agrave; ng&ocirc;i ch&ugrave;a nh&agrave; L&yacute; na v&igrave; s&aacute;ch c &atilde; ghi: Nm 1249, "...m&ugrave;a xu&acirc;n, th&aacute;ng gi&ecirc;ng, sa li ch&ugrave;a Di&ecirc;n Hu, xung chiu vn l&agrave;m  nn c...".<br /><br />Ch&ugrave;a Mt Ct c tr&ugrave;ng tu v&agrave;o khong nhng nm 1840-1850 v&agrave; v&agrave;o nm 1922. &agrave;i Li&ecirc;n Hoa ch&uacute;ng ta thy hin nay c sa cha li nm 1955 do kin-tr&uacute;c-s Nguyn B&aacute; Lng m-nhim.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca602211b7_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca602211b7_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca602211b7_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca602211b7_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca602211b7_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ca602211b7_large.jpg', 'active', 4, '14:00:48', 1, 'admin', '15:40:22', 1, 'admin', 1, 'admin', '14:00:57', 0, 0, 0, 0, NULL, 0, 'vi_VN');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (11, 9, 'Steamed cake', '', 'steamed-cake', '<p style="text-align: justify;">B&aacute;nh chng (steamed cake) is a traditional Vietnamese cake which is made from glutinous rice, mung bean, pork and other ingredients. Its origin is told by the legend of Lang Li&ecirc;u, a prince of the sixth H&ugrave;ng Vng, who became H&ugrave;ng Vng''s successor thank to his creation of b&aacute;nh chng and b&aacute;nh d&agrave;y, which symbolized respectively the Earth and the Sky. Considered an essential element of the family altar on the occasion of Tt, the making and eating b&aacute;nh chng during this time is a well-preserved tradition of Vietnamese people. Beside the Tt holiday, b&aacute;nh chng is also tasted all year round as a delicacy of Vietnamese cuisine and one of the national dishes of Vietnam besides nem r&aacute;n and ph.</p>', '<h2 style="text-align: justify;"><span id="Legend_and_symbol" class="mw-headline">Legend and symbol</span></h2>\r\n<p style="text-align: justify;">According to the book Lnh Nam ch&iacute;ch qu&aacute;i (Extraordinary stories of Lnh Nam) published in 1695, the creation of b&aacute;nh chng was credited to Lang Li&ecirc;u, a prince of the sixth H&ugrave;ng Vng. It was said that after the victory against the army of Shang Dynasty, in choosing a successor among his sons, H&ugrave;ng Vng decided to carry out a competition in which each prince brought a delicacy representing the sincerity for the ancestors on the occasion of the Tt, whoever could introduce the most delicious dish for the altar would become the next ruler of the country.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cac4720ab1_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;">An unwrapped, cut into 8 pieces b&aacute;nh chng ready to serve</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">While other princes tried to find the rare and delicious foods from forest and sea, the eighteenth prince Lang Li&ecirc;u, who was the poorest son of H&ugrave;ng Vng, could not afford those luxury dishes and had to content with the everyday ingredients ingredients such as rice or pork. Finally, he created one cake in the square form of earth called b&aacute;nh chng and one in the round form of sky called b&aacute;nh d&agrave;y from these simple ingredients. In tasting the dishes offered by his son, H&ugrave;ng Vng found b&aacute;nh chng and b&aacute;nh d&agrave;y not only delicious but also a fine respresentation of the respect for ancestors. Therefore he decided to cede the throne to Lang Li&ecirc;u and b&aacute;nh chng, b&aacute;nh d&agrave;y became traditional foods during the Tt.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cac46114ad_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;">B&aacute;nh chng is served with gi&ograve; la and other dishes</p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: center;">&nbsp;</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7caba005603_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7caba005603_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7caba005603_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7caba005603_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7caba005603_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7caba005603_large.jpg', 'active', 5, '14:10:47', 1, 'admin', '14:19:19', 1, 'admin', 1, 'admin', '14:13:47', 0, 0, 0, 0, NULL, 0, 'en_US');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (12, 20, 'Bnh chng', '', 'banh-chung', '<p>B&aacute;nh chng l&agrave; mt loi b&aacute;nh truyn thng ca d&acirc;n tc Vit nhm th hin l&ograve;ng bit n ca con ch&aacute;u i vi cha &ocirc;ng v&agrave; t tri x s. Nguy&ecirc;n liu l&agrave;m b&aacute;nh bao gm go np, u xanh, tht ln, l&aacute; dong v&agrave; b&aacute;nh thng c l&agrave;m v&agrave;o c&aacute;c dp Tt c truyn ca d&acirc;n tc Vit, cng nh ng&agrave;y gi t H&ugrave;ng Vng) (m&ugrave;ng 10 th&aacute;ng 3 &acirc;m lch).</p>', '<h2><span id="S.E1.BB.B1_t.C3.ADch" class="mw-headline">S t&iacute;ch</span></h2>\r\n<p style="text-align: justify;">L&agrave; loi b&aacute;nh duy nht c&oacute; lch s l&acirc;u i trong m thc truyn thng Vit Nam c&ograve;n c s s&aacute;ch nhc li, b&aacute;nh chng c&oacute; v tr&iacute; c bit trong t&acirc;m thc ca cng ng ngi Vit v&agrave; ngun gc ca n&oacute; c&oacute; th truy nguy&ecirc;n v truyn thuyt li&ecirc;n quan n ho&agrave;ng t Lang Li&ecirc;u v&agrave;o i vua H&ugrave;ng th 6. S t&iacute;ch tr&ecirc;n mun. nhc nh con ch&aacute;u v truyn thng ca d&acirc;n tc; l&agrave; li gii th&iacute;ch &yacute; ngha cng nh ngun ci ca ca B&aacute;nh Chng, B&aacute;nh Giy trong vn h&oacute;a, ng thi nhn mnh tm quan trng ca c&acirc;y l&uacute;a v&agrave; thi&ecirc;n nhi&ecirc;n trong nn vn ho&aacute; l&uacute;a nc.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cac4720ab1_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;">Mt chic b&aacute;nh chng vu&ocirc;ng v&agrave; mt chic b&aacute;nh chng t&agrave;y va c g&oacute;i</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Quan_ni.E1.BB.87m_truy.E1.BB.81n_th.E1.BB.91ng" class="mw-headline">Quan nim truyn thng</span></h2>\r\n<p style="text-align: justify;">Theo quan nim ph bin hin nay, c&ugrave;ng vi b&aacute;nh giy, b&aacute;nh chng tng trng cho quan nim v v tr ca ngi Vit xa. B&aacute;nh c&oacute; m&agrave;u xanh l&aacute; c&acirc;y, h&igrave;nh vu&ocirc;ng, c coi l&agrave; c trng cho t trong t&iacute;n ngng ca ngi Vit c v&agrave; c&aacute;c d&acirc;n tc kh&aacute;c trong khu vc ch&acirc;u &Aacute;. Tuy nhi&ecirc;n, theo Gi&aacute;o s Trn Quc Vng, b&aacute;nh chng nguy&ecirc;n thy c&oacute; h&igrave;nh tr&ograve;n v&agrave; d&agrave;i, ging nh b&aacute;nh t&eacute;t, [ng thi b&aacute;nh chng v&agrave; b&aacute;nh giy tng trng cho nam v&agrave; n trong t&iacute;n ngng phn thc Vit Nam. B&aacute;nh t&eacute;t, thay th v tr&iacute; ca b&aacute;nh chng v&agrave;o c&aacute;c dp Tt trong cng ng ngi Vit  min nam Vit Nam, theo Trn Quc Vng l&agrave; dng nguy&ecirc;n thy ca b&aacute;nh chng.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cac46114ad_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;">B&aacute;nh chng v&agrave; gi&ograve; la</p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: justify;">G&oacute;i v&agrave; nu b&aacute;nh chng, ngi canh ni b&aacute;nh chng tr&ecirc;n bp la &atilde; tr th&agrave;nh mt tp qu&aacute;n, vn h&oacute;a sng trong c&aacute;c gia &igrave;nh ngi Vit mi dp tt n xu&acirc;n v.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7caba005603_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7caba005603_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7caba005603_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7caba005603_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7caba005603_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7caba005603_large.jpg', 'active', 1, '14:32:18', 1, 'admin', '15:41:43', 1, 'admin', 1, 'admin', '14:32:28', 0, 0, 0, 0, NULL, 0, 'vi_VN');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (13, 2, 'Vietnamese cuisine', '', 'vietnamese-cuisine', '<p style="text-align: justify;">Vietnamese cuisine is a style of cooking derived from Vietnam with fish sauce, soy sauce, rice, fresh herbs, fruits and vegetables all commonly used. Vietnamese recipes utilize a diverse range of herbs, including lemongrass, mint, Vietnamese mint, long coriander and Thai basil leaves. Traditional Vietnamese cooking is greatly admired for freshness of the ingredients and for the healthy eating style.</p>', '<p style="text-align: justify;">The most common meats used in Vietnamese cuisine are beef, pork, chicken, fish, and various kinds of seafood. The Vietnamese also have a strong vegetarian tradition influenced by Buddhist values.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb06864644_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Hu tieu kho, braised rice noodle dish</em></p>\r\n<h2 style="text-align: justify;"><span id="Regional_variations" class="mw-headline">Regional variations</span></h2>\r\n<p style="text-align: justify;">As a whole, Vietnamese cuisine contains strong influences mainly from Cantonese cooking with a bit of a French twist to it. This is because unlike many other Indochinese nations, which are strongly influences by the culture of India, Vietnam is the only country in which Chinese-like cultures remained strong, since that it was first ruled by China for at least a 1000 years, followed by control from France centuries later for a 100 years. However, depending on the region, it can be divided into three categories, each pertaining to a distinct geographical region. With Northern Vietnam being the cradle of Vietnamese civilization, many of Vietnam''s most notable dishes such as ph and b&aacute;nh cun can trace their origin to the North. Northern cuisine is more traditional and less diverse in choosing spices and ingredients.</p>\r\n<p style="text-align: justify;">The cuisine of South Vietnam has historically been influenced by the influx of southern Chinese immigrants, French colonists and other nationalities. Southerners prefer sweet flavors in many dishes.[citation needed] As a region of perhaps greater diversity in terms of external influences, the South''s cuisine uses a wider variety of herbs.</p>\r\n<p style="text-align: justify;">The cuisine of Central Vietnam is distinct from the cuisines of both the Northern and Southern regions in its use of many small side dishes. For a while the country was ruled from Hu in Central Vietnam, so that most of the dishes were made small and dedicated to the kings. Compared to its counterparts, its cuisine is more spicy.</p>\r\n<p style="text-align: justify;">One most common French influence is the use of baguettes in Vietnamese meals. Vietnamese sandwiches are traditionally made with baguettes, and sometimes soups such as canh chua are served with a baguette on the side.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2 style="text-align: justify;"><span id="Typical_Vietnamese_family_meals" class="mw-headline">Typical Vietnamese family meals</span></h2>\r\n<p style="text-align: justify;">A typical meal for the average Vietnamese family would include:<br /><br />- Individual bowls of rice<br />- Meat, fish or seafood (grilled, boiled, steamed, stewed or stir fried with vegetables)<br />- Stir-fried, raw, pickled or steamed vegetables<br />- Canh (a clear broth with vegetables and often meat or seafood) or other Vietnamese-style soup<br />- Prepared fish sauce and/or soy sauce for dipping, to which garlic, chili, ginger or lime juice are sometimes added according to taste.<br /><br />All dishes apart from the individual bowls of rice are communal and to be shared.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb06864644_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb06864644_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb06864644_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb06864644_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb06864644_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb06864644_large.jpg', 'active', 4, '14:34:47', 1, 'admin', '15:33:22', 1, 'admin', 1, 'admin', '14:34:54', 0, 0, 0, 0, NULL, 0, 'en_US');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (14, 17, 'm thc Vit Nam', '', 'am-thuc-viet-nam', '<p>m thc Vit Nam l&agrave; c&aacute;ch gi ca phng thc ch bin m&oacute;n n, nguy&ecirc;n l&yacute; phi trn gia v v&agrave; nhng th&oacute;i quen n ung n&oacute;i chung ca mi ngi Vit tr&ecirc;n t nc Vit Nam. Tuy hu nh c&oacute; &iacute;t nhiu c&oacute; s kh&aacute;c bit, m thc Vit Nam vn bao h&agrave;m &yacute; ngha kh&aacute;i qu&aacute;t nht  ch tt c nhng m&oacute;n n ph bin trong cng ng c&aacute;c d&acirc;n tc thiu s nhng &atilde; tng i ph th&ocirc;ng trong cng ng ngi Vit.</p>', '<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb06864644_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>H tiu<br /></em></p>\r\n<h3><span id="B.E1.BB.AFa_.C4.83n_gia_.C4.91.C3.ACnh_Vi.E1.BB.87t_Nam_truy.E1.BB.81n_th.E1.BB.91ng" class="mw-headline">Ba n gia &igrave;nh Vit Nam truyn thng</span></h3>\r\n<p>Ngi Vit thng n ph v&agrave;o bui s&aacute;ng vi c&aacute;c thc qu&agrave; vt (nh c&aacute;c loi b&aacute;nh, x&ocirc;i, ch&aacute;o, ph, b&uacute;n). Mt ba n ch&iacute;nh, c trng ca mt gia &igrave;nh Vit Nam din ra v&agrave;o bui tra v&agrave;/hoc bui ti, th&ocirc;ng thng l&agrave; khi gia &igrave;nh &atilde; t hp &ocirc;ng . Ba n ch&iacute;nh ca ngi Vit thng bao gm mt m&oacute;n ch lc (cm), mt m&oacute;n gia v (nc chm) v&agrave; ba m&oacute;n n c bn  cht v&agrave; c&acirc;n bng &acirc;m dng:<br /><br />&nbsp;&nbsp;&nbsp; * Mt ni cm chung cho c gia &igrave;nh (mi ngi c&oacute; mt c&aacute;i b&aacute;t nh v&agrave; &ocirc;i a ca ri&ecirc;ng m&igrave;nh)<br />&nbsp;&nbsp;&nbsp; * Mt b&aacute;t nh ng nc chm (nc mm, tng hoc x&igrave; du) c gia &igrave;nh d&ugrave;ng chung.<br />&nbsp;&nbsp;&nbsp; * Mt m&oacute;n mn c&oacute; cht m ng vt v&agrave; cht b&eacute;o c luc, r&aacute;n hoc kho nh tht, c&aacute;<br />&nbsp;&nbsp;&nbsp; * Mt m&oacute;n rau luc hoc x&agrave;o, hoc rau thm, rau sng, da mui<br />&nbsp;&nbsp;&nbsp; * Mt m&oacute;n canh c&oacute; th m &agrave;, cu k nhng cng kh&ocirc;ng him khi ch n gin l&agrave; mt b&aacute;t nc luc rau<br /><br />Hin nay, do i sng c n&acirc;ng cao hn, c cu ba n ch&iacute;nh ca ngi Vit hin cng &atilde; ci thin &aacute;ng k theo hng gia tng c&aacute;c m&oacute;n mn nhiu dinh dng s dng nguy&ecirc;n liu ng vt. B&ecirc;n cnh xu hng mt s v&ugrave;ng min (nht l&agrave; nhng v&ugrave;ng th&ocirc;n qu&ecirc;) vn c&ograve;n b&agrave;y v c&agrave;ng nhiu m&oacute;n tr&ecirc;n m&acirc;m c&agrave;ng tt, nhiu gia &igrave;nh th&agrave;nh th li ch&uacute; trng xu hng tinh gin bng c&aacute;ch ch nu mt m&oacute;n trng t&acirc;m c&oacute;  cht m v&agrave; c&aacute;c loi rau b&agrave;y l&ecirc;n m&acirc;m, n k&egrave;m vi c&aacute;c loi rau da lt vt kh&aacute;c. Mt s gia &igrave;nh l&agrave;m c&aacute;c m&oacute;n n c bit nh&acirc;n ng&agrave;y ch nht rnh ri, nhng m&oacute;n cu k m&agrave; ng&agrave;y thng &iacute;t c&oacute; thi gian  l&agrave;m. B&aacute;t nc chm "cng ng" nay cng dn c nhiu gia &igrave;nh, hoc c&aacute;c nh&agrave; h&agrave;ng cu k san ri&ecirc;ng ra b&aacute;t cho tng ngi  hp v sinh hn, v&agrave; c&oacute; nhiu loi nc chm kh&aacute;c nhau t&ugrave;y theo trong ba c&oacute; loi  n g&igrave;.</p>\r\n<p>&nbsp;</p>\r\n<h3>C b&agrave;n</h3>\r\n<p>C b&agrave;n thng s dng nhiu m&oacute;n n trong &oacute; nhn mnh c bit c&aacute;c m&oacute;n mn d&ugrave;ng nguy&ecirc;n liu ng vt, loi tr tt c nhng m&oacute;n n ng&agrave;y thng nh rau luc, da c&agrave; v.v.</p>\r\n<p>&nbsp;</p>\r\n<h3>C c&uacute;ng t ti&ecirc;n</h3>\r\n<p>C&uacute;ng t ti&ecirc;n (ng&agrave;y gi chp, ng&agrave;y tt c truyn) thng s dng x&ocirc;i u xanh, x&ocirc;i gc vi g&agrave; luc nguy&ecirc;n con hoc ch&acirc;n gi&ograve;. C&uacute;ng ngi mi mt ch d&ugrave;ng x&ocirc;i trng v&agrave; mt qu trng luc.</p>\r\n<p>&nbsp;</p>\r\n<h3>C Tt<span id="B.E1.BB.AFa_.C4.83n_gia_.C4.91.C3.ACnh_Vi.E1.BB.87t_Nam_truy.E1.BB.81n_th.E1.BB.91ng" class="mw-headline"> <br /></span></h3>\r\n<p>C tt truyn thng rt cu k, m&acirc;m c c bn thng l&agrave; 5 b&aacute;t: b&oacute;ng, min, mng, mc, chim hoc g&agrave; tn v&agrave; 5 a: gi&ograve;, ch, g&agrave; hoc vt luc, nm, x&agrave;o. Ng&agrave;y nay m&acirc;m c tt &atilde; c&oacute; nhiu thay i v thc n theo xu hng tinh gin, ch&uacute; trng "chi" hn "n".</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb06864644_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb06864644_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb06864644_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb06864644_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb06864644_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb06864644_large.jpg', 'active', 1, '14:49:59', 1, 'admin', '15:42:49', 1, 'admin', 1, 'admin', '14:50:12', 0, 0, 0, 0, NULL, 0, 'vi_VN');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (15, 1, 'Halong Bay', '', 'halong-bay', '<p>Ha Long Bay (literally: "Descending Dragon Bay"; Vietnamese: Vnh H Long) is a UNESCO World Heritage Site, and a popular travel destination, located in Qung Ninh province, Vietnam.</p>', '<p style="text-align: justify;">Administratively, the bay belongs to Ha Long City, Cm Ph town, and part of Van Don district. The bay features thousands of limestone karsts and isles in various sizes and shapes. Ha Long Bay is a center of a larger zone which includes B&aacute;i T Long bay to the northeast, and C&aacute;t B&agrave; islands to the southwest. These larger zones share similar geological, geographical, geomorphological, climate, and cultural characters.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb60b67f10_crop.JPG" alt="" /></p>\r\n<p style="text-align: center;"><em>The Kissing Rocks</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">Ha Long Bay has an area of around 1,553km2, including 1,960 islets, most of which are limestone. The core of the bay has an area of 334km2 with a high density of 775 islets. The limestone in this bay has gone through 500 million years of formation in different conditions and environments. The evolution of the karst in this bay has taken 20 million years under the impact of the tropical wet climate. The geo-diversity of the environment in the area has created biodiversity, including a tropical evergreen biosystem, oceanic and sea shore biosystem. Ha Long Bay is home to 14 endemic floral species and 60 endemic faunal species.<br /><br />Historical research surveys have shown the presence of prehistorical human beings in this area tens of thousands years ago. The successive ancient cultures are the Soi Nh culture around 18,000-7,000 BC, the C&aacute;i B&egrave;o culture 7,000-5,000 BC and the H Long culture 3,500-5,000 years ago. Ha Long Bay also marked important events in the history of Vietnam with many artifacts found in B&agrave;i Th Mout, u G Cave, B&atilde;i Ch&aacute;y.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb60a1bf5b_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Thien Cung grotto</em></p>\r\n<p style="text-align: justify;"><br />500 years ago, Nguyen Trai praised the beauty of Ha Long Bay in his verse L nhp V&acirc;n n, in which he called it "rock wonder in the sky".In 1962, the Ministry of Culture, Sports and Tourism of Vietnam listed Ha Long Bay in the National Relics and Landscapes publication.In 1994, the core zone of Ha Long Bay was listed by UNESCO as a World Heritage Site according to criteria vii, and listed for a second time according to criteria viii.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb69b56214_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb69b56214_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb69b56214_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb69b56214_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb69b56214_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb69b56214_large.jpg', 'active', 1, '15:00:37', 1, 'admin', '15:07:34', 1, 'admin', 1, 'admin', '15:00:47', 0, 0, 0, 0, NULL, 0, 'en_US');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (16, 13, 'Vnh H Long', '', 'vinh-ha-long', '<p>Vnh H Long (vnh nc ni rng &aacute;p xung) l&agrave; mt vnh nh thuc phn b T&acirc;y vnh Bc B ti khu vc bin &ocirc;ng Bc Vit Nam, bao gm v&ugrave;ng bin o thuc th&agrave;nh ph H Long, th x&atilde; Cm Ph v&agrave; mt phn ca huyn o V&acirc;n n.</p>', '<p style="text-align: justify;">L&agrave; trung t&acirc;m ca mt khu vc rng ln c&oacute; nhng yu t &iacute;t nhiu tng ng v a cht, a mo, cnh quan, kh&iacute; hu v&agrave; vn h&oacute;a, vi vnh B&aacute;i T Long ph&iacute;a &ocirc;ng Bc v&agrave; qun o C&aacute;t B&agrave; ph&iacute;a T&acirc;y Nam, vnh H Long gii hn trong din t&iacute;ch khong 1.553km&sup2; bao gm 1.960 h&ograve;n o ln nh, phn ln l&agrave; o &aacute; v&ocirc;i, trong &oacute; v&ugrave;ng l&otilde;i ca Vnh c&oacute; din t&iacute;ch 334km&sup2; qun t d&agrave;y c 775 h&ograve;n o[. Lch s kin to a cht &aacute; v&ocirc;i ca Vnh &atilde; tri qua khong 500 triu nm vi nhng ho&agrave;n cnh c a l&yacute; rt kh&aacute;c nhau; v&agrave; qu&aacute; tr&igrave;nh tin h&oacute;a carxt y  tri qua tr&ecirc;n 20 triu nm vi s kt hp c&aacute;c yu t nh tng &aacute; v&ocirc;i d&agrave;y, kh&iacute; hu n&oacute;ng m v&agrave; tin tr&igrave;nh n&acirc;ng kin to chm chp tr&ecirc;n tng th S kt hp ca m&ocirc;i trng, kh&iacute; hu, a cht, a mo, &atilde; khin vnh H Long tr th&agrave;nh qun t ca a dng sinh hc bao gm h sinh th&aacute;i rng k&iacute;n thng xanh ma m nhit i v&agrave; h sinh th&aacute;i bin v&agrave; ven b vi nhiu tiu h sinh th&aacute;i. 14 lo&agrave;i thc vt c hu v&agrave; khong 60 lo&agrave;i ng vt c hu &atilde; c ph&aacute;t hin trong s h&agrave;ng ng&agrave;n ng, thc vt qun c ti Vnh.</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb60b67f10_crop.JPG" alt="" /></p>\r\n<p style="text-align: center;"><em>H&ograve;n Trng M&aacute;i</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">Nhng kt qu nghi&ecirc;n cu, th&aacute;m s&aacute;t kho c hc v&agrave; vn h&oacute;a hc cho thy s hin din ca nhng c d&acirc;n tin s tr&ecirc;n v&ugrave;ng vnh H Long t kh&aacute; sm, &atilde; to lp nhng h&igrave;nh th&aacute;i vn h&oacute;a c i tip ni nhau bao gm vn h&oacute;a Soi Nh trong khong 18.000-7.000 nm trc C&ocirc;ng Nguy&ecirc;n, vn h&oacute;a C&aacute;i B&egrave;o trong 7.000-5.000 nm trc C&ocirc;ng Nguy&ecirc;n v&agrave; vn h&oacute;a H Long c&aacute;ch ng&agrave;y nay khong t 3.500-5.000 nm. Tin tr&igrave;nh dng nc v&agrave; truyn thng gi nc ca d&acirc;n tc Vit Nam, trong sut h&agrave;nh tr&igrave;nh lch s, cng khng nh v tr&iacute; tin ti&ecirc;u v&agrave; v th vn h&oacute;a ca vnh H Long qua nhng a danh m&agrave; t&ecirc;n gi gn vi in t&iacute;ch c&ograve;n lu truyn n nay, nh n&uacute;i B&agrave;i Th, hang u G, B&atilde;i Ch&aacute;yv.v. Hin nay, vnh H Long l&agrave; mt khu vc ph&aacute;t trin nng ng nh nhng iu kin v&agrave; li th sn c&oacute; nh c&oacute; mt tim nng ln v du lch, nghi&ecirc;n cu khoa hc, nu&ocirc;i trng, &aacute;nh bt thy sn, giao th&ocirc;ng thy i vi khu vc v&ugrave;ng bin &ocirc;ng Bc Vit Nam n&oacute;i ri&ecirc;ng v&agrave; min Bc Vit Nam n&oacute;i chung.</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb60a1bf5b_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>ng Thien Cung </em></p>\r\n<p style="text-align: justify;"><br /><br />T hn 500 nm v trc trong b&agrave;i th L nhp V&acirc;n n, Nguyn Tr&atilde;i &atilde; ln u ti&ecirc;n ca ngi vnh H Long l&agrave; "k quan &aacute; dng gia tri cao". Nm 1962 B Vn h&oacute;a-Th&ocirc;ng tin Vit Nam &atilde; xp hng vnh H Long l&agrave; di t&iacute;ch danh thng cp quc gia ng thi quy hoch v&ugrave;ng bo v. Nm 1994 v&ugrave;ng l&otilde;i ca vnh H Long c UNESCO c&ocirc;ng nhn l&agrave; Di sn Thi&ecirc;n nhi&ecirc;n Th gii vi gi&aacute; tr thm m (ti&ecirc;u chun vii), v&agrave; c t&aacute;i c&ocirc;ng nhn ln th 2 vi gi&aacute; tr ngoi hng to&agrave;n cu v a cht-a mo (ti&ecirc;u chun viii) v&agrave;o nm 2000. C&ugrave;ng vi vnh Nha Trang ca Vit Nam, vnh H Long l&agrave; mt trong s 29 vnh c C&acirc;u lc b nhng vnh p nht th gii xp hng v&agrave; ch&iacute;nh thc c&ocirc;ng nhn v&agrave;o th&aacute;ng 7 nm 2003.<br /><br />Vnh H Long c&ugrave;ng vi o C&aacute;t B&agrave; to th&agrave;nh 1 trong 21 khu du lch quc gia Vit Nam</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb69b56214_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb69b56214_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb69b56214_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb69b56214_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb69b56214_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cb69b56214_large.jpg', 'active', 1, '15:06:36', 1, 'admin', '15:39:57', 1, 'admin', 1, 'admin', '15:07:11', 0, 0, 0, 0, NULL, 0, 'vi_VN');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (17, 1, 'Nha Trang', '', 'nha-trang', '<p>Nha Trang About this sound pronunciation is a coastal city and capital of Kh&aacute;nh H&ograve;a province, on the South Central Coast of Vietnam.</p>', '<p style="text-align: justify;">It is bounded on the North by Ninh Ho&agrave; district, on the East by the South China Sea, on the South by Cam Ranh town and on the West by Di&ecirc;n Kh&aacute;nh district. The city has about 300,000 inhabitants, a number which is projected to increase to between 500,000 and 600,000 inhabitants by 2020 according to an estimation of Nha Trang Administrative Board Statistics.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cba2d1b84a_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Nha Trang&acute;s coastline</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">Nha Trang is well known for its pristine beaches and excellent scuba diving and is fast becoming a popular destination for international tourists, attracting large numbers of backpackers as well as more affluent travelers on the Southeast Asia circuit. It is already very popular with Vietnamese tourists. Nha Trang Bay is widely considered as amongst the world''s most beautiful bays. Tourists are welcome to participate in the Sea Festival, held biennially. Nha Trang was the site of the Miss Universe 2008 Pageant on July 14, 2008 and it will also host Miss Earth 2010.<br /><br />Historically, the city was known as Kauthara under the Champa. The city is still home to the famous Po Nagar Tower built by the Champa. Being a coastal city, Nha Trang is a center for marine science based at the Nha Trang Oceanography Institute. The Hon Mun marine protected area is one of four first marine protected areas in the world admitted by the IUCN.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cba2c0dd5e_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Nha Trang''s beach</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">Commercial flights to Nha Trang no longer use the city''s municipal airport, but fly to the larger Cam Ranh International Airport, a former U.S. Air Force Base (built during the Vietnam War) located approximately 35 km south of Nha Trang (see the Transport section below for additional details).<br /><br />The French-Swiss bacteriologist Alexandre Yersin (who discovered the Yersinia pestis bacterium) identified himself with Nha Trang''s life for 50 years (affectionately known as &Ocirc;ng Nm). He established the Indochina Pasteur Institute (now known as the Nha Trang Pasteur Institute) devoted to research on the bubonic plague. Yersin died in Nha Trang on March 1, 1943. A street in the city is named after him, there is a shrine located next to his tomb, and his house has been converted into the Yersin Museum.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbae63edcd_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbae63edcd_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbae63edcd_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbae63edcd_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbae63edcd_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbae63edcd_large.jpg', 'active', 3, '15:18:51', 1, 'admin', '15:43:45', 1, 'admin', 1, 'admin', '15:18:57', 0, 0, 0, 0, NULL, 0, 'en_US');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (18, 3, 'Tt Holiday', '', 'tet-holiday', '<p>Tt Nguy&ecirc;n &aacute;n, more commonly known by its shortened name Tt, is the most important and popular holiday and festival in Vietnam.</p>', '<p>Tt is celebrated on the same day as Chinese New Year though exceptions arise due to the one-hour time difference between Hanoi and Beijing. It takes place from the first day of the first month of the Lunar calendar (around late January or early February) until at least the third day. Many Vietnamese prepare for Tt by cooking special holiday foods and cleaning the house. There are a lot of customs practiced during Tt, like visiting a person''s house on the first day of the new year (x&ocirc;ng nh&agrave;), ancestral worshipping, wishing New Year''s greetings, giving lucky money to children and elderly people, and opening a shop.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbaa183723_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Tt display in Saigon</em></p>\r\n<p>Tt is also an occasion for pilgrims and family reunions. During Tt, Vietnamese visit their relatives and temples, forgetting about the troubles of the past year and hoping for a better upcoming year. They consider Tt to be the first day of spring and the festival is often called Hi xu&acirc;n (spring festival).</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbaa08d743_medium.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Hoa mai</em></p>\r\n<p>Vietnamese people usually return to their families during Tt. Some return to worship at the family altar or visit the graves of their ancestors in their homeland. Although Tt is a national holiday among all Vietnamese, each region and religion has its own customs.</p>\r\n<p>Tt in the three Vietnamese regions can be divided into three periods, known as Tt Ni&ecirc;n (Before New Year''s Eve), Giao Tha (New Year''s Eve), and T&acirc;n Ni&ecirc;n (the New Year), representing the preparation before Tt, the eve of Tt, and the days of and following Tt, respectively.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbaa3b26b3_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbaa3b26b3_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbaa3b26b3_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbaa3b26b3_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbaa3b26b3_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbaa3b26b3_large.jpg', 'active', 3, '15:20:20', 1, 'admin', NULL, NULL, NULL, 1, 'admin', '15:20:27', 0, 0, 0, 0, NULL, 0, 'en_US');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (19, 13, 'Nha Trang', '', 'nha-trang', '<p style="text-align: justify;">Nha Trang l&agrave; mt th&agrave;nh ph ven bin v&agrave; l&agrave; trung t&acirc;m ch&iacute;nh tr, kinh t, vn h&oacute;a, khoa hc k thut v&agrave; du lch ca tnh Kh&aacute;nh H&ograve;a, Vit Nam.</p>', '<p style="text-align: justify;">Trc khi tr th&agrave;nh phn t ca Vit Nam, Nha Trang thuc v Chi&ecirc;m Th&agrave;nh. C&aacute;c di t&iacute;ch ca ngi Chm vn c&ograve;n ti nhiu ni  Nha Trang. Nha Trang c Th tng ch&iacute;nh ph Vit Nam c&ocirc;ng nhn l&agrave; &ocirc; th loi 1 v&agrave;o ng&agrave;y 22 th&aacute;ng 4 nm 2009. &acirc;y l&agrave; mt trong 6 &ocirc; th loi 1 trc thuc tnh ca Vit Nam<br /><br />C&aacute;c b&atilde;i bin p ca th&agrave;nh ph n&agrave;y &atilde; bin n&oacute; th&agrave;nh mt danh lam thng cnh. Ni &acirc;y cng c bit n nh mt a im l&yacute; tng  t chc c&aacute;c s kin ln nh Festival Bin (Nha Trang), hay c&aacute;c cuc thi sc p ln nh Hoa hu Vit Nam, Hoa hu Th gii ngi Vit 2007 , Hoa hu Ho&agrave;n v 2008, Hoa hu Tr&aacute;i t 2010, Hoa hu Th gii 2010...</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cba2d1b84a_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><small>B&atilde;i bin Nha Trang</small></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">Th&agrave;nh ph Nha Trang hin nay c&oacute; din t&iacute;ch t nhi&ecirc;n l&agrave; 251 km&sup2;, d&acirc;n s 361.454 (2009). Ph&iacute;a Bc gi&aacute;p huyn Ninh H&ograve;a, ph&iacute;a Nam gi&aacute;p huyn Cam L&acirc;m, ph&iacute;a T&acirc;y gi&aacute;p huyn Di&ecirc;n Kh&aacute;nh, ph&iacute;a &ocirc;ng gi&aacute;p Bin &ocirc;ng.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cba2c0dd5e_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>ng ven bin</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<h2>ng bng Di&ecirc;n Kh&aacute;nh - Nha Trang</h2>\r\n<p style="text-align: justify;"><br />Nha Trang nm  ph&iacute;a &ocirc;ng ng bng Di&ecirc;n Kh&aacute;nh - Nha Trang. Mt ng bng c bi lp bi s&ocirc;ng C&aacute;i Nha Trang c&oacute; din t&iacute;ch gn 300 km&sup2;, a h&igrave;nh &ograve;ng bng b ph&acirc;n h&oacute;a mnh:<br /><br />&nbsp;&nbsp;&nbsp; * phn ph&iacute;a T&acirc;y dc s&ocirc;ng Ch&ograve; t Kh&aacute;nh B&igrave;nh n Di&ecirc;n ng b b&oacute;c m&ograve;n,  cao tuyt i khong 10-20 m<br />&nbsp;&nbsp;&nbsp; * phn ph&iacute;a &ocirc;ng l&agrave; a h&igrave;nh t&iacute;ch t  cao tuyt i di 10m, b mt a h&igrave;nh b ph&acirc;n ct mnh bi c&aacute;c d&ograve;ng chy.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbae63edcd_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbae63edcd_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbae63edcd_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbae63edcd_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbae63edcd_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbae63edcd_large.jpg', 'active', 3, '15:23:15', 1, 'admin', '15:44:04', 1, 'admin', 1, 'admin', '15:23:35', 0, 0, 0, 0, NULL, 0, 'vi_VN');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (20, 21, 'Tt m Lch', '', 'tet-am-lich', '<p>Tt Nguy&ecirc;n &aacute;n (hay c&ograve;n gi l&agrave; Tt C, Tt Ta, Tt &Acirc;m lch, Tt C truyn, nm mi hay ch n gin Tt) l&agrave; dp l quan trng nht trong vn h&oacute;a ca ngi Vit Nam.</p>', '<p>Ngi Vit tin rng v&agrave;o ng&agrave;y Tt mi th u phi mi, phi i kh&aacute;c, t ngoi vt cho n l&ograve;ng ngi, v&igrave; vy khong mi ng&agrave;y trc Tt h thng sn, qu&eacute;t v&ocirc;i nh&agrave; ca li. H cng tt bt i sm sa qun &aacute;o mi  mc trong dp n&agrave;y.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbc5502b05_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>C&aacute;c &ocirc;ng  vit ch l&ecirc;n giy d&oacute;</em></p>\r\n<p>&nbsp;</p>\r\n<p>Trong nhng ng&agrave;y Tt h ki&ecirc;ng c kh&ocirc;ng n&oacute;ng gin, c&atilde;i c. Tt l&agrave; dp  mi ngi h&agrave;n gn nhng him kh&iacute;ch &atilde; qua v&agrave; l&agrave; dp  chuc li. Mi ngi i thm ving nhau v&agrave; ch&uacute;c nhau nhng li y &yacute; ngha. Tr em sau khi ch&uacute;c Tt ngi ln c&ograve;n c l&igrave; x&igrave; bng mt phong b&igrave;  thm c&oacute; ng &iacute;t tin d&agrave;nh cho ch&uacute;ng ti&ecirc;u x&agrave;i ng&agrave;y Tt. Tt  3 min Bc, Trung, Nam  Vit Nam cng c&oacute; nhng iu kh&aacute;c nhau.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbc5604543_medium.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Hoa &agrave;o Nht T&acirc;n</em></p>\r\n<p>&nbsp;</p>\r\n<p>Ngo&agrave;i hai loi hoa c trng cho Tt l&agrave; &agrave;o v&agrave; mai, hu nh nh&agrave; n&agrave;o cng c&oacute; th&ecirc;m nhng loi hoa  th c&uacute;ng v&agrave; hoa trang tr&iacute;. Hoa th c&uacute;ng c&oacute; th nh hoa vn th, c&uacute;c, lay n, hoa hu...; hoa  trang tr&iacute; th&igrave; mu&ocirc;n m&agrave;u sc nh hoa hng, hoa thy ti&ecirc;n, hoa lan, hoa thc dc, hoa violet, hoa ng tin,... Ngo&agrave;i ra, hoa hng, cm chng, loa k&egrave;n, hu t&acirc;y, l&aacute; mng, thch tho... cm k&egrave;m s to s phong ph&uacute; v&agrave; mang &yacute; ngha sum hp cho b&igrave;nh hoa ng&agrave;y tt. M&agrave;u sc ti vui ch o ca b&igrave;nh hoa cng ng &yacute; cu mong mt nm mi l&agrave;m n ph&aacute;t t, gia &igrave;nh an khang v&agrave; sung t&uacute;c.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbc5800369_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbc5800369_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbc5800369_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbc5800369_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbc5800369_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbc5800369_large.jpg', 'active', 2, '15:26:31', 1, 'admin', NULL, NULL, NULL, 1, 'admin', '15:26:42', 0, 0, 0, 0, NULL, 0, 'vi_VN');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (21, 1, 'Da Lat', '', 'da-lat', '<p>Da Lat, or Dalat, (pop. 197,000 as of 2007[update], spelled &agrave; Lt in Vietnamese) About this sound pronunciation (help&middot;info) is the capital of L&acirc;m ng Province in Vietnam.</p>', '<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbde84daa7_crop.JPG" alt="" /></p>\r\n<p style="text-align: center;"><em>Tuyn L&acirc;m Lake in the morning fog&nbsp; </em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">The city is located 1,500 m (4,900 ft) above sea level on the Langbiang Plateau in the southern parts of the Central Highlands (in Vietnamese, T&acirc;y Nguy&ecirc;n). In Vietnam, &agrave; Lt is a popular tourist destination.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbde95d244_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em><small>Outside Da Lat Market at night</small></em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">&agrave; Lt''s specific sights are pine wood (forming the name: "City of thousands of pine trees") with twisting roads and tree marigold (Vietnamese: d&atilde; qu) blossom in the winter. The city''s temperate weather stands in contrast to Vietnam''s otherwise tropical climate. Mist covering the valleys almost year-round leads to its name "City of eternal spring."</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbde738539_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Da Lat''s Market square at night</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">&agrave; Lt is also known as an area for science research in the fields of biotechnology and nuclear physics.<br /><br />With its year-round cool weather, &agrave; Lt supplies temperate agriculture products for all over Vietnam, for example, cabbage and cauliflower. Its flower industry produces two typical flowers: hydrangea (Vietnamese: cm t&uacute; cu) and golden everlasting (Vietnamese: hoa bt t). The confectionery industry offers a wide range of mt, a kind of fruit preserve made from strawberry, mulberry, sweet potato, and rose.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbd8c1bde4_square.jpeg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbd8c1bde4_thumbnail.jpeg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbd8c1bde4_small.jpeg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbd8c1bde4_crop.jpeg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbd8c1bde4_medium.jpeg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbd8c1bde4_large.jpeg', 'active', 3, '15:30:10', 1, 'admin', '15:43:17', 1, 'admin', 1, 'admin', '15:30:17', 0, 0, 0, 0, NULL, 0, 'en_US');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (22, 13, ' Lt', '', 'da-lat', '<p>&agrave; Lt l&agrave; mt th&agrave;nh ph trc thuc tnh v&agrave; tnh l tnh L&acirc;m ng, nm tr&ecirc;n cao nguy&ecirc;n L&acirc;m Vi&ecirc;n,   cao 1.500 m so vi mt nc bin v&agrave; din t&iacute;ch t nhi&ecirc;n: 393,29 km&sup2;.</p>', '<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbde84daa7_crop.JPG" alt="" /></p>\r\n<p style="text-align: center;"><em>H Tuyn L&acirc;m</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">&agrave; Lt c Th tng ch&iacute;nh ph Vit Nam c&ocirc;ng nhn l&agrave; &ocirc; th loi 1 v&agrave;o ng&agrave;y 24 th&aacute;ng 3 nm 2009. &acirc;y l&agrave; mt trong 6 &ocirc; th loi 1 thuc tnh.<br /><br />Vi nhiu cnh quan p, &agrave; Lt l&agrave; mt trong nhng th&agrave;nh ph du lch ni ting nht ca Vit Nam. Trong thi Ph&aacute;p thuc, t&ecirc;n ting Latin Dat Aliis Laetitiam Aliis Temperiem c&oacute; ngha l&agrave; "cho nhng ngi n&agrave;y nim vui, cho nhng ngi kh&aacute;c s m&aacute;t m"[2]. &agrave; Lt c mnh danh l&agrave;: th&agrave;nh ph hoa, th&agrave;nh ph t&igrave;nh y&ecirc;u, th&agrave;nh ph m&ugrave;a xu&acirc;n, th&agrave;nh ph sng m&ugrave;.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbde95d244_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em><small>Ch &agrave; Lt<br /></small></em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">Th&agrave;nh ph &agrave; Lt nm trong cao nguy&ecirc;n Lang Biang, ph&iacute;a Bc tnh L&acirc;m ng, V ph&iacute;a Bc, &agrave; Lt gi&aacute;p vi huyn Lc Dng, v ph&iacute;a &ocirc;ng v&agrave; &ocirc;ng Nam gi&aacute;p vi huyn n Dng, v ph&iacute;a T&acirc;y v&agrave; T&acirc;y Nam gi&aacute;p vi hai huyn L&acirc;m H&agrave; v&agrave; c Trng.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbde738539_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Ch &agrave; Lt v &ecirc;m<br /></em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">H&agrave;ng trm nm trc &acirc;y, &agrave; Lt l&agrave; a b&agrave;n c tr&uacute; ca ngi Lch, vn l&agrave; c d&acirc;n ca to&agrave;n b cao nguy&ecirc;n Lang Biang. &agrave; Lt c&oacute; din t&iacute;ch hn 400 km&sup2;, bao bc bi c&aacute;c nh n&uacute;i cao v&agrave; d&atilde;y n&uacute;i li&ecirc;n tip:<br /> <br /> &nbsp;&nbsp;&nbsp; * Ph&iacute;a Bc v&agrave; T&acirc;y Bc gii hn bi d&atilde;y Chormui, Y a Myut (1.816 m), T&acirc;y Bc da v&agrave;o ch&acirc;n d&atilde;y n&uacute;i Ch Yang Kae (1.921 m), thuc qun sn Lang Biang m&agrave; nh cao nht l&agrave; Ch Yang Sinh (1.408 m).<br /> &nbsp;&nbsp;&nbsp; * Ph&iacute;a &ocirc;ng l&agrave; ch&acirc;n d&atilde;y n&uacute;i Bi Doup (2.278 m) dc xung cao nguy&ecirc;n Dran.<br /> &nbsp;&nbsp;&nbsp; * Ph&iacute;a &ocirc;ng Nam chn bi d&atilde;y Cho Proline (1.629 m).<br /> &nbsp;&nbsp;&nbsp; * Ph&iacute;a Nam v&agrave; T&acirc;y Nam c&oacute; d&atilde;y n&uacute;i Voi (1.754 m) v&agrave; Y&agrave;ng Sreng bao bc.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbd8c1bde4_square.jpeg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbd8c1bde4_thumbnail.jpeg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbd8c1bde4_small.jpeg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbd8c1bde4_crop.jpeg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbd8c1bde4_medium.jpeg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cbd8c1bde4_large.jpeg', 'active', 2, '15:37:17', 1, 'admin', '15:43:30', 1, 'admin', 1, 'admin', '15:37:38', 0, 0, 0, 0, NULL, 0, 'vi_VN');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (23, 2, 'Colled cake', '', 'colled-cake', '<p style="text-align: justify;">B&aacute;nh cun (literally "rolled cake") is a dish from northern Vietnam.</p>', '<p style="text-align: justify;">It is a cr&ecirc;pe-like roll made from a thin, wide sheet of rice flour filled with ground pork, minced wood ear mushroom, and other ingredients. Sides for this dish usually consist of ch la (Vietnamese pork sausage) and bean sprouts, with the dipping sauce called nc chm. Sometimes, a drop of c&agrave; cung, which is the essence of a giant water bug, Lethocerus indicus, is added to the nc chm for extra flavor, although this ingredient is scarce and quite expensive.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc37ba9053_small.jpg" alt="" width="207" height="155" /> <img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc37d048e1_small.JPG" alt="" width="207" height="155" /> <img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc37e5c955_small.jpg" alt="" width="238" height="154" /></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">Unlike shahe fen, the rice sheet in b&aacute;nh cun is extremely thin and delicate. It is a light dish, and is generally eaten for breakfast everywhere in Vietnam. A different version of b&aacute;nh cun, called b&aacute;nh cun Thanh Tr&igrave; and b&aacute;nh cun l&agrave;ng K&ecirc;nh, may be found in Thanh Tr&igrave;, a southern district of Hanoi and K&ecirc;nh village of Nam nh, an ancient village in the centre of Nam nh city.B&aacute;nh cun Thanh Tr&igrave; or B&aacute;nh cun l&agrave;ng K&ecirc;nh are not rolls, but just rice sheets eaten with ch la, fried shallots, or prawns; see the article B&aacute;nh cun in the Vietnamese Wikipedia.<br /><br />A similar Chinese dish is the Cantonese dish coeng4 fan2 (; pinyin: ch&aacute;ngfn), usually translated into English as "steamed rice roll" or "rice noodle roll," which is a form of dim sum.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc45c523c6_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc45c523c6_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc45c523c6_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc45c523c6_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc45c523c6_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc45c523c6_large.jpg', 'active', 2, '15:59:15', 1, 'admin', '16:03:19', 1, 'admin', 1, 'admin', '15:59:26', 0, 0, 0, 0, NULL, 0, 'en_US');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (24, 17, 'Bnh cun', '', 'banh-cuon', '<p>B&aacute;nh cun hay b&aacute;nh t l&agrave; loi b&aacute;nh l&agrave;m t bt go hp tr&aacute;ng mng,  n khi c&ograve;n t, trong c&oacute; th cun nh&acirc;n hoc kh&ocirc;ng nh&acirc;n (kh&aacute;c vi b&aacute;nh tr&aacute;ng, (b&aacute;nh a) phi phi kh&ocirc;; hay b&aacute;nh t  min Nam. B&aacute;nh thng n vi mt loi nc chm pha nht t nc mm v&agrave; nu l&agrave; b&aacute;nh cun truyn thng th&igrave; kh&ocirc;ng th thiu tinh du c&agrave; cung pha trong nc chm khi n thng k&egrave;m th&ecirc;m ch ln.</p>', '<h2>C&aacute;ch l&agrave;m</h2>\r\n<p style="text-align: justify;">Go ngon, xay mn, h&ograve;a vi nc. t ni hp, cng vi mng tr&ecirc;n ming ni. Mi ln cho mt mu&ocirc;i bt nh. xoa u l&ecirc;n b mt ming vi  l&aacute; b&aacute;nh cun c mng, c&oacute; th thoa th&ecirc;m ch&uacute;t m  d ly ra.<br /><br />Sau khi b&aacute;nh ch&iacute;n, d&ugrave;ng a to hoc thanh tre gt ra a, l&uacute;c n&agrave;y c&oacute; th cun th&ecirc;m nh&acirc;n gm mt &iacute;t tht vai na nc na m, t&ocirc;m, bm c&ugrave;ng mc nh, nm hng &atilde; x&agrave;o ch&iacute;n vi c&aacute;c gia v nh mm, ht ti&ecirc;u... Rc th&ecirc;m h&agrave;nh kh&ocirc; phi thm v&agrave; d&ugrave;ng vi nc chm  c&aacute;c v chua, cay, mn, ngt.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: justify;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc37ba9053_small.jpg" alt="" width="207" height="155" /> <img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc37d048e1_small.JPG" alt="" width="207" height="155" /> <img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc37e5c955_small.jpg" alt="" width="238" height="154" /></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2>B&aacute;nh cun Thanh Tr&igrave;</h2>\r\n<p style="text-align: justify;">B&aacute;nh cun Thanh Tr&igrave; l&agrave; m&oacute;n n ni ting ca ngi H&agrave; Ni, l&agrave; c sn ca phng Thanh Tr&igrave;, Qun Ho&agrave;ng Mai, H&agrave; Ni. B&aacute;nh c l&agrave;m t go gi&eacute; c&aacute;nh, t&aacute;m thm, tr&aacute;ng mng nh t giy.<br /><br />B&aacute;nh cun Thanh Tr&igrave; kh&ocirc;ng c&oacute; nh&acirc;n, thng c xp th&agrave;nh tng lp trong l&ograve;ng mt chic th&uacute;ng, tr&ecirc;n ph t l&aacute; sen hay l&aacute; chui, l&aacute; dong. Ngi b&aacute;n b&aacute;nh thng i th&uacute;ng b&aacute;nh tr&ecirc;n u, di do b&aacute;n tr&ecirc;n ph phng H&agrave; Ni. Khi gp ngi mua, ngi b&aacute;n h&agrave;ng s h th&uacute;ng xung, ln gi tng lp b&aacute;nh cun mng, t&aacute;ch tng lp b&aacute;nh sao l&aacute; b&aacute;nh cun khi b r&aacute;ch. Tr&ecirc;n mt l&aacute; b&aacute;nh cun im nhng cng h&agrave;nh l&aacute; m&agrave;u v&agrave;ng, n&acirc;u &atilde; c phi qua tr&ecirc;n cho. Mi lp b&aacute;nh cun &oacute; s c sp xp gn li tr&ecirc;n a, ln lt tng ming b&aacute;nh mt c t cnh nhau. Sau &oacute;, vi mt nh&aacute;t k&eacute;o, tt c c&aacute;c l&aacute; b&aacute;nh cun c ct &ocirc;i. C&ocirc;ng vic tip theo l&agrave; nhc mt na u b&aacute;nh cun mi ct &oacute;, t l&ecirc;n tr&ecirc;n na kia  ngi thng thc c&oacute; th nh&igrave;n thy r&otilde; tng lp b&aacute;nh cun tr&aacute;ng mng nh giy. B&aacute;nh c n vi th nc chm c trng cho tng ngi b&aacute;n b&aacute;nh ri&ecirc;ng, c&oacute; th n k&egrave;m ch qu, gi&ograve; la hoc u r&aacute;n v&agrave; rau m&ugrave;i.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc45c523c6_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc45c523c6_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc45c523c6_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc45c523c6_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc45c523c6_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc45c523c6_large.jpg', 'active', 2, '16:08:32', 1, 'admin', '16:09:18', 1, 'admin', 1, 'admin', '16:08:52', 0, 0, 0, 0, NULL, 0, 'vi_VN');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (25, 7, 'Ph&nbsp;', '', 'pho', '<p style="text-align: justify;">Ph (Vietnamese pronunciation: often written pho in Australia, New Zealand, United Kingdom, and United States where it is typically pronounced /f/) is a Vietnamese noodle soup, usually served with beef (pho bo) or chicken (pho ga). The soup includes noodles made from rice and is often served with basil, lime, bean sprouts, and peppers that are added to the soup by the consumer.</p>', '<p style="text-align: justify;">The origin of ph is uncertain, and is mostly culled from oral histories. The specific place of origin appears to be southwest of Hanoi in Nam Dinh province, believed to harbor both a Chinese and French connection. The noodles comes from China, and the broth appears to be French in origin, making use of bones and lesser cuts of beef. Some have suggested that ph is derived from pot au feu, the classic French beef stew. Ph did not become popular in South Vietnam until the mid-1950s. Linguistically, however, this is unlikely. In the Vietnamese language, the word ph carries a non-flat category, whereas most French loanwords carry a flat tone, sc or nng tone, depending on the end consonant except the loanwords are ended with -t, -p, -c, -ch consonants. Ph does not match this rule.<br />a plain Cantonese Hofan</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc79c58b72_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>a plain Cantonese Hofan</em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: justify;">Some observers believe ph may come from the Cantonese rice vermicelli Hofan () which are interchangeably abbreviated as either fan2(, phn in T H&aacute;n Vit) or Ho2(, H&agrave; inT H&aacute;n Vit ), the two sounds giving the name "ph".Both fan and pho refer to the same rice noodles found in Vietnam and Guangdong, China, suggesting that rice noodles may have been brought to Vietnam by Cantonese immigrants from the Guangdong province in the early 20th century. The noodles are cooked identically in both places, and are often seasoned using fish sauce, garnished with bean sprouts, and served with meat balls and sliced beef. Vietnamese ph, however, is further garnished with fresh mint, cilantro, basil, bean sprouts, limes, sliced chili peppers and sliced raw beef[9]; this is especially true of Saigon-style ph. Furthermore, the broth of ph is made of beef bones and fresh onion, whereas the Cantonese broth of fan is made of dried flatfish and other seafood. In some regional varieties, the Vietnamese broth may also have a mildly sweet flavour from Asian yellow rock sugar, but the Cantonese version is not.</p>\r\n<p style="text-align: justify;">Vietnamese ph noodle soup with sliced rare beef and well done beef brisket<br />Possibly the earliest reference to ph in English was in the book Recipes of All Nations edited by Countess Morphy in 1935. In the book, ph is described as "an Annamese soup held in high esteem...made with beef, a veal bone, onions, a bayleaf, salt, and pepper, and a small teaspoon of nuoc-man."<br /><br />With the Vietnam war and the victory of the Viet Cong, ph was brought to many countries by Vietnamese refugees fleeing Vietnam from the 1970s onwards. It is especially popular in large cities with substantial Vietnamese populations and enclaves such as Paris in France, the West Coast of Canada[10], the East and West Coast of the United States, Sydney and Melbourne of Australia, and Hong Kong.</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc79d3600b_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Typical garnishes for ph: cilantro, basil, lime, bean sprouts and onions.</em></p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc85f10dc6_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc85f10dc6_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc85f10dc6_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc85f10dc6_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc85f10dc6_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc85f10dc6_large.jpg', 'active', 3, '16:16:37', 1, 'admin', '16:18:28', 1, 'admin', 1, 'admin', '16:16:44', 0, 0, 0, 0, NULL, 0, 'en_US');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (26, 18, 'Ph&nbsp;', '', 'pho', '<p style="text-align: justify;">Ph l&agrave; mt m&oacute;n n truyn thng ca Vit Nam, cng c&oacute; th xem l&agrave; mt trong nhng m&oacute;n n c trng nht cho m thc Vit Nam.</p>', '<p style="text-align: justify;">Th&agrave;nh phn ch&iacute;nh ca ph l&agrave; b&aacute;nh ph v&agrave; nc d&ugrave;ng (hay nc l&egrave;o theo c&aacute;ch gi min Nam) c&ugrave;ng vi tht b&ograve; hoc g&agrave; ct l&aacute;t mng. Ngo&agrave;i ra c&ograve;n k&egrave;m theo c&aacute;c gia v nh: tng, ti&ecirc;u, chanh, nc mm, t... Nhng gia v n&agrave;y c th&ecirc;m v&agrave;o t&ugrave;y theo khu v ca tng ngi d&ugrave;ng. Ph th&ocirc;ng thng d&ugrave;ng l&agrave;m m&oacute;n n im t&acirc;m, hoc n ti.  c&aacute;c tnh ph&iacute;a Nam Vit Nam, ph c b&agrave;y bin vi nhng th&agrave;nh phn ph gi l&agrave; rau thm nh h&agrave;nh, gi&aacute; v&agrave; nhng l&aacute; c&acirc;y rau m&ugrave;i, rau h&uacute;ng, trong &oacute; ng&ograve; gai l&agrave; loi l&aacute; c trng ca ph. Ph thng l&agrave; ph b&ograve;, nhng cng c&oacute; ph g&agrave;, ph heo, ph t&ocirc;m ...</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc79c58b72_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Mt b&aacute;t ph<br /></em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p>Nc d&ugrave;ng n&oacute;i chung c l&agrave;m bng vic hm xng b&ograve;, tht d&ugrave;ng cho m&oacute;n ph l&agrave; tht b&ograve; hoc g&agrave; v&agrave; gia v bao gm qu, hi, gng, tho qu, inh hng, ht m&ugrave;i... "B&aacute;nh ph", theo truyn thng, c l&agrave;m t bt go, tr&aacute;ng th&agrave;nh tm mng ri ct th&agrave;nh si.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc79d3600b_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>a rau i k&egrave;m ph</em><em>.</em></p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc85f10dc6_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc85f10dc6_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc85f10dc6_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc85f10dc6_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc85f10dc6_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cc85f10dc6_large.jpg', 'active', 1, '16:21:44', 1, 'admin', NULL, NULL, NULL, 1, 'admin', '16:22:05', 0, 0, 0, 0, NULL, 0, 'vi_VN');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (27, 3, 'Hng Temple', '', 'hung-temple', '<p style="text-align: justify;">H&ugrave;ng temple, locating in Ngha Lnh mountain, C T&iacute;ch village, Hy Cng commune, Phong Ch&acirc;u district, Ph&uacute; Th province, is the most sacred Vietnamese national historic and cultural complex site.</p>', '<p style="text-align: justify;">H&ugrave;ng temple''s the place of worship H&ugrave;ng Vng- the first King of Vietnam and the first son of Lc Long Qu&acirc;n and &Acirc;u C. H&ugrave;ng temple''s also the place where was the center of the first capital of Vietnam- Phong Ch&acirc;u, according to ancient tales. It is the symbol of glorious &ocirc;ng Sn culture. This temple is the most significant political and social institution of Vietnam. For thousands of year, all Vietnamese dynasties and governments have always regarded it as "Nam Vit Triu T" (place of Vietnamese millionth-generation ancestor). All Vietnamese people are proud of it and remember to celebrate Gi T H&ugrave;ng Vng every 10th day of 3rd lunar month here.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2>Locating</h2>\r\n<p style="text-align: justify;">It told that once upon a time, H&ugrave;ng Vng The First was looking for a place to set up the capital. The King had gone to 99 places but he hadn''t been satisfactory. One day, he gone to Hy Cng village. Immediately, his horse whinny resoundingly. He stopped and gone to the highest mountain- Ngha Lnh, saw four directions. At last, he decided to choose this place.<br /><br />Ph&uacute; Th is fat and green midland area. In Fengshui opinion, Phong Ch&acirc;u is the land of "sn chu thy t" (place where mountains and rivers gather), "long chu h phc" (place where dragons and tigers attend upon), "phng bay nga chy" (place where phoenixes fly and horses run) and the land where''s abundant in "kh&iacute; thi&ecirc;ng s&ocirc;ng n&uacute;i" (sacred country''s qi- flow of energy). This place is full of obstacles and difficult of access that''s easy to protect, but also easy of access to welcome people come from other places. This is the confluence of 3 rivers: &agrave; river, red water- Thao river and clear blue water- L&ocirc; river. In front of Ngha Lnh mountain, it''s Vit Tr&igrave; three- way crossroads where there''re tens of turtle- shape low hill direct to H&ugrave;ng temple. Behind Ngha Lnh mountain, it''s Hy Cng village form a letter- bringing phoenix shape. At the right of this mountain, there''s Khang Ph hill like an attending tiger. At the left of this mountain, there''s An Th&aacute;i hill like an arrow- shooting army general for protecting this temple. At the foot of the mountain, there''s C T&iacute;ch village on the horse- shape hill and Thm Th&igrave;nh village where villagers stay awake throung a night to pound rice in order that Lang Li&ecirc;u make b&aacute;nh chng and b&aacute;nh d&agrave;y. From Thm Th&igrave;nh to Ph&uacute; Lc, there''re hills which''re incarnation of 100 elephants of which 99 elephants direct to H&ugrave;ng temple but 1 elephant didn''t. That''s why the God cut off its head and the hill of this elephant still overflow blood nowadays. Ngha Lnh mountain- 175m high like head of a dragon direct to the South and the body of this dragon are Trc mountain, Vn mountain and Pheo mountain. Ngha Lnh mountain, Vn mountain and Trc mountain''s regarded as "Tam Sn Cm a" (3 forbidden mountain).</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ccb95d5d73_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ccb95d5d73_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ccb95d5d73_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ccb95d5d73_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ccb95d5d73_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ccb95d5d73_large.jpg', 'active', 3, '16:27:03', 1, 'admin', '16:30:03', 1, 'admin', 1, 'admin', '16:27:10', 0, 0, 0, 0, NULL, 0, 'en_US');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (28, 21, 'L hi n Hng', '', 'le-hoi-den-hung', '<p style="text-align: justify;">H&ugrave;ng temple, locating in Ngha Lnh mountain, C T&iacute;ch village, Hy Cng commune, Phong Ch&acirc;u district, Ph&uacute; Th province, is the most sacred Vietnamese national historic and cultural complex site.</p>', '<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; D&ugrave; ai i ngc v xu&ocirc;i<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Nh ng&agrave;y gi t m&ugrave;ng mi th&aacute;ng ba<br /><br />L hi n H&ugrave;ng c&ograve;n gi l&agrave; Gi t H&ugrave;ng Vng l&agrave; mt l hi ln mang tm v&oacute;c quc gia  Vit Nam, tng nh v&agrave; t l&ograve;ng bit n c&ocirc;ng lao lp nc ca c&aacute;c vua H&ugrave;ng, nhng v vua u ti&ecirc;n ca d&acirc;n tc. L hi din ra v&agrave;o ng&agrave;y 10 th&aacute;ng 3 &acirc;m lch, tuy nhi&ecirc;n, l hi thc cht &atilde; din ra t h&agrave;ng tun trc &oacute; vi nhng phong tc nh &acirc;m ung (&aacute;nh trng ng) ca d&acirc;n tc Mng, h&agrave;nh hng tng nim c&aacute;c vua H&ugrave;ng, v&agrave; kt th&uacute;c v&agrave;o ng&agrave;y 10 th&aacute;ng 3 &acirc;m lch vi l rc kiu v&agrave; d&acirc;ng hng tr&ecirc;n n Thng. L hi n H&ugrave;ng hin c Nh&agrave; nc Cng h&ograve;a x&atilde; hi ch ngha Vit Nam n&acirc;ng l&ecirc;n th&agrave;nh quc gi t chc ln v&agrave;o nhng nm chn.<br /><br />C&oacute; 2 l c c h&agrave;nh c&ugrave;ng thi im ng&agrave;y ch&iacute;nh hi:<br /><br />L rc kiu vua: &aacute;m rc kiu vi m&agrave;u sc sc s ca bt ng&agrave;n c, hoa, lng, kiu, trang phc truyn thng xut ph&aacute;t t di ch&acirc;n n&uacute;i ri ln lt qua c&aacute;c n  ti n Thng, ni l&agrave;m l d&acirc;ng hng. &aacute;m rc nh mt con rng un ln tr&ecirc;n nhng bc &aacute; di nhng t&aacute;n l&aacute; c&acirc;y  ti nh n&uacute;i Thi&ecirc;ng.<br /><br />L d&acirc;ng hng: Ngi h&agrave;nh hng ti n H&ugrave;ng ch yu v&igrave; nhu cu ca i sng t&acirc;m linh. Mi ngi u thp l&ecirc;n v&agrave;i n&eacute;n hng khi ti t T  nh l&agrave;n kh&oacute;i thm n&oacute;i h nhng iu t&acirc;m nim ca m&igrave;nh vi t ti&ecirc;n. Trong t&acirc;m hn ngi Vit th&igrave; mi nm t, gc c&acirc;y ni &acirc;y u linh thi&ecirc;ng v&agrave; chng c&oacute; g&igrave; kh&oacute; hiu khi nh&igrave;n thy nhng gc c&acirc;y, hc &aacute; cm  nhng ch&acirc;n hng.<br /><br />Phn hi c&oacute; nhiu tr&ograve; chi d&acirc;n gian c sc. &oacute; l&agrave; nhng cuc thi h&aacute;t xoan (tc h&aacute;t gho), mt h&igrave;nh thc d&acirc;n ca c bit ca Vnh Ph&uacute;, nhng cuc thi vt, thi k&eacute;o co, hay thi bi tri  ng&atilde; ba s&ocirc;ng Bch Hc, ni c&aacute;c vua H&ugrave;ng luyn tp c&aacute;c o&agrave;n thy binh luyn chin.<br /><br />T nm 2007, gi t H&ugrave;ng Vng tr th&agrave;nh quc l. Ng&agrave;y 10/3 &acirc;m lch h&agrave;ng nm l&agrave; ng&agrave;y ngh l. L&ecirc; hi n H&ugrave;ng nhng nm l s do tnh Ph&uacute; Th ng ra t chc. C&aacute;c nm chn s c&oacute; quy m&ocirc;  c&aacute;c cp ln hn. L hi n H&ugrave;ng kh&ocirc;ng ch din ra  khu di t&iacute;ch lch s n H&ugrave;ng Ph&uacute; Th m&agrave; s din ra  nhiu a phng trong c nc nh th&agrave;nh ph H Ch&iacute; Minh, Cn Th, &agrave; Nng .v.v.<br /><br />Theo ngh nh 82/2001/N-CP v vic quy c l hi n H&ugrave;ng th&igrave;:<br /><br />&nbsp;&nbsp;&nbsp; * "Nm chn" l&agrave; s nm k nim c&oacute; ch s cui c&ugrave;ng l&agrave; "0"; B Vn ho&aacute; - Th&ocirc;ng tin v&agrave; y ban Nh&acirc;n d&acirc;n tnh Ph&uacute; Th t chc L hi; mi i din l&atilde;nh o ng, Nh&agrave; nc, Quc hi, Ch&iacute;nh ph, y ban Trung ng Mt trn T quc Vit Nam v&agrave; c&aacute;c o&agrave;n th d l d&acirc;ng hng.<br />&nbsp;&nbsp;&nbsp; * "Nm tr&ograve;n" l&agrave; s nm k nim c&oacute; ch s cui c&ugrave;ng l&agrave; "5 "; y ban Nh&acirc;n d&acirc;n tnh Ph&uacute; Th t chc l hi; mi i din l&atilde;nh o ng, Nh&agrave; nc, Quc hi, Ch&iacute;nh ph, y ban Trung ng Mt trn T quc Vit Nam v&agrave; c&aacute;c o&agrave;n th d l d&acirc;ng hng.<br />&nbsp;&nbsp;&nbsp; * "Nm l" l&agrave; s nm k nim c&oacute; c&aacute;c ch s cui c&ugrave;ng c&ograve;n li. y ban Nh&acirc;n d&acirc;n tnh Ph&uacute; Th t chc l hi; mi l&atilde;nh o B Vn ho&aacute; - Th&ocirc;ng tin d l d&acirc;ng hng v&agrave; t chc c&aacute;c hot ng trong l hi.<br /><br /></p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ccb95d5d73_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ccb95d5d73_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ccb95d5d73_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ccb95d5d73_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ccb95d5d73_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ccb95d5d73_large.jpg', 'active', 3, '16:32:10', 1, 'admin', NULL, NULL, NULL, 1, 'admin', '16:32:21', 0, 0, 0, 0, NULL, 0, 'vi_VN');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (29, 21, 'L hi c  Hoa L', '', 'le-hoi-co-do-hoa-lu', '<p style="text-align: justify;">L hi c &ocirc; l&agrave; mt l hi truyn thng c m  suy t&ocirc;n c&ocirc;ng lao c&aacute;c anh h&ugrave;ng d&acirc;n tc &atilde; x&acirc;y dng kinh &ocirc; Hoa L, lp ra nh&agrave; nc i C Vit th k X m&agrave; ti&ecirc;u biu l&agrave; hai v vua inh Ti&ecirc;n Ho&agrave;ng v&agrave; L&ecirc; i H&agrave;nh.</p>', '<p style="text-align: justify;">L hi din ra ti qung trng trung t&acirc;m khu di t&iacute;ch c &ocirc; Hoa L v&agrave; c&aacute;c di t&iacute;ch. &acirc;y l&agrave; mt l hi c truyn hng v ci ngun dng nc v&agrave; gi nc ca d&acirc;n tc Vit Nam. L hi c&oacute; lch s t khi kinh &ocirc; Hoa L tr th&agrave;nh c &ocirc;. L hi c&ograve;n c&oacute; t&ecirc;n l&agrave; l hi c lau v&igrave; c&oacute; m&agrave;n din t&aacute;i hin cnh inh B Lnh vi tr&ograve; chi "C lau tp trn". L hi truyn thng c &ocirc; Hoa L c&oacute; quy m&ocirc; ln nht v&agrave;o nhng nm c&oacute; s h&agrave;ng n v l&agrave; 3, 5, 8, 0. L hi c m v&agrave;o m&ugrave;a xu&acirc;n, c&oacute; th v&agrave;o ng&agrave;y sinh ca inh B Lnh (15/2 &acirc;m lch), hoc u th&aacute;ng 3 bt u t 6/3 n 10/3 &acirc;m lch (Tng truyn, ng&agrave;y 10/3 l&agrave; ng&agrave;y inh B Lnh l&ecirc;n ng&ocirc;i Ho&agrave;ng , ng&agrave;y 8/3 l&agrave; ng&agrave;y mt ca vua L&ecirc; i H&agrave;nh).[28] Nhc n l hi Trng Y&ecirc;n d&acirc;n gian c&oacute; c&acirc;u: [29] "Ai l&agrave; con ch&aacute;u rng ti&ecirc;n - Th&aacute;ng hai m hi Trng Y&ecirc;n th&igrave; v".</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7ccee48a2a8_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Rng v&agrave;ng trong l hi C Lau</em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: justify;">L rc nc: M u l&agrave; l rc nc, o&agrave;n ngi khi h&agrave;nh t n vua inh t 5-6 gi s&aacute;ng, n b&ecirc;n s&ocirc;ng Ho&agrave;ng Long th&igrave; dng li ly nc v&agrave;o ch&eacute; em v n. Cuc rc c chun b kh&aacute; c&ocirc;ng phu. Trc ng&agrave;y khai hi,  s&ocirc;ng Ho&agrave;ng Long chn mt c&acirc;y tre ln. Tr&ecirc;n ngn tre c&oacute; treo mt di phn m&agrave;u v&agrave;ng, ghi li ch&uacute;. Ni dung nhng li ch&uacute; i lc l&agrave; Thn d&acirc;n, con ch&aacute;u trm h lu&ocirc;n nh n rng v&agrave;ng  s&ocirc;ng n&agrave;y &atilde; cu gi&uacute;p v Ho&agrave;ng  nh&agrave; inh; Cu mong thn s&ocirc;ng gi cho d&ograve;ng nc m&aacute;t hin ho&agrave;, ph&ugrave; tr cho d&acirc;n tr&aacute;nh mi iu &aacute;c d&hellip;[30][31] o&agrave;n rc i theo th t dn u l&agrave; nhng ngi mang c ng sc c&aacute;c loi, i h&agrave;ng &ocirc;i. K tip l&agrave; phng nhc b&aacute;t &acirc;m, phng trng. Tip n l&agrave; mt kiu b&aacute;t cng ln c&oacute; hng &aacute;n do t&aacute;m nam thanh ni&ecirc;n trong trang phc l&iacute;nh tr&aacute;ng nh&agrave; inh xa kho mnh khi&ecirc;ng, tip theo l&agrave; c&aacute;c v quan kh&aacute;ch, i biu trung ng, a phng. Ri tip n l&agrave; nhng kiu b&aacute;t cng c&oacute; t&aacute;n do c&aacute;c thiu n mang l vt. o&agrave;n ngi i sau l&agrave; c&aacute;c b&ocirc; l&atilde;o, nhng i t n quan ca nhiu a phng xa gn, nh&acirc;n d&acirc;n v&agrave; du kh&aacute;ch.<br /><br />L t: Ngay sau khi o&agrave;n rc nc tr v s&acirc;n khu trung t&acirc;m l hi, sau nghi thc th rng bay th&igrave; khi im phn l t ti 2 n Vua inh, Vua L&ecirc;. Phn t c&oacute; s tham gia ca nhiu o&agrave;n theo lch ng k&yacute;. C&aacute;c o&agrave;n rc kiu v&agrave; ch&acirc;n nhang t c&aacute;c di t&iacute;ch th c&aacute;c danh nh&acirc;n thi inh - L&ecirc; s tham gia rc kiu v 2 n, hu ht c&aacute;c o&agrave;n  c ly xa phi rc tr&ecirc;n xe l hi tin v Hoa L. L t c tin h&agrave;nh sau &oacute; c ban ng&agrave;y v&agrave; ban &ecirc;m  c n vua inh v&agrave; vua L&ecirc; vi ni dung ca ngi c&ocirc;ng c ca hai v vua, sen k &oacute; du kh&aacute;ch v&agrave;o thp hng tng nim. Tham gia l t c&oacute; nhiu o&agrave;n trong v&ugrave;ng.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7ccee363d4b_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>L t c truyn ti n Vua inh Ti&ecirc;n Ho&agrave;ng</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">M&agrave;n s&acirc;n khu h&oacute;a: &acirc;y l&agrave; m&agrave;n din s&acirc;n khu ng i  khai mc l hi v&agrave; truyn h&igrave;nh trc tip. Sau li din vn khai mc l&agrave; m&agrave;n trng hi Hoa L, c&aacute;c m&agrave;n din t&aacute;i hin lch s trng i din ra ti kinh &ocirc; Hoa L xa nh: s kin l&ecirc;n ng&ocirc;i ho&agrave;ng  ca inh B Lnh v&agrave; L&ecirc; Ho&agrave;n; &aacute;nh thng gic Tng; di &ocirc; v Thng Long v&agrave; kt th&uacute;c l&agrave; m&agrave;n th rng bay l&ecirc;n cho ti ht bui s&aacute;ng khai mc.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd2f967c8c_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd2f967c8c_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd2f967c8c_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd2f967c8c_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd2f967c8c_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd2f967c8c_large.jpg', 'active', 2, '16:42:26', 1, 'admin', '17:01:33', 1, 'admin', 1, 'admin', '16:42:49', 0, 0, 0, 0, NULL, 0, 'vi_VN');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (41, 8, 'Cm tm', '', 'com-tam', '<p style="text-align: justify;">Cm tm in Vietnamese is cooked rice from fractured rice grains. Tm refers to the broken rice grains while cm refers to cooked rice.<br /><br /></p>', '<p style="text-align: justify;">It is usually served with grilled pork (either ribs or shredded) plus a Vietnamese dish called b&igrave; (thinly shredded pork mixed with cooked and thinly shredded pork skin) over broken rice. The rice and meat are served with various greens and pickled vegetables, along with a prawn paste cake, trng hp (baked egg), and grilled prawns. Typically restaurants will serve this popular combination rice plate with a small bowl of nc chm, as well as a small bowl of soup broth (canh) with garlic chives (to cleanse the throat).</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce1fb72ae2_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Cm tm with grilled pork, shredded pork, pork skin, fried egg, pork meatloaf, pickled carrot, and sliced cucumber</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Broken_Rice" class="mw-headline">Broken Rice</span></h2>\r\n<p style="text-align: justify;">Some rice grains break in the transport and processing of the rice from the field to the pot. Machinery is available to separate the broken grains from the whole grains. Broken rice may or may not have lower fiber and nutrient content, but generally has a similar energy content to intact rice.<br /><br />The broken varieties are often less expensive, so are preferred by poorer consumers or used as raw material (such as in beer brewing). ue to the different size and shape of the grains, broken rice has a different texture from "unbroken" rice. Some chefs and consumers may prefer the qualities of broken rice for certain dishes.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce2687f4e4_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce2687f4e4_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce2687f4e4_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce2687f4e4_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce2687f4e4_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce2687f4e4_large.jpg', 'active', 1, '18:07:28', 1, 'admin', NULL, NULL, NULL, 1, 'admin', '18:07:36', 0, 0, 0, 0, NULL, 0, 'en_US');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (30, 3, 'Hoa Lu ancient capital festival', '', 'hoa-lu-ancient-capital-festival', '<p style="text-align: justify;">Hoa Lu ancient capital Festival opened in the spring.</p>', '<p style="text-align: justify;">Start a ceremony at the Yellow River Long procession of the temple 2 Kings. Group stage near the procession of King Dinh Tien Hoang Temple. There will be many games in the Assembly. Hoa Lu Festival described the history of Vietnam from 968 to 1010 with many changes.</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7ccee363d4b_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Hoa Lu ancient capital Festival&nbsp; </em></p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd2542ef64_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd2542ef64_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd2542ef64_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd2542ef64_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd2542ef64_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd2542ef64_large.jpg', 'active', 2, '17:00:21', 1, 'admin', NULL, NULL, NULL, 1, 'admin', '17:00:29', 0, 0, 0, 0, NULL, 0, 'en_US');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (31, 3, 'Lantern Festival', '', 'lantern-festival', '<p style="text-align: justify;">The Lantern Festival or "Tt Thng Nguy&ecirc;n" or "Tt Nguy&ecirc;n Ti&ecirc;u"(Vietnamese: Tt Nguy&ecirc;n ti&ecirc;u) in Vietnam; is a festival celebrated on the fifteenth day of the first month in the lunar year in the Chinese calendar, the last day of the Lunar Chinese New Year celebration.</p>', '<p style="text-align: justify;">It is not to be confused with the Mid-Autumn Festival, which is sometimes also known as the "Lantern Festival" in locations such as Singapore and Malaysia. During the Lantern Festival, children go out at night to temples carrying paper lanterns and solve riddles on the lanterns. It officially ends the Chinese New Year celebrations.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd59188688_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Lantern Festival celebration in Shijiazhuang.</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">In ancient times, the lanterns were fairly simple, for only the emperor and noblemen had large ornate ones; in modern times, lanterns have been embellished with many complex designs. For example, lanterns are now often made in shapes of animals.<br /><br />The Lantern Festival is also known as the Little New Year since it marks the end of the series of celebrations starting from the Chinese New Year.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd59084397_crop.JPG" alt="" /></p>\r\n<p style="text-align: center;"><em>Year 2008 Taiwan Lantern Festival in Tainan, Taiwan.</em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: justify;">In some region and countries, this festival is also regarded as the Chinese version of St. Valentine''s Day, a day celebrating love and affection between lovers in Chinese tradition and culture.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd59188688_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd59188688_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd59188688_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd59188688_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd59188688_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd59188688_large.jpg', 'active', 3, '17:14:03', 1, 'admin', '17:33:02', 1, 'admin', 1, 'admin', '17:14:09', 0, 0, 0, 0, NULL, 0, 'en_US');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (32, 21, 'Tt Nguyn Tiu', '', 'tet-nguyen-tieu', '<p style="text-align: justify;">Tt Nguy&ecirc;n Ti&ecirc;u hay tt Thng Nguy&ecirc;n l&agrave; mt tt, l hi c truyn ca Trung Quc v&agrave;o ng&agrave;y 15 (ng&agrave;y rm) th&aacute;ng gi&ecirc;ng &Acirc;m lch.</p>', '<p style="text-align: justify;">Tt n&agrave;y phn ln t chc ti ch&ugrave;a, v&igrave; ng&agrave;y rm th&aacute;ng gi&ecirc;ng c&ograve;n l&agrave; ng&agrave;y v&iacute;a ca Pht t. V&agrave;o ng&agrave;y n&agrave;y, cng c&oacute; gia &igrave;nh Vit Nam t tp  nh&agrave; trng h hoc nh&agrave; th h.<br /><br />Theo s&aacute;ch Trung Hoa, l Thng Nguy&ecirc;n kh&ocirc;ng phi l&agrave; mt ng&agrave;y l Pht. Trc &acirc;y ch&iacute;nh l&agrave; Tt Trng Nguy&ecirc;n. Nh&acirc;n dp n&agrave;y, nh&agrave; vua hi hp c&aacute;c &ocirc;ng Trng  tht tic v&agrave; mi v&agrave;o vn Thng Uyn thm hoa, ngm cnh, l&agrave;m th.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd59188688_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Hi hoa ng ti Thch Gia Trang</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<h2>Tp tc v&agrave; l hi</h2>\r\n<p style="text-align: justify;"> Trung Quc v&agrave; &agrave;i Loan hin nay, Tt Nguy&ecirc;n Ti&ecirc;u (rm th&aacute;ng gi&ecirc;ng, &ecirc;m rm u ti&ecirc;n ca nm mi) c coi l&agrave; ng&agrave;y l thi&ecirc;ng li&ecirc;ng nht u nm mi v&agrave; c&ograve;n c gi l&agrave; "L hi &egrave;n hoa" hoc "Hi hoa ng", c&oacute; th bt ngun t tc c&uacute;ng t thi H&aacute;n V , vi tp tc trng &egrave;n tr&ecirc;n c&acirc;y n&ecirc;u trc ca nh&agrave;, t &egrave;n, chi lng &egrave;n ng sc, c&oacute; th k&eacute;o t 13 n 17 th&aacute;ng gi&ecirc;ng. c y&ecirc;u chung l&agrave; nhng lng &egrave;n c&oacute; h&igrave;nh th&ugrave; rng, phng, mi hai con gi&aacute;p hoc nhng nh&acirc;n vt c trong truyn thuyt, c t&iacute;ch. Ngo&agrave;i ra c&ograve;n nhng tp tc kh&aacute;c nh : c&uacute;ng t cu an cu phc, n b&aacute;nh tr&ocirc;i (gi l&agrave; "thang vi&ecirc;n" - vi&ecirc;n tr&ograve;n trong nc), thi o&aacute;n h&igrave;nh th&ugrave; tr&ecirc;n lng &egrave;n, ng&acirc;m th. Ngi &agrave;i Loan c&ograve;n ghi nhng c&acirc;u c nguyn ca m&igrave;nh v&agrave;o &egrave;n lng v&agrave; th bay l&ecirc;n tri. Nhiu ngi c&ograve;n coi &acirc;y l&agrave; m&ugrave;a Valentine phng &ocirc;ng, tng t nh l Tht Tch. Th ng xa &atilde; vit: Nguy&ecirc;n ti&ecirc;u chi d hoa lng nguyt, m&ugrave;a trng tr&ograve;n lung linh sc m&agrave;u hoa ng rc r cng l&agrave; dp Ngu Lang Chc N hin i gp g se duy&ecirc;n.</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd68c98531_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>L hi &egrave;n hoa nm 2007 ti Gia Ngha, &agrave;i Loan</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;"> Philippines, c&oacute; l hi diu h&agrave;nh truyn thng v&agrave;o ng&agrave;y rm th&aacute;ng gi&ecirc;ng, &aacute;nh du khi u nm mi.  Vit Nam, nhng ni c&oacute; &ocirc;ng cng ng ngi Hoa sinh sng nh Ch Ln, Hi An cng c&oacute; nhiu sinh hot c bit.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd6b62062a_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd6b62062a_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd6b62062a_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd6b62062a_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd6b62062a_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd6b62062a_large.jpg', 'active', 1, '17:17:33', 1, 'admin', NULL, NULL, NULL, 1, 'admin', '17:17:47', 0, 0, 0, 0, NULL, 0, 'vi_VN');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (33, 2, 'Canh chua', '', 'canh-chua', '<p>Canh chua (literally "sour soup") is a sour soup indigenous to the Mekong River region of southern Vietnam.</p>', '<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd9c2e4dfa_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A bowl of <em>canh chua</em></em></p>\r\n<p>&nbsp;</p>\r\n<p>It is typically made with fish from the Mekong River, pineapple, tomatoes (and sometimes also other vegetables such as okra or bc h&agrave;), and bean sprouts, in a tamarind-flavored broth. It is garnished with the lemony-scented herb ng&ograve; &ocirc;m (Limnophila aromatica), caramelized garlic, and chopped scallions, as well as other herbs, according to the specific variety of canh chua; these other herbs may include rau rm (Vietnamese coriander), ng&ograve; gai (long coriander), and rau qu (Thai basil).<br /><br />The sour taste of the soup comes from tamarind, which is mixed with a small amount of hot water; the mixture is then stirred for a few moments to release all the essence, and the liquid (minus the tamarind seeds and other solids, which are discarded) is then added to the soup.<br /><br />When made in style of a hot pot, canh chua is called lu canh chua.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cda8d94a50_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cda8d94a50_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cda8d94a50_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cda8d94a50_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cda8d94a50_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cda8d94a50_large.jpg', 'active', 1, '17:33:55', 1, 'admin', NULL, NULL, NULL, 1, 'admin', '17:34:04', 0, 0, 0, 0, NULL, 0, 'en_US');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (34, 17, 'Canh chua', '', 'canh-chua', '<p style="text-align: justify;">Canh chua l&agrave; t&ecirc;n gi ca nhng m&oacute;n n nhiu nc v&agrave; c&oacute; v chua do c nu bng c&aacute;c nguy&ecirc;n liu phi trn vi gia v to chua.</p>', '<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cd9c2e4dfa_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Mt b&aacute;t <em>canh chua</em></em></p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: justify;">Canh chua thng c d&ugrave;ng  nhng v&ugrave;ng n&oacute;ng nh min Nam, min Trung, hoc nhng l&uacute;c n&oacute;ng nc ca m&ugrave;a h&egrave; min Bc Vit Nam, m&oacute;n canh chua n&agrave;y cng c bit ph bin trong ngh thut m thc H&agrave; Ni.<br /><br />Nguy&ecirc;n liu ch&iacute;nh  nu canh chua l&agrave; loi rau c qu, c&aacute;c loi tht hay thy sn (c&aacute;, t&ocirc;m, cua, c, hn, trai, s&ograve;) kh&aacute;c nhau, trong &oacute; thng d&ugrave;ng mt loi rau chua hoc mt gia v chua  to v chua thm ngon cho nc canh. Rt ph bin c&aacute;c m&oacute;n canh chua s dng nguy&ecirc;n liu ch&iacute;nh l&agrave; mt loi thy sn, hi sn, do cht to chua c&oacute; t&aacute;c dng kh m&ugrave;i v tanh ca nguy&ecirc;n liu n&agrave;y. Cng kh&ocirc;ng him khi canh chua ch l&agrave; mt b&aacute;t nc luc rau cho th&ecirc;m ch&uacute;t nc ct chanh, ch&uacute;t l&aacute; me hay v&agrave;i qu su xanh.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2>c im</h2>\r\n<p style="text-align: justify;">Mt c im rt quan trng &atilde; c d&acirc;n gian &uacute;c kt bao i l&agrave;  nu ngon c&aacute;c m&oacute;n canh chua, t&ugrave;y theo nguy&ecirc;n liu ch&iacute;nh l&agrave; g&igrave;, s c s dng cht to chua v&agrave; liu lng chua kh&aacute;c nhau. C&aacute;c loi canh c&aacute; nu chua kh&ocirc;ng bao gi d&ugrave;ng qu su hay chanh m&agrave; bt buc phi d&ugrave;ng bng ru, dc, da v.v. Nc rau mung luc  ngui vt nc ct chanh, hoc cho qu su hay v&agrave;i l&aacute;t tai chua khi nc c&ograve;n n&oacute;ng. Canh ri&ecirc;u cua nu dm bng hoc qu dc nng ch&iacute;n b&oacute;c v. Canh ri&ecirc;u hn, trai, tr&ugrave;ng trc phi nu vi me. S phi trn gia v to chua v&agrave; nguy&ecirc;n liu ch&iacute;nh c&oacute; th linh ng, thng da tr&ecirc;n kinh nghim m thc ca ngi ni tr.<br /><br />Th&ecirc;m v&agrave;o &oacute;, c&aacute;c loi rau gia v (nh h&agrave;nh, th&igrave; l&agrave;, l&aacute; gc, rau rm v.v.) c gia v&agrave;o m&oacute;n canh chua phi t&ugrave;y theo loi, nh canh ri&ecirc;u cua kh&ocirc;ng th thiu h&agrave;nh, canh c&aacute; nu chua phi d&ugrave;ng th&igrave; l&agrave;, canh hn d&ugrave;ng rau rm v.v. C&aacute;c loi rau gia v kh&ocirc;ng c gia v&agrave;o ni canh trong l&uacute;c nu, thm ch&iacute; kh&ocirc;ng n&ecirc;n  di &aacute;y b&aacute;t m&uacute;c canh l&ecirc;n v&igrave; di t&aacute;c dng ca v chua rau s v&agrave;ng, tr&ocirc;ng mt ngon. Thng thng ngi ta hay m&uacute;c canh ra b&aacute;t v&agrave; rc rau gia v l&ecirc;n tr&ecirc;n.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cda8d94a50_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cda8d94a50_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cda8d94a50_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cda8d94a50_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cda8d94a50_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cda8d94a50_large.jpg', 'active', 1, '17:38:36', 1, 'admin', '17:39:16', 1, 'admin', 1, 'admin', '17:38:55', 0, 0, 0, 0, NULL, 0, 'vi_VN');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (42, 19, 'Cm tm', '', 'com-tam', '<p style="text-align: justify;">Cm tm l&agrave; m&oacute;n c sn ca min Nam Vit Nam, n&oacute; l&agrave; mt trong nhng m&oacute;n n s&aacute;ng c a chung nht ca ngi min Nam, nht l&agrave; S&agrave;i G&ograve;n. Hin nay loi cm l&agrave;m t ht go b n&agrave;y &atilde; c&oacute; mt  mt s ni thuc min Trung, min Bc v&agrave; hi ngoi.</p>', '<p style="text-align: justify;">Da cm tm vi sn, b&igrave;, ch, trng thng c d&ugrave;ng l&agrave;m qu&agrave; s&aacute;ng, nhng nay cm tm &atilde; c&oacute;  mt s qu&aacute;n n tra, chiu hoc ti vi nhiu loi thc n k&egrave;m.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce1fb72ae2_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Mt da cm tm vi y  sn, b&igrave;, ch v&agrave; trng p-la</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Th.C3.A0nh_ph.E1.BA.A7n" class="mw-headline">Th&agrave;nh phn</span></h2>\r\n<p style="text-align: justify;"><strong>- Cm</strong><br /><br />Cm tm c nu t ht go tm, tc ht go b b. Go n&agrave;y xa l&agrave; loi go th phm, rt v&atilde;i sau khi s&agrave;ng, thng d&ugrave;ng cho g&agrave; n hoc cho ngi n l&uacute;c qu&aacute; t&uacute;ng thiu. Nay th&igrave; cm tm &atilde; th&agrave;nh m&oacute;n n quen thuc do &oacute; go tm cng c n&acirc;ng gi&aacute; cao hn.</p>\r\n<p style="text-align: justify;"><br />- <strong>Nc mm</strong><br /><br />n cm tm phi c&oacute; nc mm ngt, l&agrave; nc mm pha vi nc lc v&agrave; th&ecirc;m ng. T&ugrave;y theo c&aacute;ch ch bin v&agrave; khu v ca ngi n, nc mm c&oacute; th ngt nhiu hoc ngt &iacute;t (mn), c&oacute; th th&ecirc;m chanh.</p>\r\n<p style="text-align: justify;"><br />- <strong>M&oacute;n n k&egrave;m</strong><br /><br />Cm tm c&oacute; th n k&egrave;m vi nhiu th, nhng nhiu nht l&agrave; Sn nng, ch trng, trng p-la v&agrave; b&igrave;.</p>\r\n<p style="text-align: justify;"><br />- <strong>Sn, b&igrave;, ch, trng</strong><br /><br />Mt da cm tm c&oacute; th gm c sn, b&igrave;, ch, trng hoc kh&ocirc;ng gm y  c&aacute;c m&oacute;n tr&ecirc;n, nhng &acirc;y l&agrave; 4 thc n th&ocirc;ng dng nht v&agrave; ngi ta thng xp t&ecirc;n ch&uacute;ng b&ecirc;n nhau tr&ecirc;n c&aacute;c bin hiu cm tm:<br /><br />&nbsp;&nbsp;&nbsp; * Sn: Sn n vi cm tm l&agrave; loi sn heo c tm p gia v chua ngt, sau &oacute; em nng. Thng c&aacute;c qu&aacute;n cm tm nng sn ngay trc ca tim, kh&oacute;i hng bc ra mang theo m&ugrave;i v c trng ca sn nng v&agrave; nhiu ngi nhn ra ngay l&agrave; h va i ngang qua qu&aacute;n cm tm.<br />&nbsp;&nbsp;&nbsp; * Ch: Gi l&agrave; ch hoc ch trng, c l&agrave;m t trng, cua, tht xay, nm m&egrave;o v&agrave; b&uacute;n t&agrave;u. Ch trng c chng sn th&agrave;nh mt c&aacute;i b&aacute;nh h&igrave;nh tr&ograve;n hoc ch nht, khi n xt l&aacute;t.<br />&nbsp;&nbsp;&nbsp; * Trng: Trng  &acirc;y thng l&agrave; trng g&agrave; p la.<br />&nbsp;&nbsp;&nbsp; * B&igrave;: l&agrave; hn hp nhiu th, thng gm tht heo ct si, da heo ct si trn vi th&iacute;nh v&agrave; gia v.<br /><br />- <strong>M h&agrave;nh</strong><br /><br />M h&agrave;nh cng rt quan trng trong da cm tm. N&oacute; l&agrave; hn hp lng c l&agrave;m t h&agrave;nh l&aacute;, phi du hoc m, &ocirc;i khi trn vi t&oacute;p m chi&ecirc;n. M h&agrave;nh gi&uacute;p cm tm c&oacute;  b&eacute;o c trng, tuy nhi&ecirc;n mt s ngi kh&ocirc;ng n v&igrave; nhiu l&yacute; do.</p>\r\n<p style="text-align: justify;"><br />- <strong> chua</strong><br /><br /> chua gi&uacute;p cm tm c&oacute; v chua ngt. Thng l&agrave;m t u , c&agrave; rt, c ci, da leo, &ocirc;i khi l&agrave; c&agrave; chua.</p>\r\n<p style="text-align: justify;"><br />- <strong>C&aacute;c thc kh&aacute;c</strong><br /><br />&ocirc;i khi cm tm c n vi tht kho, u h nhi tht, c&aacute; chi&ecirc;n, g&agrave;, rau,  x&agrave;o... ging nh cm go nguy&ecirc;n ht. Kiu n n&agrave;y thng thy  c&aacute;c qu&aacute;n cm tm &ecirc;m.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce2687f4e4_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce2687f4e4_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce2687f4e4_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce2687f4e4_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce2687f4e4_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce2687f4e4_large.jpg', 'active', 1, '18:12:07', 1, 'admin', '18:16:39', 1, 'admin', 1, 'admin', '18:12:17', 0, 0, 0, 0, NULL, 0, 'vi_VN');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (35, 2, 'Bnh c', '', 'banh-duc', '<p>B&aacute;nh &uacute;c is a term used to refer to two different varieties of Vietnamese b&aacute;nh, or cake.</p>', '<h2>Northern Vietnamese version</h2>\r\n<p style="text-align: justify;">In northern Vietnam, b&aacute;nh &uacute;c is a cake made from either non-glutinous rice flour or corn flour. It is white in color and has a soft texture and mild flavour.photo It is typically garnished with savory ingredients such as ground pork, t&ocirc;m chy (grilled ground shrimp),photo fried onions, sesame seeds, salt, peanuts, lime juice, and soy sauce or fish sauce.photo Although it may be eaten on its own, it may also be served hot, accompanied by steamed meat or mushrooms.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdd066d361_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A bowl of northern Vietnamese b&aacute;nh &uacute;c tht (b&aacute;nh &uacute;c with ground pork and fish sauce)</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">B&aacute;nh &uacute;c is available at small stalls and is eaten throughout the day.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Southern_Vietnamese_version" class="mw-headline">Southern Vietnamese version</span></h2>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A plate of southern Vietnamese b&aacute;nh &uacute;c colored with Pandanus amaryllifolius leaf extract, and topped with sweetened coconut milk and sesame seeds</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">In southern Vietnam, b&aacute;nh &uacute;c is a dessert made from non-glutinous rice flour. It takes the form of gelatinous blocks that are often colored green by the addition of Pandanus amaryllifolius leaf extract. It is cooked by boiling the ingredients and allowing them to cool, solidifying into a jelly-like sheetphoto that is then cut into blocks.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_large.jpg', 'active', 3, '17:45:30', 1, 'admin', '18:20:23', 1, 'admin', 1, 'admin', '17:45:37', 0, 0, 0, 0, NULL, 0, 'en_US');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (36, 17, 'Bnh c', '', 'banh-duc', '<p style="text-align: justify;">B&aacute;nh &uacute;c l&agrave; mt loi b&aacute;nh ca Vit Nam, thng c l&agrave;m bng bt go (ti min Bc v&agrave; min Trung) hoc bt nng (min Nam) vi mt s gia v. B&aacute;nh c l&agrave;m th&agrave;nh tm to, khi n th&igrave; ct nh th&agrave;nh ming t&ugrave;y th&iacute;ch.</p>', '<p style="text-align: justify;">L&agrave; m&oacute;n n d&acirc;n d&atilde; thnh h&agrave;nh khp ba min, b&aacute;nh &uacute;c n gi&ograve;n, m&aacute;t, mn, no bng m&agrave; li d ti&ecirc;u d l&agrave;m v&agrave; gi&aacute; th&agrave;nh cng rt r. Kh&ocirc;ng ch c n nh mt thc qu&agrave; qu&ecirc;, ba n s&aacute;ng m&agrave; in h&igrave;nh l&agrave; b&aacute;nh &uacute;c chm tng, b&aacute;nh &uacute;c cng c&oacute; th n k&egrave;m vi canh ri&ecirc;u cua, rau thm, mm t&ocirc;m, mt ong, mt m&iacute;a, mt tr&aacute;i c&acirc;y v&agrave; thm ch&iacute; c c&aacute; kho, tht kho t&ugrave;y th&iacute;ch. T loi b&aacute;nh &uacute;c thun t&uacute;y ch c bng bt go pha vi nc v&ocirc;i trong ngai ng&aacute;i m&ugrave;i v&ocirc;i, b&aacute;nh &uacute;c cng &atilde; c&oacute; nhiu bin tu nh b&aacute;nh &uacute;c cm thch, b&aacute;nh &uacute;c lc, b&aacute;nh &uacute;c da, b&aacute;nh &uacute;c ng&ocirc; v.v.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdd066d361_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>B&aacute;nh &uacute;c tht kiu min Bc</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">B&aacute;nh &uacute;c is available at small stalls and is eaten throughout the day.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Quy_tr.C3.ACnh_th.E1.BB.B1c_hi.E1.BB.87n" class="mw-headline">Quy tr&igrave;nh thc hin</span></h2>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>B&aacute;nh &uacute;c kiu min Nam vi nc ct da, m&egrave;, nhum m&agrave;u bng l&aacute; da thm</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">Quy tr&igrave;nh thc hin b&aacute;nh &uacute;c, d&ugrave; vi nhiu bin th, v c bn bao gm 3 c&ocirc;ng on: l&agrave;m nc ng&acirc;m go, chun b bt v&agrave; un b&aacute;nh. Di &acirc;y ch tr&igrave;nh b&agrave;y m&oacute;n b&aacute;nh &uacute;c bt go, l&agrave; m&oacute;n c bn v&agrave; ph bin nht trong h b&aacute;nh &uacute;c.<br /><br />Nc ng&acirc;m go c l&agrave;m bng nc v&ocirc;i trong hoc nc tro. V&ocirc;i t&ocirc;i (loi v&ocirc;i mm nhuyn  trn va x&acirc;y nh&agrave;, v&agrave; c&aacute;c c  Vit Nam ng&agrave;y xa thng d&ugrave;ng n tru) c pha vi nc, thng vi t l 1 l&iacute;t nc 10g v&ocirc;i. Khuy u  lng v&agrave; cht ly nc v&ocirc;i trong. Vi nc tro, ngi ta thng ly tro bp, loi tro un ci hoc l&aacute; c&acirc;y kh&ocirc;ng ln tp cht ca c&aacute;c loi nhi&ecirc;n liu kh&aacute;c, s&agrave;ng xy tht k v&agrave; h&ograve;a v&agrave;o nc ng&acirc;m go. Mt s ni cu k c&ograve;n s dng c&aacute;c loi c&agrave;nh xoan, c&agrave;nh bi, l&aacute; si, d&acirc;y tm gi v.v.  t th&agrave;nh tro v&agrave; ly tro &oacute; h&ograve;a nc.<br /><br />Chn loi go ngon, t&ugrave;y loi go s quyt nh cht lng b&aacute;nh ( dai, gi&ograve;n, n; m&agrave;u sc; hng v). Go c vo, &atilde;i sch v&agrave; ng&acirc;m trong nc v&ocirc;i trong hoc nc tro &atilde; n&oacute;i  tr&ecirc;n trong mt thi hn nht nh c&oacute; th mi ting ng h hoc v&agrave;i ng&agrave;y (t&ugrave;y a phng, t&ugrave;y loi go) cho n khi tay b&oacute;p ht go thy b ti. Sau &oacute; go c em xay ln vi nc cho ra bt nc. Bt li xay i xay li v&agrave;i ln cho n khi tht nhuyn mn v&agrave; sau &oacute; c&ograve;n c lc qua r&acirc;y cho sch tp cht.<br /><br /> to  dai gi&ograve;n ca b&aacute;nh c&oacute; khi ngi ta c&ograve;n d&ugrave;ng c h&agrave;n the h&ograve;a ln v&agrave;o bt d&ugrave; rng &acirc;y l&agrave; ph gia kh&ocirc;ng m bo v sinh an to&agrave;n thc phm.<br /><br />Ni c l&aacute;ng mt ch&uacute;t m nc  bt kh&ocirc;ng d&iacute;nh &aacute;y,  bt v&agrave;o v&agrave; bc l&ecirc;n bp un nh la, khuy tht u tay v&agrave; li&ecirc;n tc sao cho bt kh&ocirc;ng v&oacute;n, kh&ocirc;ng kh&ecirc;, kh&ocirc;ng s&aacute;t ni, cho n khi bt trong ni c st li, tr n&ecirc;n trong mt, ht mt a bt l&ecirc;n thy bt chy xung kh&ocirc;ng d&iacute;nh a, th&igrave; tt bp. Rt thng thy b&aacute;nh &uacute;c bt go n&agrave;y c&ograve;n kt hp vi lc nh&acirc;n luc ch&iacute;n b&oacute;c v v&agrave; cm da xt vn. Khi &oacute; lc v&agrave; da c th&ecirc;m v&agrave;o ni bt trc thi im nhc ni xung.<br /><br />D&ugrave;ng du n, nc m ln hoc ch&uacute;t &oacute;c ln t&aacute;n nhuyn thoa u l&ecirc;n l&ograve;ng mt c&aacute;i a to, hoc c&aacute;c khu&ocirc;n b&aacute;nh.  bt v&agrave;o vi  d&agrave;y khong 3-5cm.  ngui th&agrave;nh phm s &ocirc;ng cng li v&agrave; c&oacute; th xt ming  n k&egrave;m c&aacute;c thc phm kh&aacute;c (tng, mt, c&aacute; kho, canh cua, u ph r&aacute;n, tht ln luc v.v.).</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_large.jpg', 'active', 1, '17:49:50', 1, 'admin', NULL, NULL, NULL, 1, 'admin', '17:50:01', 0, 0, 0, 0, NULL, 0, 'vi_VN');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (37, 6, 'Bn Thnh Market', '', 'ben-thanh-market', '<p style="text-align: justify;">Ben Thanh Market (from Vietnamese Ben meaning "wharf", and Quy Thanh meaning "turtle citadel") is a big marketplace in the downtown area of Ho Chi Minh City, Vietnam, in District 1. The market is one of the earliest surviving structures in Saigon and today is considered one of symbols of Ho Chi Minh City, popular with tourists seeking local handicrafts, textiles, &aacute;o d&agrave;i, and souvenirs, as well as local cuisine.</p>', '<h2 style="text-align: justify;"><span id="History" class="mw-headline">History</span></h2>\r\n<p style="text-align: justify;">The market developed from informal markets created by early 17th century street vendors gathering together near the Saigon River. The market was formally established by the French colonial powers after taking over the Gia Dinh citadel in 1859 (see Citadel of Saigon). This market was destroyed by fire in 1870 and rebuilt to become Saigon''s largest market. In 1912 the market was moved to a new building and called the New Ben Thanh Market to distinguish over its predecessor. The building was renovated in 1985.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce02254cc8_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Bn Th&agrave;nh Market</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2 style="text-align: justify;"><span id="Transport" class="mw-headline">Transport</span></h2>\r\n<p style="text-align: justify;">Apart from being a major hub for the network of city buses serving Ho Chi Minh City, Ben Thanh Market is planned to be a hub for several lines of the planned Ho Chi Minh City Metro; Line 1, already under construction, would connect Ben Thanh with Suoi Tien Park and Long Binh in District 9. Other lines would connect Ben Thanh with Tham Luong in District 12, and with Cholon and Binh Tan District.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce021481af_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Ben Thanh Market</em></p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdffa0e6ad_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdffa0e6ad_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdffa0e6ad_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdffa0e6ad_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdffa0e6ad_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdffa0e6ad_large.jpg', 'active', 0, '17:58:41', 1, 'admin', '18:24:10', 1, 'admin', 1, 'admin', '17:58:48', 0, 0, 0, 0, NULL, 0, 'en_US');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (38, 12, 'Mid-Autumn Festival', '', 'mid-autumn-festival', '<p>The Mid-Autumn Festival, also known as the Moon Festival, Zhongqiu Festival, or in Chinese, Zhongqiujie, or in Vietnamese "Tt Trung Thu", is a popular harvest festival celebrated by Chinese and Vietnamese people, dating back over 3,000 years to moon worship in China''s Shang Dynasty.</p>', '<p>The Mid-Autumn Festival is held on the 15th day of the eighth month in the Chinese calendar, which is usually around late September or early October in the Gregorian calendar. It is a date that parallels the autumnal equinox of the solar calendar, when the moon is supposedly at its fullest and roundest. The traditional food of this festival is the mooncake, of which there are many different varieties.</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce033ae1f6_medium.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Mid-Autumn Festival at the Botanical Garden, Montreal</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p>The Mid-Autumn Festival is one of the few most important holidays in the Chinese calendar, the others being Chinese New Year and Winter Solstice, and is a legal holiday in several countries. Farmers celebrate the end of the summer harvesting season on this date. Traditionally on this day, Chinese family members and friends will gather to admire the bright mid-autumn harvest moon, and eat moon cakes and pomelos under the moon together.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce032b90af_medium.JPG" alt="" /></p>\r\n<p style="text-align: center;"><em>Mid-Autumn Festival celebrations in Victoria Park, Hong Kong</em></p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce033ae1f6_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce033ae1f6_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce033ae1f6_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce033ae1f6_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce033ae1f6_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce033ae1f6_large.jpg', 'active', 2, '18:00:14', 1, 'admin', '18:23:15', 1, 'admin', 1, 'admin', '18:00:22', 0, 0, 0, 0, NULL, 0, 'en_US');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (39, 16, 'Ch Bn Thnh', '', 'cho-ben-thanh', '<p style="text-align: justify;">Ch Bn Th&agrave;nh l&agrave; mt trong nhng a im ti&ecirc;u biu ca Th&agrave;nh ph H Ch&iacute; Minh. Trong nhiu trng hp, h&igrave;nh nh khu ca nam ng&ocirc;i ch n&agrave;y c xem l&agrave; biu tng ca th&agrave;nh ph.</p>', '<h2><span id="Ngu.E1.BB.93n_g.E1.BB.91c_v.C3.A0_xu.E1.BA.A5t_x.E1.BB.A9_t.C3.AAn_g.E1.BB.8Di" class="mw-headline">Ngun gc v&agrave; xut x t&ecirc;n gi</span></h2>\r\n<p style="text-align: justify;">Nguy&ecirc;n thy, ch Bn Th&agrave;nh &atilde; c&oacute; t trc khi ngi Ph&aacute;p x&acirc;m chim Gia nh. Ban u, v tr&iacute; ca ch nm b&ecirc;n b s&ocirc;ng Bn Ngh&eacute;, cnh mt bn s&ocirc;ng gn th&agrave;nh Gia nh (by gi l&agrave; th&agrave;nh Quy, c&ograve;n gi l&agrave; th&agrave;nh B&aacute;t Qu&aacute;i). Bn n&agrave;y d&ugrave;ng  cho h&agrave;nh kh&aacute;ch v&atilde;ng lai v&agrave; qu&acirc;n nh&acirc;n v&agrave;o th&agrave;nh, v&igrave; vy mi c&oacute; t&ecirc;n gi l&agrave; Bn Th&agrave;nh, v&agrave; khu ch cng c&oacute; t&ecirc;n gi l&agrave; ch Bn Th&agrave;nh.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce02254cc8_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;">Cng ch&iacute;nh v&agrave;o dp Tt 2003</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Ch.E1.BB.A3_M.E1.BB.9Bi" class="mw-headline">Ch Mi</span></h2>\r\n<p style="text-align: justify;">Khu vc x&acirc;y ch, vn l&agrave; mt c&aacute;i ao s&igrave;nh ly c, gi l&agrave; ao B Rt (Marais Boresse), c ngi Ph&aacute;p cho lp i. Khu&ocirc;n vi&ecirc;n ch quy hoch bn mt bi bn con ng. Mt tin l&agrave; Place Cuniac, t&ecirc;n t theo vi&ecirc;n X&atilde; T&acirc;y (y vi&ecirc;n Hi ng) Cuniac, ngi &atilde;  ra c&ocirc;ng vic lp ao. Ngi Vit th&igrave; quen gi &oacute; l&agrave; B&ugrave;ng binh Ch Bn Th&agrave;nh cho d&ugrave; t&ecirc;n ch&iacute;nh thc &atilde; tng i l&agrave; "C&ocirc;ng trng Cng H&ograve;a", "C&ocirc;ng trng Di&ecirc;n Hng", ri "Qung trng Qu&aacute;ch Th Trang". Mt bc ch l&agrave; Rue d''Espagne, ph&iacute;a &ocirc;ng l&agrave; rue Vi&eacute;not, v&agrave; ph&iacute;a t&acirc;y l&agrave; rue Schroeder. Nm 1955 thi  nht Cng h&ograve;a Vit Nam, bn con ng n&agrave;y i t&ecirc;n th&agrave;nh ng L&ecirc; Th&aacute;nh T&ocirc;n, ng Phan Bi Ch&acirc;u v&agrave; ng Phan Ch&acirc;u Trinh.<br /><br />Ng&ocirc;i ch mi do h&atilde;ng thu Brossard et Maupin khi c&ocirc;ng x&acirc;y dng t nm 1912 n cui th&aacute;ng 3 nm 1914 th&igrave; ho&agrave;n tt. L n mng ch kh&aacute;nh th&agrave;nh c b&aacute;o ch&iacute; thi &oacute; gi l&agrave; "T&acirc;n Vng Hi", do c din ra trong ba ng&agrave;y 28, 29 v&agrave; 30 th&aacute;ng 3 nm 1914, vi ph&aacute;o b&ocirc;ng, xe hoa v&agrave; hn 100.000 ngi tham d, k c t c&aacute;c tnh  v.<br /><br />Khu ch mi n&agrave;y vn mang t&ecirc;n gi Bn Th&agrave;nh; tuy nhi&ecirc;n cho n trc nm 1975 t&ecirc;n gi ch Bn Th&agrave;nh n&agrave;y thng ch hin din trong s&aacute;ch v, c&ograve;n ngi d&acirc;n th&igrave; thng gi l&agrave; ch S&agrave;i G&ograve;n hay ch Mi,  ph&acirc;n bit ch C ti im c, vn ch c&ograve;n li gian h&agrave;ng tht. Phn c&ograve;n li b ph&aacute; i v&agrave; c ngi Ph&aacute;p x&acirc;y dng th&agrave;nh c quan Ng&acirc;n kh. Hai con ng b&ecirc;n h&ocirc;ng ch m&atilde;i n nm 1940 c&ograve;n l&agrave; bn xe &ograve; min &ocirc;ng v&agrave; min T&acirc;y. V sau, bn xe n&agrave;y mi c di i ch kh&aacute;c.<br /><br />Ch Bn Th&agrave;nh hot ng li&ecirc;n tc trong 70 nm. T ng&agrave;y 1 th&aacute;ng 7 n 15 th&aacute;ng 8 nm 1985, ch Bn Th&agrave;nh c ci to v&agrave; sa cha ln.<br /><br />&nbsp;&nbsp;&nbsp; * a ch: Ca Nam (nm gia c&aacute;c ng Phan Bi Ch&acirc;u - Phan Chu Trinh - L&ecirc; Th&aacute;nh T&ocirc;n - C&ocirc;ng trng Qu&aacute;ch Th Trang) - Phng Bn Th&agrave;nh - Qun 1<br />&nbsp;&nbsp;&nbsp; * in thoi: +84.8.8292096<br />&nbsp;&nbsp;&nbsp; * Din t&iacute;ch: 13.056 m&sup2;<br />&nbsp;&nbsp;&nbsp; * Ng&agrave;nh h&agrave;ng kinh doanh ch yu: Qun &aacute;o, vi si, giy d&eacute;p, thi trang, h&agrave;ng th c&ocirc;ng m ngh, thc phm ti sng, tr&aacute;i c&acirc;y, hoa ti.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce021481af_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Ch Bn Th&agrave;nh<br /></em></p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdffa0e6ad_square.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdffa0e6ad_thumbnail.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdffa0e6ad_small.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdffa0e6ad_crop.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdffa0e6ad_medium.jpg', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7cdffa0e6ad_large.jpg', 'active', 0, '18:01:51', 1, 'admin', '18:24:41', 1, 'admin', 1, 'admin', '18:02:11', 0, 0, 0, 0, NULL, 0, 'vi_VN');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (40, 24, 'Tt Trung thu', '', 'tet-trung-thu', '<p>Tt Trung Thu theo &acirc;m lch l&agrave; ng&agrave;y rm th&aacute;ng 8 hng nm. &acirc;y l&agrave; ng&agrave;y tt ca tr em, c&ograve;n c gi l&agrave; "Tt tr&ocirc;ng Trng" hay Tt Thiu nhi, Tt Nhi ng.</p>', '<p>Tr em rt mong i c &oacute;n tt n&agrave;y v&igrave; thng c ngi ln tng  chi, thng l&agrave; &egrave;n &ocirc;ng sao, mt n, &egrave;n k&eacute;o qu&acirc;n, s&uacute;ng phun nc... ri b&aacute;nh nng, b&aacute;nh do. V&agrave;o ng&agrave;y tt n&agrave;y, ngi ta t chc b&agrave;y c, tr&ocirc;ng trng. Thi im trng l&ecirc;n cao, tr em s va m&uacute;a h&aacute;t va ngm trng ph&aacute; c.  mt s ni ngi ta c&ograve;n t chc m&uacute;a l&acirc;n, m&uacute;a s t, m&uacute;a rng  c&aacute;c em vui chi tho th&iacute;ch.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce110b10d9_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>B&aacute;nh nng ng&agrave;y tt Trung thu</em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p>Tt Trung Thu c&oacute; ngun gc vn minh n&ocirc;ng nghip ca d&acirc;n tc Vit. Thi im n&agrave;y kh&iacute; tri m&aacute;t m, m&ugrave;a m&agrave;ng ang ch thu hoch. N&ocirc;ng d&acirc;n ngh ngi v&agrave; vui chi sau mt v m&ugrave;a. Sau n&agrave;y ngi Trung Hoa tip nhn t vn h&oacute;a Vit v&agrave; tip &oacute; ph bin th&agrave;nh ng&agrave;y l truyn thng ca d&acirc;n tc &ocirc;ng &Aacute; nh H&agrave;n Quc, Nht Bn.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce10ebf455_crop.JPG" alt="" /></p>\r\n<p style="text-align: center;"><em>C&aacute;c loi &egrave;n lng</em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p>Theo phong tc ngi Vit, v&agrave;o dp Tt Trung Thu, cha m b&agrave;y c cho c&aacute;c con  mng trung thu, mua v&agrave; l&agrave;m  th lng &egrave;n thp bng nn  treo trong nh&agrave; v&agrave;  c&aacute;c con rc &egrave;n. C mng trung thu gm b&aacute;nh Trung Thu, ko, m&iacute;a, bi v&agrave; c&aacute;c th hoa qu kh&aacute;c na. &acirc;y l&agrave; dp  cha m t&ugrave;y theo kh nng kinh t gia &igrave;nh th hin t&igrave;nh thng y&ecirc;u con c&aacute;i mt c&aacute;ch c th. V&igrave; th, t&igrave;nh y&ecirc;u gia &igrave;nh li c&agrave;ng khng kh&iacute;t th&ecirc;m. Cng trong dp n&agrave;y ngi ta mua b&aacute;nh trung thu, tr&agrave;, ru  c&uacute;ng t ti&ecirc;n, biu &ocirc;ng b&agrave;, cha m, thy c&ocirc;, bn b&egrave;, h h&agrave;ng v&agrave; c&aacute;c &acirc;n nh&acirc;n kh&aacute;c.</p>', 'Wikipedia', '', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce10ebf455_square.JPG', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce10ebf455_thumbnail.JPG', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce10ebf455_small.JPG', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce10ebf455_crop.JPG', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce10ebf455_medium.JPG', 'http://localhost/tomatocms/upload/news/admin/2010/08/4c7ce10ebf455_large.JPG', 'active', 1, '18:03:29', 1, 'admin', '18:23:35', 1, 'admin', 1, 'admin', '18:03:40', 0, 0, 0, 0, NULL, 0, 'vi_VN');


