INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (10, 4, 14, 'H Hon Kim', '', 'ho-hoan-kiem', '<p style="text-align: justify;"><strong>H Ho&agrave;n Kim</strong> hay h Gm l&agrave; mt h nc ngt nm gia th &ocirc; H&agrave; Ni. T&ecirc;n h cng c t cho mt qun ca H&agrave; Ni, qun Ho&agrave;n Kim.</p>', '<h2>Lch s</h2>\r\n<p style="text-align: justify;">C&aacute;ch &acirc;y khong 6 th k, h Gm gm hai phn chy d&agrave;i t ph H&agrave;ng &agrave;o, qua Hai B&agrave; Trng, L&yacute; Thng Kit ti ph H&agrave;ng Chui, th&ocirc;ng vi s&ocirc;ng Hng. Nc h quanh nm xanh bic n&ecirc;n h Gm cng c gi l&agrave; h Lc Thu.<br /><br />Tng truyn v&agrave;o th k 15 h c i t&ecirc;n th&agrave;nh h Ho&agrave;n Kim, gn lin vi truyn thuyt tr gm thn cho R&ugrave;a V&agrave;ng, ghi li thng li ca cuc chin u 10 nm ca nh&acirc;n d&acirc;n Vit Nam chng li qu&acirc;n Minh (1417-1427) di s l&atilde;nh o ca L&ecirc; Li. Truyn thuyt k rng khi L&ecirc; Li khi ngha  Lam Sn (Thanh Ho&aacute;) c&oacute; m&ograve; c mt li gm, sau &oacute; li nht c mt c&aacute;i chu&ocirc;i  rung c&agrave;y, gh&eacute;p li th&agrave;nh thanh gm, t t&ecirc;n l&agrave; Thun Thi&ecirc;n. Gm b&aacute;u n&agrave;y &atilde; theo L&ecirc; Li trong sut thi gian kh&aacute;ng chin chng gic Minh. Khi l&ecirc;n ng&ocirc;i v &oacute;ng &ocirc;  Thng Long, trong mt ln nh&agrave; vua i chi thuyn tr&ecirc;n h Lc Thu, bng mt con r&ugrave;a xut hin. L&ecirc; Th&aacute;i T r&uacute;t gm khi v, gi gm ra th&igrave; gm bay v ph&iacute;a con r&ugrave;a. R&ugrave;a ngm gm, ln xung &aacute;y h, v&agrave; t &oacute; h Lc Thu c&oacute; t&ecirc;n gi mi l&agrave; h Ho&agrave;n Kim (tr gm) hay h Gm.</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7c880db619c_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;">H Ho&agrave;n Kim vi Th&aacute;p R&ugrave;a</p>\r\n<h2>Quang cnh</h2>\r\n<p style="text-align: justify;">H Ho&agrave;n Kim c du kh&aacute;ch cho l&agrave; mt thng cnh ca H&agrave; Ni. Quanh h trng nhiu loi hoa v&agrave; c&acirc;y cnh. Gia h c&oacute; th&aacute;p R&ugrave;a, cnh h c&oacute; n Ngc Sn. Xung quanh h c&ograve;n c&oacute; nhng di t&iacute;ch lch s kh&aacute;c nh tng vua L&ecirc; Th&aacute;i T, cu Th&ecirc; H&uacute;c, th&aacute;p B&uacute;t, n B&agrave; Kiu,... b&ecirc;n cnh nhng c&ocirc;ng tr&igrave;nh kin tr&uacute;c hin i. To&agrave; nh&agrave; Bu in vi th&aacute;p ng h c k&iacute;nh in b&oacute;ng h Gm &atilde; i v&agrave;o l&ograve;ng nhiu ngi d&acirc;n H&agrave; Ni.<br /><br />Ngi d&acirc;n H&agrave; Ni sng  khu vc quanh h c&oacute; th&oacute;i quen ra &acirc;y tp th dc v&agrave;o s&aacute;ng sm, c bit l&agrave; v&agrave;o m&ugrave;a h&egrave;. H gi c&aacute;c khu ph nm quanh h l&agrave; B H.<br /><br />Th&aacute;p B&uacute;t (hay l&agrave; b&uacute;t th&aacute;p) nm  cnh h, i vi &agrave;i nghi&ecirc;n nm  b h. Mi ng&agrave;y, b&oacute;ng ca Th&aacute;p b&uacute;t ng xung chm mc trong &agrave;i nghi&ecirc;n, to th&agrave;nh mt biu tng rt p cho hc vn: "Th&aacute;p B&uacute;t - &agrave;i nghi&ecirc;n -  th l&ecirc;n tri xanh"<br /><br />Chu vi s&aacute;t b h l&agrave; khong 1750m, c&ograve;n chu vi ng xe c gii i v&ograve;ng quanh h l&agrave; khong 2000m.</p>\r\n<h2>R&ugrave;a</h2>\r\n<p style="text-align: justify;">Ti&ecirc;u bn mt con r&ugrave;a H Gm lu gi trong n Ngc Sn<br /><br />L&agrave; lo&agrave;i r&ugrave;a khng l hin sinh sng trong H Gm. Ng&agrave;y trc r&ugrave;a sng trong l&ograve;ng H Gm rt him khi ni l&ecirc;n mt nc, truyn rng mi ln r&ugrave;a ni u li&ecirc;n quan n vic quc gia i s. Nhng thi gian gn &acirc;y r&ugrave;a ni l&ecirc;n nhiu hn, c&oacute; l v&igrave; nc h &ocirc; nhim n&ecirc;n r&ugrave;a phi thng xuy&ecirc;n ni l&ecirc;n  th. Trong n Ngc Sn c&oacute; trng b&agrave;y ti&ecirc;u bn mt c&aacute; th r&ugrave;a H Gm. H&igrave;nh nh ca r&ugrave;a cng gn lin vi h, th&ocirc;ng qua t&ecirc;n gi th&aacute;p R&ugrave;a  gia h v&agrave; truyn thuyt L&ecirc; Li tr gm cho thn Kim Quy, mt truyn thuyt mang li t&ecirc;n gi cho bn th&acirc;n h. C&aacute;c nh&agrave; khoa hc vn ang tip tc nghi&ecirc;n cu  t&igrave;m ra c ch&iacute;nh x&aacute;c ph&acirc;n loi ca r&ugrave;a H Gm.</p>', 'Wikipedia', '', '11:58:11', 1, 'admin');
