INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (95, 29, 21, 'L hi c  Hoa L', '', 'le-hoi-co-do-hoa-lu', '<p style="text-align: justify;">L hi c &ocirc; l&agrave; mt l hi truyn thng c m  suy t&ocirc;n c&ocirc;ng lao c&aacute;c anh h&ugrave;ng d&acirc;n tc &atilde; x&acirc;y dng kinh &ocirc; Hoa L, lp ra nh&agrave; nc i C Vit th k X m&agrave; ti&ecirc;u biu l&agrave; hai v vua inh Ti&ecirc;n Ho&agrave;ng v&agrave; L&ecirc; i H&agrave;nh.</p>', '<p style="text-align: justify;">L hi din ra ti qung trng trung t&acirc;m khu di t&iacute;ch c &ocirc; Hoa L v&agrave; c&aacute;c di t&iacute;ch. &acirc;y l&agrave; mt l hi c truyn hng v ci ngun dng nc v&agrave; gi nc ca d&acirc;n tc Vit Nam. L hi c&oacute; lch s t khi kinh &ocirc; Hoa L tr th&agrave;nh c &ocirc;. L hi c&ograve;n c&oacute; t&ecirc;n l&agrave; l hi c lau v&igrave; c&oacute; m&agrave;n din t&aacute;i hin cnh inh B Lnh vi tr&ograve; chi "C lau tp trn". L hi truyn thng c &ocirc; Hoa L c&oacute; quy m&ocirc; ln nht v&agrave;o nhng nm c&oacute; s h&agrave;ng n v l&agrave; 3, 5, 8, 0. L hi c m v&agrave;o m&ugrave;a xu&acirc;n, c&oacute; th v&agrave;o ng&agrave;y sinh ca inh B Lnh (15/2 &acirc;m lch), hoc u th&aacute;ng 3 bt u t 6/3 n 10/3 &acirc;m lch (Tng truyn, ng&agrave;y 10/3 l&agrave; ng&agrave;y inh B Lnh l&ecirc;n ng&ocirc;i Ho&agrave;ng , ng&agrave;y 8/3 l&agrave; ng&agrave;y mt ca vua L&ecirc; i H&agrave;nh).[28] Nhc n l hi Trng Y&ecirc;n d&acirc;n gian c&oacute; c&acirc;u: [29] "Ai l&agrave; con ch&aacute;u rng ti&ecirc;n - Th&aacute;ng hai m hi Trng Y&ecirc;n th&igrave; v".</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ccee48a2a8_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Rng v&agrave;ng trong l hi C Lau</em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: justify;">L rc nc: M u l&agrave; l rc nc, o&agrave;n ngi khi h&agrave;nh t n vua inh t 5-6 gi s&aacute;ng, n b&ecirc;n s&ocirc;ng Ho&agrave;ng Long th&igrave; dng li ly nc v&agrave;o ch&eacute; em v n. Cuc rc c chun b kh&aacute; c&ocirc;ng phu. Trc ng&agrave;y khai hi,  s&ocirc;ng Ho&agrave;ng Long chn mt c&acirc;y tre ln. Tr&ecirc;n ngn tre c&oacute; treo mt di phn m&agrave;u v&agrave;ng, ghi li ch&uacute;. Ni dung nhng li ch&uacute; i lc l&agrave; Thn d&acirc;n, con ch&aacute;u trm h lu&ocirc;n nh n rng v&agrave;ng  s&ocirc;ng n&agrave;y &atilde; cu gi&uacute;p v Ho&agrave;ng  nh&agrave; inh; Cu mong thn s&ocirc;ng gi cho d&ograve;ng nc m&aacute;t hin ho&agrave;, ph&ugrave; tr cho d&acirc;n tr&aacute;nh mi iu &aacute;c d&hellip;[30][31] o&agrave;n rc i theo th t dn u l&agrave; nhng ngi mang c ng sc c&aacute;c loi, i h&agrave;ng &ocirc;i. K tip l&agrave; phng nhc b&aacute;t &acirc;m, phng trng. Tip n l&agrave; mt kiu b&aacute;t cng ln c&oacute; hng &aacute;n do t&aacute;m nam thanh ni&ecirc;n trong trang phc l&iacute;nh tr&aacute;ng nh&agrave; inh xa kho mnh khi&ecirc;ng, tip theo l&agrave; c&aacute;c v quan kh&aacute;ch, i biu trung ng, a phng. Ri tip n l&agrave; nhng kiu b&aacute;t cng c&oacute; t&aacute;n do c&aacute;c thiu n mang l vt. o&agrave;n ngi i sau l&agrave; c&aacute;c b&ocirc; l&atilde;o, nhng i t n quan ca nhiu a phng xa gn, nh&acirc;n d&acirc;n v&agrave; du kh&aacute;ch.<br /><br />L t: Ngay sau khi o&agrave;n rc nc tr v s&acirc;n khu trung t&acirc;m l hi, sau nghi thc th rng bay th&igrave; khi im phn l t ti 2 n Vua inh, Vua L&ecirc;. Phn t c&oacute; s tham gia ca nhiu o&agrave;n theo lch ng k&yacute;. C&aacute;c o&agrave;n rc kiu v&agrave; ch&acirc;n nhang t c&aacute;c di t&iacute;ch th c&aacute;c danh nh&acirc;n thi inh - L&ecirc; s tham gia rc kiu v 2 n, hu ht c&aacute;c o&agrave;n  c ly xa phi rc tr&ecirc;n xe l hi tin v Hoa L. L t c tin h&agrave;nh sau &oacute; c ban ng&agrave;y v&agrave; ban &ecirc;m  c n vua inh v&agrave; vua L&ecirc; vi ni dung ca ngi c&ocirc;ng c ca hai v vua, sen k &oacute; du kh&aacute;ch v&agrave;o thp hng tng nim. Tham gia l t c&oacute; nhiu o&agrave;n trong v&ugrave;ng.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ccee363d4b_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>L t c truyn ti n Vua inh Ti&ecirc;n Ho&agrave;ng</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">M&agrave;n s&acirc;n khu h&oacute;a: &acirc;y l&agrave; m&agrave;n din s&acirc;n khu ng i  khai mc l hi v&agrave; truyn h&igrave;nh trc tip. Sau li din vn khai mc l&agrave; m&agrave;n trng hi Hoa L, c&aacute;c m&agrave;n din t&aacute;i hin lch s trng i din ra ti kinh &ocirc; Hoa L xa nh: s kin l&ecirc;n ng&ocirc;i ho&agrave;ng  ca inh B Lnh v&agrave; L&ecirc; Ho&agrave;n; &aacute;nh thng gic Tng; di &ocirc; v Thng Long v&agrave; kt th&uacute;c l&agrave; m&agrave;n th rng bay l&ecirc;n cho ti ht bui s&aacute;ng khai mc.</p>', 'Wikipedia', '', '17:00:45', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (43, 15, 1, 'Halong Bay', '', 'halong-bay', '<p>Ha Long Bay (literally: "Descending Dragon Bay"; Vietnamese: Vnh H Long) is a UNESCO World Heritage Site, and a popular travel destination, located in Qung Ninh province, Vietnam.</p>', '<p style="text-align: justify;">Administratively, the bay belongs to Ha Long City, Cm Ph town, and part of Van Don district. The bay features thousands of limestone karsts and isles in various sizes and shapes. Ha Long Bay is a center of a larger zone which includes B&aacute;i T Long bay to the northeast, and C&aacute;t B&agrave; islands to the southwest. These larger zones share similar geological, geographical, geomorphological, climate, and cultural characters.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cb60b67f10_crop.JPG" alt="" /></p>\r\n<p style="text-align: center;"><em>The Kissing Rocks</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">Ha Long Bay has an area of around 1,553km2, including 1,960 islets, most of which are limestone. The core of the bay has an area of 334km2 with a high density of 775 islets. The limestone in this bay has gone through 500 million years of formation in different conditions and environments. The evolution of the karst in this bay has taken 20 million years under the impact of the tropical wet climate. The geo-diversity of the environment in the area has created biodiversity, including a tropical evergreen biosystem, oceanic and sea shore biosystem. Ha Long Bay is home to 14 endemic floral species and 60 endemic faunal species.<br /><br />Historical research surveys have shown the presence of prehistorical human beings in this area tens of thousands years ago. The successive ancient cultures are the Soi Nh culture around 18,000-7,000 BC, the C&aacute;i B&egrave;o culture 7,000-5,000 BC and the H Long culture 3,500-5,000 years ago. Ha Long Bay also marked important events in the history of Vietnam with many artifacts found in B&agrave;i Th Mout, u G Cave, B&atilde;i Ch&aacute;y.</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cb60a1bf5b_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;">Thien Cung grotto</p>\r\n<p style="text-align: justify;"><br /><br />500 years ago, Nguyen Trai praised the beauty of Ha Long Bay in his verse L nhp V&acirc;n n, in which he called it "rock wonder in the sky".In 1962, the Ministry of Culture, Sports and Tourism of Vietnam listed Ha Long Bay in the National Relics and Landscapes publication.In 1994, the core zone of Ha Long Bay was listed by UNESCO as a World Heritage Site according to criteria vii, and listed for a second time according to criteria viii.</p>', 'Wikipedia', '', '15:00:37', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (44, 16, 0, 'Vnh H Long', '', 'vinh-ha-long', '<p>Vnh H Long (vnh nc ni rng &aacute;p xung) l&agrave; mt vnh nh thuc phn b T&acirc;y vnh Bc B ti khu vc bin &ocirc;ng Bc Vit Nam, bao gm v&ugrave;ng bin o thuc th&agrave;nh ph H Long, th x&atilde; Cm Ph v&agrave; mt phn ca huyn o V&acirc;n n.</p>', '<p style="text-align: justify;">L&agrave; trung t&acirc;m ca mt khu vc rng ln c&oacute; nhng yu t &iacute;t nhiu tng ng v a cht, a mo, cnh quan, kh&iacute; hu v&agrave; vn h&oacute;a, vi vnh B&aacute;i T Long ph&iacute;a &ocirc;ng Bc v&agrave; qun o C&aacute;t B&agrave; ph&iacute;a T&acirc;y Nam, vnh H Long gii hn trong din t&iacute;ch khong 1.553km&sup2; bao gm 1.960 h&ograve;n o ln nh, phn ln l&agrave; o &aacute; v&ocirc;i, trong &oacute; v&ugrave;ng l&otilde;i ca Vnh c&oacute; din t&iacute;ch 334km&sup2; qun t d&agrave;y c 775 h&ograve;n o[. Lch s kin to a cht &aacute; v&ocirc;i ca Vnh &atilde; tri qua khong 500 triu nm vi nhng ho&agrave;n cnh c a l&yacute; rt kh&aacute;c nhau; v&agrave; qu&aacute; tr&igrave;nh tin h&oacute;a carxt y  tri qua tr&ecirc;n 20 triu nm vi s kt hp c&aacute;c yu t nh tng &aacute; v&ocirc;i d&agrave;y, kh&iacute; hu n&oacute;ng m v&agrave; tin tr&igrave;nh n&acirc;ng kin to chm chp tr&ecirc;n tng th S kt hp ca m&ocirc;i trng, kh&iacute; hu, a cht, a mo, &atilde; khin vnh H Long tr th&agrave;nh qun t ca a dng sinh hc bao gm h sinh th&aacute;i rng k&iacute;n thng xanh ma m nhit i v&agrave; h sinh th&aacute;i bin v&agrave; ven b vi nhiu tiu h sinh th&aacute;i. 14 lo&agrave;i thc vt c hu v&agrave; khong 60 lo&agrave;i ng vt c hu &atilde; c ph&aacute;t hin trong s h&agrave;ng ng&agrave;n ng, thc vt qun c ti Vnh.</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cb60b67f10_crop.JPG" alt="" /></p>\r\n<p style="text-align: center;"><em>H&ograve;n Trng M&aacute;i</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">Nhng kt qu nghi&ecirc;n cu, th&aacute;m s&aacute;t kho c hc v&agrave; vn h&oacute;a hc cho thy s hin din ca nhng c d&acirc;n tin s tr&ecirc;n v&ugrave;ng vnh H Long t kh&aacute; sm, &atilde; to lp nhng h&igrave;nh th&aacute;i vn h&oacute;a c i tip ni nhau bao gm vn h&oacute;a Soi Nh trong khong 18.000-7.000 nm trc C&ocirc;ng Nguy&ecirc;n, vn h&oacute;a C&aacute;i B&egrave;o trong 7.000-5.000 nm trc C&ocirc;ng Nguy&ecirc;n v&agrave; vn h&oacute;a H Long c&aacute;ch ng&agrave;y nay khong t 3.500-5.000 nm. Tin tr&igrave;nh dng nc v&agrave; truyn thng gi nc ca d&acirc;n tc Vit Nam, trong sut h&agrave;nh tr&igrave;nh lch s, cng khng nh v tr&iacute; tin ti&ecirc;u v&agrave; v th vn h&oacute;a ca vnh H Long qua nhng a danh m&agrave; t&ecirc;n gi gn vi in t&iacute;ch c&ograve;n lu truyn n nay, nh n&uacute;i B&agrave;i Th, hang u G, B&atilde;i Ch&aacute;yv.v. Hin nay, vnh H Long l&agrave; mt khu vc ph&aacute;t trin nng ng nh nhng iu kin v&agrave; li th sn c&oacute; nh c&oacute; mt tim nng ln v du lch, nghi&ecirc;n cu khoa hc, nu&ocirc;i trng, &aacute;nh bt thy sn, giao th&ocirc;ng thy i vi khu vc v&ugrave;ng bin &ocirc;ng Bc Vit Nam n&oacute;i ri&ecirc;ng v&agrave; min Bc Vit Nam n&oacute;i chung.</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cb60a1bf5b_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>ng Thien Cung </em></p>\r\n<p style="text-align: justify;"><br /><br />T hn 500 nm v trc trong b&agrave;i th L nhp V&acirc;n n, Nguyn Tr&atilde;i &atilde; ln u ti&ecirc;n ca ngi vnh H Long l&agrave; "k quan &aacute; dng gia tri cao". Nm 1962 B Vn h&oacute;a-Th&ocirc;ng tin Vit Nam &atilde; xp hng vnh H Long l&agrave; di t&iacute;ch danh thng cp quc gia ng thi quy hoch v&ugrave;ng bo v. Nm 1994 v&ugrave;ng l&otilde;i ca vnh H Long c UNESCO c&ocirc;ng nhn l&agrave; Di sn Thi&ecirc;n nhi&ecirc;n Th gii vi gi&aacute; tr thm m (ti&ecirc;u chun vii), v&agrave; c t&aacute;i c&ocirc;ng nhn ln th 2 vi gi&aacute; tr ngoi hng to&agrave;n cu v a cht-a mo (ti&ecirc;u chun viii) v&agrave;o nm 2000. C&ugrave;ng vi vnh Nha Trang ca Vit Nam, vnh H Long l&agrave; mt trong s 29 vnh c C&acirc;u lc b nhng vnh p nht th gii xp hng v&agrave; ch&iacute;nh thc c&ocirc;ng nhn v&agrave;o th&aacute;ng 7 nm 2003.<br /><br />Vnh H Long c&ugrave;ng vi o C&aacute;t B&agrave; to th&agrave;nh 1 trong 21 khu du lch quc gia Vit Nam</p>', 'Wikipedia', '', '15:06:36', 1, 'admin');
