INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (50, 20, 21, 'Tt m Lch', '', 'tet-am-lich', '<p>Tt Nguy&ecirc;n &aacute;n (hay c&ograve;n gi l&agrave; Tt C, Tt Ta, Tt &Acirc;m lch, Tt C truyn, nm mi hay ch n gin Tt) l&agrave; dp l quan trng nht trong vn h&oacute;a ca ngi Vit Nam.</p>', '<p>Ngi Vit tin rng v&agrave;o ng&agrave;y Tt mi th u phi mi, phi i kh&aacute;c, t ngoi vt cho n l&ograve;ng ngi, v&igrave; vy khong mi ng&agrave;y trc Tt h thng sn, qu&eacute;t v&ocirc;i nh&agrave; ca li. H cng tt bt i sm sa qun &aacute;o mi  mc trong dp n&agrave;y.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cbc5502b05_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>C&aacute;c &ocirc;ng  vit ch l&ecirc;n giy d&oacute;</em></p>\r\n<p>&nbsp;</p>\r\n<p>Trong nhng ng&agrave;y Tt h ki&ecirc;ng c kh&ocirc;ng n&oacute;ng gin, c&atilde;i c. Tt l&agrave; dp  mi ngi h&agrave;n gn nhng him kh&iacute;ch &atilde; qua v&agrave; l&agrave; dp  chuc li. Mi ngi i thm ving nhau v&agrave; ch&uacute;c nhau nhng li y &yacute; ngha. Tr em sau khi ch&uacute;c Tt ngi ln c&ograve;n c l&igrave; x&igrave; bng mt phong b&igrave;  thm c&oacute; ng &iacute;t tin d&agrave;nh cho ch&uacute;ng ti&ecirc;u x&agrave;i ng&agrave;y Tt. Tt  3 min Bc, Trung, Nam  Vit Nam cng c&oacute; nhng iu kh&aacute;c nhau.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cbc5604543_medium.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Hoa &agrave;o Nht T&acirc;n</em></p>\r\n<p>&nbsp;</p>\r\n<p>Ngo&agrave;i hai loi hoa c trng cho Tt l&agrave; &agrave;o v&agrave; mai, hu nh nh&agrave; n&agrave;o cng c&oacute; th&ecirc;m nhng loi hoa  th c&uacute;ng v&agrave; hoa trang tr&iacute;. Hoa th c&uacute;ng c&oacute; th nh hoa vn th, c&uacute;c, lay n, hoa hu...; hoa  trang tr&iacute; th&igrave; mu&ocirc;n m&agrave;u sc nh hoa hng, hoa thy ti&ecirc;n, hoa lan, hoa thc dc, hoa violet, hoa ng tin,... Ngo&agrave;i ra, hoa hng, cm chng, loa k&egrave;n, hu t&acirc;y, l&aacute; mng, thch tho... cm k&egrave;m s to s phong ph&uacute; v&agrave; mang &yacute; ngha sum hp cho b&igrave;nh hoa ng&agrave;y tt. M&agrave;u sc ti vui ch o ca b&igrave;nh hoa cng ng &yacute; cu mong mt nm mi l&agrave;m n ph&aacute;t t, gia &igrave;nh an khang v&agrave; sung t&uacute;c.</p>', 'Wikipedia', '', '15:26:31', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (51, 21, 1, 'Da Lat', '', 'da-lat', '<p>Da Lat, or Dalat, (pop. 197,000 as of 2007[update], spelled &agrave; Lt in Vietnamese) About this sound pronunciation (help&middot;info) is the capital of L&acirc;m ng Province in Vietnam.</p>', '<p style="text-align: justify;">The city is located 1,500 m (4,900 ft) above sea level on the Langbiang Plateau in the southern parts of the Central Highlands (in Vietnamese, T&acirc;y Nguy&ecirc;n). In Vietnam, &agrave; Lt is a popular tourist destination.<br /><br />&agrave; Lt''s specific sights are pine wood (forming the name: "City of thousands of pine trees") with twisting roads and tree marigold (Vietnamese: d&atilde; qu) blossom in the winter. The city''s temperate weather stands in contrast to Vietnam''s otherwise tropical climate. Mist covering the valleys almost year-round leads to its name "City of eternal spring."<br /><br />&agrave; Lt is also known as an area for science research in the fields of biotechnology and nuclear physics.<br /><br />With its year-round cool weather, &agrave; Lt supplies temperate agriculture products for all over Vietnam, for example, cabbage and cauliflower. Its flower industry produces two typical flowers: hydrangea (Vietnamese: cm t&uacute; cu) and golden everlasting (Vietnamese: hoa bt t). The confectionery industry offers a wide range of mt, a kind of fruit preserve made from strawberry, mulberry, sweet potato, and rose.</p>', '', '', '15:30:10', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (52, 21, 1, 'Da Lat', '', 'da-lat', '<p>Da Lat, or Dalat, (pop. 197,000 as of 2007[update], spelled &agrave; Lt in Vietnamese) About this sound pronunciation (help&middot;info) is the capital of L&acirc;m ng Province in Vietnam.</p>', '<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cbde84daa7_crop.JPG" alt="" /></p>\r\n<p style="text-align: center;"><em>Tuyn L&acirc;m Lake in the morning fog&nbsp; </em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">The city is located 1,500 m (4,900 ft) above sea level on the Langbiang Plateau in the southern parts of the Central Highlands (in Vietnamese, T&acirc;y Nguy&ecirc;n). In Vietnam, &agrave; Lt is a popular tourist destination.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cbde95d244_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em><small>Outside Da Lat Market at night</small></em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">&agrave; Lt''s specific sights are pine wood (forming the name: "City of thousands of pine trees") with twisting roads and tree marigold (Vietnamese: d&atilde; qu) blossom in the winter. The city''s temperate weather stands in contrast to Vietnam''s otherwise tropical climate. Mist covering the valleys almost year-round leads to its name "City of eternal spring."</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cbde738539_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Da Lat''s Market square at night</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">&agrave; Lt is also known as an area for science research in the fields of biotechnology and nuclear physics.<br /><br />With its year-round cool weather, &agrave; Lt supplies temperate agriculture products for all over Vietnam, for example, cabbage and cauliflower. Its flower industry produces two typical flowers: hydrangea (Vietnamese: cm t&uacute; cu) and golden everlasting (Vietnamese: hoa bt t). The confectionery industry offers a wide range of mt, a kind of fruit preserve made from strawberry, mulberry, sweet potato, and rose.</p>', '', '', '15:33:11', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (53, 13, 2, 'Vietnamese cuisine', '', 'vietnamese-cuisine', '<p style="text-align: justify;">Vietnamese cuisine is a style of cooking derived from Vietnam with fish sauce, soy sauce, rice, fresh herbs, fruits and vegetables all commonly used. Vietnamese recipes utilize a diverse range of herbs, including lemongrass, mint, Vietnamese mint, long coriander and Thai basil leaves. Traditional Vietnamese cooking is greatly admired for freshness of the ingredients and for the healthy eating style.</p>', '<p style="text-align: justify;">The most common meats used in Vietnamese cuisine are beef, pork, chicken, fish, and various kinds of seafood. The Vietnamese also have a strong vegetarian tradition influenced by Buddhist values.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cb06864644_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Hu tieu kho, braised rice noodle dish</em></p>\r\n<h2 style="text-align: justify;"><span id="Regional_variations" class="mw-headline">Regional variations</span></h2>\r\n<p style="text-align: justify;">As a whole, Vietnamese cuisine contains strong influences mainly from Cantonese cooking with a bit of a French twist to it. This is because unlike many other Indochinese nations, which are strongly influences by the culture of India, Vietnam is the only country in which Chinese-like cultures remained strong, since that it was first ruled by China for at least a 1000 years, followed by control from France centuries later for a 100 years. However, depending on the region, it can be divided into three categories, each pertaining to a distinct geographical region. With Northern Vietnam being the cradle of Vietnamese civilization, many of Vietnam''s most notable dishes such as ph and b&aacute;nh cun can trace their origin to the North. Northern cuisine is more traditional and less diverse in choosing spices and ingredients.</p>\r\n<p style="text-align: justify;">The cuisine of South Vietnam has historically been influenced by the influx of southern Chinese immigrants, French colonists and other nationalities. Southerners prefer sweet flavors in many dishes.[citation needed] As a region of perhaps greater diversity in terms of external influences, the South''s cuisine uses a wider variety of herbs.</p>\r\n<p style="text-align: justify;">The cuisine of Central Vietnam is distinct from the cuisines of both the Northern and Southern regions in its use of many small side dishes. For a while the country was ruled from Hu in Central Vietnam, so that most of the dishes were made small and dedicated to the kings. Compared to its counterparts, its cuisine is more spicy.</p>\r\n<p style="text-align: justify;">One most common French influence is the use of baguettes in Vietnamese meals. Vietnamese sandwiches are traditionally made with baguettes, and sometimes soups such as canh chua are served with a baguette on the side.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2 style="text-align: justify;"><span id="Typical_Vietnamese_family_meals" class="mw-headline">Typical Vietnamese family meals</span></h2>\r\n<p style="text-align: justify;">A typical meal for the average Vietnamese family would include:<br /><br />- Individual bowls of rice<br />- Meat, fish or seafood (grilled, boiled, steamed, stewed or stir fried with vegetables)<br />- Stir-fried, raw, pickled or steamed vegetables<br />- Canh (a clear broth with vegetables and often meat or seafood) or other Vietnamese-style soup<br />- Prepared fish sauce and/or soy sauce for dipping, to which garlic, chili, ginger or lime juice are sometimes added according to taste.<br /><br />All dishes apart from the individual bowls of rice are communal and to be shared.</p>', 'Wikipedia', '', '15:33:23', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (96, 29, 21, 'L hi c  Hoa L', '', 'le-hoi-co-do-hoa-lu', '<p style="text-align: justify;">L hi c &ocirc; l&agrave; mt l hi truyn thng c m  suy t&ocirc;n c&ocirc;ng lao c&aacute;c anh h&ugrave;ng d&acirc;n tc &atilde; x&acirc;y dng kinh &ocirc; Hoa L, lp ra nh&agrave; nc i C Vit th k X m&agrave; ti&ecirc;u biu l&agrave; hai v vua inh Ti&ecirc;n Ho&agrave;ng v&agrave; L&ecirc; i H&agrave;nh.</p>', '<p style="text-align: justify;">L hi din ra ti qung trng trung t&acirc;m khu di t&iacute;ch c &ocirc; Hoa L v&agrave; c&aacute;c di t&iacute;ch. &acirc;y l&agrave; mt l hi c truyn hng v ci ngun dng nc v&agrave; gi nc ca d&acirc;n tc Vit Nam. L hi c&oacute; lch s t khi kinh &ocirc; Hoa L tr th&agrave;nh c &ocirc;. L hi c&ograve;n c&oacute; t&ecirc;n l&agrave; l hi c lau v&igrave; c&oacute; m&agrave;n din t&aacute;i hin cnh inh B Lnh vi tr&ograve; chi "C lau tp trn". L hi truyn thng c &ocirc; Hoa L c&oacute; quy m&ocirc; ln nht v&agrave;o nhng nm c&oacute; s h&agrave;ng n v l&agrave; 3, 5, 8, 0. L hi c m v&agrave;o m&ugrave;a xu&acirc;n, c&oacute; th v&agrave;o ng&agrave;y sinh ca inh B Lnh (15/2 &acirc;m lch), hoc u th&aacute;ng 3 bt u t 6/3 n 10/3 &acirc;m lch (Tng truyn, ng&agrave;y 10/3 l&agrave; ng&agrave;y inh B Lnh l&ecirc;n ng&ocirc;i Ho&agrave;ng , ng&agrave;y 8/3 l&agrave; ng&agrave;y mt ca vua L&ecirc; i H&agrave;nh).[28] Nhc n l hi Trng Y&ecirc;n d&acirc;n gian c&oacute; c&acirc;u: [29] "Ai l&agrave; con ch&aacute;u rng ti&ecirc;n - Th&aacute;ng hai m hi Trng Y&ecirc;n th&igrave; v".</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ccee48a2a8_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Rng v&agrave;ng trong l hi C Lau</em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: justify;">L rc nc: M u l&agrave; l rc nc, o&agrave;n ngi khi h&agrave;nh t n vua inh t 5-6 gi s&aacute;ng, n b&ecirc;n s&ocirc;ng Ho&agrave;ng Long th&igrave; dng li ly nc v&agrave;o ch&eacute; em v n. Cuc rc c chun b kh&aacute; c&ocirc;ng phu. Trc ng&agrave;y khai hi,  s&ocirc;ng Ho&agrave;ng Long chn mt c&acirc;y tre ln. Tr&ecirc;n ngn tre c&oacute; treo mt di phn m&agrave;u v&agrave;ng, ghi li ch&uacute;. Ni dung nhng li ch&uacute; i lc l&agrave; Thn d&acirc;n, con ch&aacute;u trm h lu&ocirc;n nh n rng v&agrave;ng  s&ocirc;ng n&agrave;y &atilde; cu gi&uacute;p v Ho&agrave;ng  nh&agrave; inh; Cu mong thn s&ocirc;ng gi cho d&ograve;ng nc m&aacute;t hin ho&agrave;, ph&ugrave; tr cho d&acirc;n tr&aacute;nh mi iu &aacute;c d&hellip;[30][31] o&agrave;n rc i theo th t dn u l&agrave; nhng ngi mang c ng sc c&aacute;c loi, i h&agrave;ng &ocirc;i. K tip l&agrave; phng nhc b&aacute;t &acirc;m, phng trng. Tip n l&agrave; mt kiu b&aacute;t cng ln c&oacute; hng &aacute;n do t&aacute;m nam thanh ni&ecirc;n trong trang phc l&iacute;nh tr&aacute;ng nh&agrave; inh xa kho mnh khi&ecirc;ng, tip theo l&agrave; c&aacute;c v quan kh&aacute;ch, i biu trung ng, a phng. Ri tip n l&agrave; nhng kiu b&aacute;t cng c&oacute; t&aacute;n do c&aacute;c thiu n mang l vt. o&agrave;n ngi i sau l&agrave; c&aacute;c b&ocirc; l&atilde;o, nhng i t n quan ca nhiu a phng xa gn, nh&acirc;n d&acirc;n v&agrave; du kh&aacute;ch.<br /><br />L t: Ngay sau khi o&agrave;n rc nc tr v s&acirc;n khu trung t&acirc;m l hi, sau nghi thc th rng bay th&igrave; khi im phn l t ti 2 n Vua inh, Vua L&ecirc;. Phn t c&oacute; s tham gia ca nhiu o&agrave;n theo lch ng k&yacute;. C&aacute;c o&agrave;n rc kiu v&agrave; ch&acirc;n nhang t c&aacute;c di t&iacute;ch th c&aacute;c danh nh&acirc;n thi inh - L&ecirc; s tham gia rc kiu v 2 n, hu ht c&aacute;c o&agrave;n  c ly xa phi rc tr&ecirc;n xe l hi tin v Hoa L. L t c tin h&agrave;nh sau &oacute; c ban ng&agrave;y v&agrave; ban &ecirc;m  c n vua inh v&agrave; vua L&ecirc; vi ni dung ca ngi c&ocirc;ng c ca hai v vua, sen k &oacute; du kh&aacute;ch v&agrave;o thp hng tng nim. Tham gia l t c&oacute; nhiu o&agrave;n trong v&ugrave;ng.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ccee363d4b_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>L t c truyn ti n Vua inh Ti&ecirc;n Ho&agrave;ng</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">M&agrave;n s&acirc;n khu h&oacute;a: &acirc;y l&agrave; m&agrave;n din s&acirc;n khu ng i  khai mc l hi v&agrave; truyn h&igrave;nh trc tip. Sau li din vn khai mc l&agrave; m&agrave;n trng hi Hoa L, c&aacute;c m&agrave;n din t&aacute;i hin lch s trng i din ra ti kinh &ocirc; Hoa L xa nh: s kin l&ecirc;n ng&ocirc;i ho&agrave;ng  ca inh B Lnh v&agrave; L&ecirc; Ho&agrave;n; &aacute;nh thng gic Tng; di &ocirc; v Thng Long v&agrave; kt th&uacute;c l&agrave; m&agrave;n th rng bay l&ecirc;n cho ti ht bui s&aacute;ng khai mc.</p>', 'Wikipedia', '', '17:01:33', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (54, 22, 0, ' Lt', '', 'da-lat', '<p>&agrave; Lt l&agrave; mt th&agrave;nh ph trc thuc tnh v&agrave; tnh l tnh L&acirc;m ng, nm tr&ecirc;n cao nguy&ecirc;n L&acirc;m Vi&ecirc;n,   cao 1.500 m so vi mt nc bin v&agrave; din t&iacute;ch t nhi&ecirc;n: 393,29 km&sup2;.</p>', '<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cbde84daa7_crop.JPG" alt="" /></p>\r\n<p style="text-align: center;"><em>H Tuyn L&acirc;m</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">&agrave; Lt c Th tng ch&iacute;nh ph Vit Nam c&ocirc;ng nhn l&agrave; &ocirc; th loi 1 v&agrave;o ng&agrave;y 24 th&aacute;ng 3 nm 2009. &acirc;y l&agrave; mt trong 6 &ocirc; th loi 1 thuc tnh.<br /><br />Vi nhiu cnh quan p, &agrave; Lt l&agrave; mt trong nhng th&agrave;nh ph du lch ni ting nht ca Vit Nam. Trong thi Ph&aacute;p thuc, t&ecirc;n ting Latin Dat Aliis Laetitiam Aliis Temperiem c&oacute; ngha l&agrave; "cho nhng ngi n&agrave;y nim vui, cho nhng ngi kh&aacute;c s m&aacute;t m"[2]. &agrave; Lt c mnh danh l&agrave;: th&agrave;nh ph hoa, th&agrave;nh ph t&igrave;nh y&ecirc;u, th&agrave;nh ph m&ugrave;a xu&acirc;n, th&agrave;nh ph sng m&ugrave;.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cbde95d244_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em><small>Ch &agrave; Lt<br /></small></em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">Th&agrave;nh ph &agrave; Lt nm trong cao nguy&ecirc;n Lang Biang, ph&iacute;a Bc tnh L&acirc;m ng, V ph&iacute;a Bc, &agrave; Lt gi&aacute;p vi huyn Lc Dng, v ph&iacute;a &ocirc;ng v&agrave; &ocirc;ng Nam gi&aacute;p vi huyn n Dng, v ph&iacute;a T&acirc;y v&agrave; T&acirc;y Nam gi&aacute;p vi hai huyn L&acirc;m H&agrave; v&agrave; c Trng.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cbde738539_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Ch &agrave; Lt v &ecirc;m<br /></em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">H&agrave;ng trm nm trc &acirc;y, &agrave; Lt l&agrave; a b&agrave;n c tr&uacute; ca ngi Lch, vn l&agrave; c d&acirc;n ca to&agrave;n b cao nguy&ecirc;n Lang Biang. &agrave; Lt c&oacute; din t&iacute;ch hn 400 km&sup2;, bao bc bi c&aacute;c nh n&uacute;i cao v&agrave; d&atilde;y n&uacute;i li&ecirc;n tip:<br /> <br /> &nbsp;&nbsp;&nbsp; * Ph&iacute;a Bc v&agrave; T&acirc;y Bc gii hn bi d&atilde;y Chormui, Y a Myut (1.816 m), T&acirc;y Bc da v&agrave;o ch&acirc;n d&atilde;y n&uacute;i Ch Yang Kae (1.921 m), thuc qun sn Lang Biang m&agrave; nh cao nht l&agrave; Ch Yang Sinh (1.408 m).<br /> &nbsp;&nbsp;&nbsp; * Ph&iacute;a &ocirc;ng l&agrave; ch&acirc;n d&atilde;y n&uacute;i Bi Doup (2.278 m) dc xung cao nguy&ecirc;n Dran.<br /> &nbsp;&nbsp;&nbsp; * Ph&iacute;a &ocirc;ng Nam chn bi d&atilde;y Cho Proline (1.629 m).<br /> &nbsp;&nbsp;&nbsp; * Ph&iacute;a Nam v&agrave; T&acirc;y Nam c&oacute; d&atilde;y n&uacute;i Voi (1.754 m) v&agrave; Y&agrave;ng Sreng bao bc.</p>', '', '', '15:37:17', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (55, 12, 20, 'Bnh trng', '', 'banh-trung', '<p>B&aacute;nh chng l&agrave; mt loi b&aacute;nh truyn thng ca d&acirc;n tc Vit nhm th hin l&ograve;ng bit n ca con ch&aacute;u i vi cha &ocirc;ng v&agrave; t tri x s. Nguy&ecirc;n liu l&agrave;m b&aacute;nh bao gm go np, u xanh, tht ln, l&aacute; dong v&agrave; b&aacute;nh thng c l&agrave;m v&agrave;o c&aacute;c dp Tt c truyn ca d&acirc;n tc Vit, cng nh ng&agrave;y gi t H&ugrave;ng Vng) (m&ugrave;ng 10 th&aacute;ng 3 &acirc;m lch).</p>', '<h2><span id="S.E1.BB.B1_t.C3.ADch" class="mw-headline">S t&iacute;ch</span></h2>\r\n<p style="text-align: justify;">L&agrave; loi b&aacute;nh duy nht c&oacute; lch s l&acirc;u i trong m thc truyn thng Vit Nam c&ograve;n c s s&aacute;ch nhc li, b&aacute;nh chng c&oacute; v tr&iacute; c bit trong t&acirc;m thc ca cng ng ngi Vit v&agrave; ngun gc ca n&oacute; c&oacute; th truy nguy&ecirc;n v truyn thuyt li&ecirc;n quan n ho&agrave;ng t Lang Li&ecirc;u v&agrave;o i vua H&ugrave;ng th 6. S t&iacute;ch tr&ecirc;n mun. nhc nh con ch&aacute;u v truyn thng ca d&acirc;n tc; l&agrave; li gii th&iacute;ch &yacute; ngha cng nh ngun ci ca ca B&aacute;nh Chng, B&aacute;nh Giy trong vn h&oacute;a, ng thi nhn mnh tm quan trng ca c&acirc;y l&uacute;a v&agrave; thi&ecirc;n nhi&ecirc;n trong nn vn ho&aacute; l&uacute;a nc.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cac4720ab1_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;">Mt chic b&aacute;nh chng vu&ocirc;ng v&agrave; mt chic b&aacute;nh chng t&agrave;y va c g&oacute;i</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Quan_ni.E1.BB.87m_truy.E1.BB.81n_th.E1.BB.91ng" class="mw-headline">Quan nim truyn thng</span></h2>\r\n<p style="text-align: justify;">Theo quan nim ph bin hin nay, c&ugrave;ng vi b&aacute;nh giy, b&aacute;nh chng tng trng cho quan nim v v tr ca ngi Vit xa. B&aacute;nh c&oacute; m&agrave;u xanh l&aacute; c&acirc;y, h&igrave;nh vu&ocirc;ng, c coi l&agrave; c trng cho t trong t&iacute;n ngng ca ngi Vit c v&agrave; c&aacute;c d&acirc;n tc kh&aacute;c trong khu vc ch&acirc;u &Aacute;. Tuy nhi&ecirc;n, theo Gi&aacute;o s Trn Quc Vng, b&aacute;nh chng nguy&ecirc;n thy c&oacute; h&igrave;nh tr&ograve;n v&agrave; d&agrave;i, ging nh b&aacute;nh t&eacute;t, [ng thi b&aacute;nh chng v&agrave; b&aacute;nh giy tng trng cho nam v&agrave; n trong t&iacute;n ngng phn thc Vit Nam. B&aacute;nh t&eacute;t, thay th v tr&iacute; ca b&aacute;nh chng v&agrave;o c&aacute;c dp Tt trong cng ng ngi Vit  min nam Vit Nam, theo Trn Quc Vng l&agrave; dng nguy&ecirc;n thy ca b&aacute;nh chng.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cac46114ad_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;">B&aacute;nh chng v&agrave; gi&ograve; la</p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: justify;">G&oacute;i v&agrave; nu b&aacute;nh chng, ngi canh ni b&aacute;nh chng tr&ecirc;n bp la &atilde; tr th&agrave;nh mt tp qu&aacute;n, vn h&oacute;a sng trong c&aacute;c gia &igrave;nh ngi Vit mi dp tt n xu&acirc;n v.</p>', 'Wikipedia', '', '15:39:08', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (56, 12, 20, 'Bnh trng', '', 'banh-trung', '<p>B&aacute;nh chng l&agrave; mt loi b&aacute;nh truyn thng ca d&acirc;n tc Vit nhm th hin l&ograve;ng bit n ca con ch&aacute;u i vi cha &ocirc;ng v&agrave; t tri x s. Nguy&ecirc;n liu l&agrave;m b&aacute;nh bao gm go np, u xanh, tht ln, l&aacute; dong v&agrave; b&aacute;nh thng c l&agrave;m v&agrave;o c&aacute;c dp Tt c truyn ca d&acirc;n tc Vit, cng nh ng&agrave;y gi t H&ugrave;ng Vng) (m&ugrave;ng 10 th&aacute;ng 3 &acirc;m lch).</p>', '<h2><span id="S.E1.BB.B1_t.C3.ADch" class="mw-headline">S t&iacute;ch</span></h2>\r\n<p style="text-align: justify;">L&agrave; loi b&aacute;nh duy nht c&oacute; lch s l&acirc;u i trong m thc truyn thng Vit Nam c&ograve;n c s s&aacute;ch nhc li, b&aacute;nh chng c&oacute; v tr&iacute; c bit trong t&acirc;m thc ca cng ng ngi Vit v&agrave; ngun gc ca n&oacute; c&oacute; th truy nguy&ecirc;n v truyn thuyt li&ecirc;n quan n ho&agrave;ng t Lang Li&ecirc;u v&agrave;o i vua H&ugrave;ng th 6. S t&iacute;ch tr&ecirc;n mun. nhc nh con ch&aacute;u v truyn thng ca d&acirc;n tc; l&agrave; li gii th&iacute;ch &yacute; ngha cng nh ngun ci ca ca B&aacute;nh Chng, B&aacute;nh Giy trong vn h&oacute;a, ng thi nhn mnh tm quan trng ca c&acirc;y l&uacute;a v&agrave; thi&ecirc;n nhi&ecirc;n trong nn vn ho&aacute; l&uacute;a nc.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cac4720ab1_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;">Mt chic b&aacute;nh chng vu&ocirc;ng v&agrave; mt chic b&aacute;nh chng t&agrave;y va c g&oacute;i</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Quan_ni.E1.BB.87m_truy.E1.BB.81n_th.E1.BB.91ng" class="mw-headline">Quan nim truyn thng</span></h2>\r\n<p style="text-align: justify;">Theo quan nim ph bin hin nay, c&ugrave;ng vi b&aacute;nh giy, b&aacute;nh chng tng trng cho quan nim v v tr ca ngi Vit xa. B&aacute;nh c&oacute; m&agrave;u xanh l&aacute; c&acirc;y, h&igrave;nh vu&ocirc;ng, c coi l&agrave; c trng cho t trong t&iacute;n ngng ca ngi Vit c v&agrave; c&aacute;c d&acirc;n tc kh&aacute;c trong khu vc ch&acirc;u &Aacute;. Tuy nhi&ecirc;n, theo Gi&aacute;o s Trn Quc Vng, b&aacute;nh chng nguy&ecirc;n thy c&oacute; h&igrave;nh tr&ograve;n v&agrave; d&agrave;i, ging nh b&aacute;nh t&eacute;t, [ng thi b&aacute;nh chng v&agrave; b&aacute;nh giy tng trng cho nam v&agrave; n trong t&iacute;n ngng phn thc Vit Nam. B&aacute;nh t&eacute;t, thay th v tr&iacute; ca b&aacute;nh chng v&agrave;o c&aacute;c dp Tt trong cng ng ngi Vit  min nam Vit Nam, theo Trn Quc Vng l&agrave; dng nguy&ecirc;n thy ca b&aacute;nh chng.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cac46114ad_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;">B&aacute;nh chng v&agrave; gi&ograve; la</p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: justify;">G&oacute;i v&agrave; nu b&aacute;nh chng, ngi canh ni b&aacute;nh chng tr&ecirc;n bp la &atilde; tr th&agrave;nh mt tp qu&aacute;n, vn h&oacute;a sng trong c&aacute;c gia &igrave;nh ngi Vit mi dp tt n xu&acirc;n v.</p>', 'Wikipedia', '', '15:39:08', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (57, 22, 13, ' Lt', '', 'da-lat', '<p>&agrave; Lt l&agrave; mt th&agrave;nh ph trc thuc tnh v&agrave; tnh l tnh L&acirc;m ng, nm tr&ecirc;n cao nguy&ecirc;n L&acirc;m Vi&ecirc;n,   cao 1.500 m so vi mt nc bin v&agrave; din t&iacute;ch t nhi&ecirc;n: 393,29 km&sup2;.</p>', '<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cbde84daa7_crop.JPG" alt="" /></p>\r\n<p style="text-align: center;"><em>H Tuyn L&acirc;m</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">&agrave; Lt c Th tng ch&iacute;nh ph Vit Nam c&ocirc;ng nhn l&agrave; &ocirc; th loi 1 v&agrave;o ng&agrave;y 24 th&aacute;ng 3 nm 2009. &acirc;y l&agrave; mt trong 6 &ocirc; th loi 1 thuc tnh.<br /><br />Vi nhiu cnh quan p, &agrave; Lt l&agrave; mt trong nhng th&agrave;nh ph du lch ni ting nht ca Vit Nam. Trong thi Ph&aacute;p thuc, t&ecirc;n ting Latin Dat Aliis Laetitiam Aliis Temperiem c&oacute; ngha l&agrave; "cho nhng ngi n&agrave;y nim vui, cho nhng ngi kh&aacute;c s m&aacute;t m"[2]. &agrave; Lt c mnh danh l&agrave;: th&agrave;nh ph hoa, th&agrave;nh ph t&igrave;nh y&ecirc;u, th&agrave;nh ph m&ugrave;a xu&acirc;n, th&agrave;nh ph sng m&ugrave;.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cbde95d244_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em><small>Ch &agrave; Lt<br /></small></em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">Th&agrave;nh ph &agrave; Lt nm trong cao nguy&ecirc;n Lang Biang, ph&iacute;a Bc tnh L&acirc;m ng, V ph&iacute;a Bc, &agrave; Lt gi&aacute;p vi huyn Lc Dng, v ph&iacute;a &ocirc;ng v&agrave; &ocirc;ng Nam gi&aacute;p vi huyn n Dng, v ph&iacute;a T&acirc;y v&agrave; T&acirc;y Nam gi&aacute;p vi hai huyn L&acirc;m H&agrave; v&agrave; c Trng.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cbde738539_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Ch &agrave; Lt v &ecirc;m<br /></em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">H&agrave;ng trm nm trc &acirc;y, &agrave; Lt l&agrave; a b&agrave;n c tr&uacute; ca ngi Lch, vn l&agrave; c d&acirc;n ca to&agrave;n b cao nguy&ecirc;n Lang Biang. &agrave; Lt c&oacute; din t&iacute;ch hn 400 km&sup2;, bao bc bi c&aacute;c nh n&uacute;i cao v&agrave; d&atilde;y n&uacute;i li&ecirc;n tip:<br /> <br /> &nbsp;&nbsp;&nbsp; * Ph&iacute;a Bc v&agrave; T&acirc;y Bc gii hn bi d&atilde;y Chormui, Y a Myut (1.816 m), T&acirc;y Bc da v&agrave;o ch&acirc;n d&atilde;y n&uacute;i Ch Yang Kae (1.921 m), thuc qun sn Lang Biang m&agrave; nh cao nht l&agrave; Ch Yang Sinh (1.408 m).<br /> &nbsp;&nbsp;&nbsp; * Ph&iacute;a &ocirc;ng l&agrave; ch&acirc;n d&atilde;y n&uacute;i Bi Doup (2.278 m) dc xung cao nguy&ecirc;n Dran.<br /> &nbsp;&nbsp;&nbsp; * Ph&iacute;a &ocirc;ng Nam chn bi d&atilde;y Cho Proline (1.629 m).<br /> &nbsp;&nbsp;&nbsp; * Ph&iacute;a Nam v&agrave; T&acirc;y Nam c&oacute; d&atilde;y n&uacute;i Voi (1.754 m) v&agrave; Y&agrave;ng Sreng bao bc.</p>', '', '', '15:39:23', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (58, 19, 13, 'Nha Trang', '', 'nha-trang', '<p style="text-align: justify;">Nha Trang l&agrave; mt th&agrave;nh ph ven bin v&agrave; l&agrave; trung t&acirc;m ch&iacute;nh tr, kinh t, vn h&oacute;a, khoa hc k thut v&agrave; du lch ca tnh Kh&aacute;nh H&ograve;a, Vit Nam.</p>', '<p style="text-align: justify;">Trc khi tr th&agrave;nh phn t ca Vit Nam, Nha Trang thuc v Chi&ecirc;m Th&agrave;nh. C&aacute;c di t&iacute;ch ca ngi Chm vn c&ograve;n ti nhiu ni  Nha Trang. Nha Trang c Th tng ch&iacute;nh ph Vit Nam c&ocirc;ng nhn l&agrave; &ocirc; th loi 1 v&agrave;o ng&agrave;y 22 th&aacute;ng 4 nm 2009. &acirc;y l&agrave; mt trong 6 &ocirc; th loi 1 trc thuc tnh ca Vit Nam<br /><br />C&aacute;c b&atilde;i bin p ca th&agrave;nh ph n&agrave;y &atilde; bin n&oacute; th&agrave;nh mt danh lam thng cnh. Ni &acirc;y cng c bit n nh mt a im l&yacute; tng  t chc c&aacute;c s kin ln nh Festival Bin (Nha Trang), hay c&aacute;c cuc thi sc p ln nh Hoa hu Vit Nam, Hoa hu Th gii ngi Vit 2007 , Hoa hu Ho&agrave;n v 2008, Hoa hu Tr&aacute;i t 2010, Hoa hu Th gii 2010...</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cba2d1b84a_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><small>B&atilde;i bin Nha Trang</small></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">Th&agrave;nh ph Nha Trang hin nay c&oacute; din t&iacute;ch t nhi&ecirc;n l&agrave; 251 km&sup2;, d&acirc;n s 361.454 (2009). Ph&iacute;a Bc gi&aacute;p huyn Ninh H&ograve;a, ph&iacute;a Nam gi&aacute;p huyn Cam L&acirc;m, ph&iacute;a T&acirc;y gi&aacute;p huyn Di&ecirc;n Kh&aacute;nh, ph&iacute;a &ocirc;ng gi&aacute;p Bin &ocirc;ng.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cba2c0dd5e_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>ng ven bin</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<h2>ng bng Di&ecirc;n Kh&aacute;nh - Nha Trang</h2>\r\n<p style="text-align: justify;"><br />Nha Trang nm  ph&iacute;a &ocirc;ng ng bng Di&ecirc;n Kh&aacute;nh - Nha Trang. Mt ng bng c bi lp bi s&ocirc;ng C&aacute;i Nha Trang c&oacute; din t&iacute;ch gn 300 km&sup2;, a h&igrave;nh &ograve;ng bng b ph&acirc;n h&oacute;a mnh:<br /><br />&nbsp;&nbsp;&nbsp; * phn ph&iacute;a T&acirc;y dc s&ocirc;ng Ch&ograve; t Kh&aacute;nh B&igrave;nh n Di&ecirc;n ng b b&oacute;c m&ograve;n,  cao tuyt i khong 10-20 m<br />&nbsp;&nbsp;&nbsp; * phn ph&iacute;a &ocirc;ng l&agrave; a h&igrave;nh t&iacute;ch t  cao tuyt i di 10m, b mt a h&igrave;nh b ph&acirc;n ct mnh bi c&aacute;c d&ograve;ng chy.</p>', '', '', '15:39:41', 1, 'admin');
