INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (73, 19, 13, 'Nha Trang', '', 'nha-trang', '<p style="text-align: justify;">Nha Trang l&agrave; mt th&agrave;nh ph ven bin v&agrave; l&agrave; trung t&acirc;m ch&iacute;nh tr, kinh t, vn h&oacute;a, khoa hc k thut v&agrave; du lch ca tnh Kh&aacute;nh H&ograve;a, Vit Nam.</p>', '<p style="text-align: justify;">Trc khi tr th&agrave;nh phn t ca Vit Nam, Nha Trang thuc v Chi&ecirc;m Th&agrave;nh. C&aacute;c di t&iacute;ch ca ngi Chm vn c&ograve;n ti nhiu ni  Nha Trang. Nha Trang c Th tng ch&iacute;nh ph Vit Nam c&ocirc;ng nhn l&agrave; &ocirc; th loi 1 v&agrave;o ng&agrave;y 22 th&aacute;ng 4 nm 2009. &acirc;y l&agrave; mt trong 6 &ocirc; th loi 1 trc thuc tnh ca Vit Nam<br /><br />C&aacute;c b&atilde;i bin p ca th&agrave;nh ph n&agrave;y &atilde; bin n&oacute; th&agrave;nh mt danh lam thng cnh. Ni &acirc;y cng c bit n nh mt a im l&yacute; tng  t chc c&aacute;c s kin ln nh Festival Bin (Nha Trang), hay c&aacute;c cuc thi sc p ln nh Hoa hu Vit Nam, Hoa hu Th gii ngi Vit 2007 , Hoa hu Ho&agrave;n v 2008, Hoa hu Tr&aacute;i t 2010, Hoa hu Th gii 2010...</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cba2d1b84a_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><small>B&atilde;i bin Nha Trang</small></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">Th&agrave;nh ph Nha Trang hin nay c&oacute; din t&iacute;ch t nhi&ecirc;n l&agrave; 251 km&sup2;, d&acirc;n s 361.454 (2009). Ph&iacute;a Bc gi&aacute;p huyn Ninh H&ograve;a, ph&iacute;a Nam gi&aacute;p huyn Cam L&acirc;m, ph&iacute;a T&acirc;y gi&aacute;p huyn Di&ecirc;n Kh&aacute;nh, ph&iacute;a &ocirc;ng gi&aacute;p Bin &ocirc;ng.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p>&nbs