INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (82, 24, 0, 'Bnh cun', '', 'banh-cuon', '<p>B&aacute;nh cun hay b&aacute;nh t l&agrave; loi b&aacute;nh l&agrave;m t bt go hp tr&aacute;ng mng,  n khi c&ograve;n t, trong c&oacute; th cun nh&acirc;n hoc kh&ocirc;ng nh&acirc;n (kh&aacute;c vi b&aacute;nh tr&aacute;ng, (b&aacute;nh a) phi phi kh&ocirc;; hay b&aacute;nh t  min Nam. B&aacute;nh thng n vi mt loi nc chm pha nht t nc mm v&agrave; nu l&agrave; b&aacute;nh cun truyn thng th&igrave; kh&ocirc;ng th thiu tinh du c&agrave; cung pha trong nc chm khi n thng k&egrave;m th&ecirc;m ch ln.</p>', '<h2>C&aacute;ch l&agrave;m</h2>\r\n<p style="text-align: justify;">Go ngon, xay mn, h&ograve;a vi nc. t ni hp, cng vi mng tr&ecirc;n ming ni. Mi ln cho mt mu&ocirc;i bt nh. xoa u l&ecirc;n b mt ming vi  l&aacute; b&aacute;nh cun c mng, c&oacute; th thoa th&ecirc;m ch&uacute;t m  d ly ra.<br /><br />Sau khi b&aacute;nh ch&iacute;n, d&ugrave;ng a to hoc thanh tre gt ra a, l&uacute;c n&agrave;y c&oacute; th cun th&ecirc;m nh&acirc;n gm mt &iacute;t tht vai na nc na m, t&ocirc;m, bm c&ugrave;ng mc nh, nm hng &atilde; x&agrave;o ch&iacute;n vi c&aacute;c gia v nh mm, ht ti&ecirc;u... Rc th&ecirc;m h&agrave;nh kh&ocirc; phi thm v&agrave; d&ugrave;ng vi nc chm  c&aacute;c v chua, cay, mn, ngt.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: justify;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cc37ba9053_small.jpg" alt="" width="207" height="155" /> <img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cc37d048e1_small.JPG" alt="" width="207" height="155" /> <img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cc37e5c955_small.jpg" alt="" width="238" height="154" /></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2>B&aacute;nh cun Thanh Tr&igrave;</h2>\r\n<p style="text-align: justify;">B&aacute;nh cun Thanh Tr&igrave; l&agrave; m&oacute;n n ni ting ca ngi H&agrave; Ni, l&agrave; c sn ca phng Thanh Tr&igrave;, Qun Ho&agrave;ng Mai, H&agrave; Ni. B&aacute;nh c l&agrave;m t go gi&eacute; c&aacute;nh, t&aacute;m thm, tr&aacute;ng mng nh t giy.<br /><br />B&aacute;nh cun Thanh Tr&igrave; kh&ocirc;ng c&oacute; nh&acirc;n, thng c xp th&agrave;nh tng lp trong l&ograve;ng mt chic th&uacute;ng, tr&ecirc;n ph t l&aacute; sen hay l&aacute; chui, l&aacute; dong. Ngi b&aacute;n b&aacute;nh thng i th&uacute;ng b&aacute;nh tr&ecirc;n u, di do b&aacute;n tr&ecirc;n ph phng H&agrave; Ni. Khi gp ngi mua, ngi b&aacute;n h&agrave;ng s h th&uacute;ng xung, ln gi tng lp b&aacute;nh cun mng, t&aacute;ch tng lp b&aacute;nh sao l&aacute; b&aacute;nh cun khi b r&aacute;ch. Tr&ecirc;n mt l&aacute; b&aacute;nh cun im nhng cng h&agrave;nh l&aacute; m&agrave;u v&agrave;ng, n&acirc;u &atilde; c phi qua tr&ecirc;n cho. Mi lp b&aacute;nh cun &oacute; s c sp xp gn li tr&ecirc;n a, ln lt tng ming b&aacute;nh mt c t cnh nhau. Sau &oacute;, vi mt nh&aacute;t k&eacute;o, tt c c&aacute;c l&aacute; b&aacute;nh cun c ct &ocirc;i. C&ocirc;ng vic tip theo l&agrave; nhc mt na u b&aacute;nh cun mi ct &oacute;, t l&ecirc;n tr&ecirc;n na kia  ngi thng thc c&oacute; th nh&igrave;n thy r&otilde; tng lp b&aacute;nh cun tr&aacute;ng mng nh giy. B&aacute;nh c n vi th nc chm c trng cho tng ngi b&aacute;n b&aacute;nh ri&ecirc;ng, c&oacute; th n k&egrave;m ch qu, gi&ograve; la hoc u r&aacute;n v&agrave; rau m&ugrave;i.</p>', 'Wikipedia', '', '16:08:32', 1, 'admin');
