INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (86, 26, 18, 'Ph&nbsp;', '', 'pho', '<p style="text-align: justify;">Ph l&agrave; mt m&oacute;n n truyn thng ca Vit Nam, cng c&oacute; th xem l&agrave; mt trong nhng m&oacute;n n c trng nht cho m thc Vit Nam.</p>', '<p style="text-align: justify;">Th&agrave;nh phn ch&iacute;nh ca ph l&agrave; b&aacute;nh ph v&agrave; nc d&ugrave;ng (hay nc l&egrave;o theo c&aacute;ch gi min Nam) c&ugrave;ng vi tht b&ograve; hoc g&agrave; ct l&aacute;t mng. Ngo&agrave;i ra c&ograve;n k&egrave;m theo c&aacute;c gia v nh: tng, ti&ecirc;u, chanh, nc mm, t... Nhng gia v n&agrave;y c th&ecirc;m v&agrave;o t&ugrave;y theo khu v ca tng ngi d&ugrave;ng. Ph th&ocirc;ng thng d&ugrave;ng l&agrave;m m&oacute;n n im t&acirc;m, hoc n ti.  c&aacute;c tnh ph&iacute;a Nam Vit Nam, ph c b&agrave;y bin vi nhng th&agrave;nh phn ph gi l&agrave; rau thm nh h&agrave;nh, gi&aacute; v&agrave; nhng l&aacute; c&acirc;y rau m&ugrave;i, rau h&uacute;ng, trong &oacute; ng&ograve; gai l&agrave; loi l&aacute; c trng ca ph. Ph thng l&agrave; ph b&ograve;, nhng cng c&oacute; ph g&agrave;, ph heo, ph t&ocirc;m ...</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cc79c58b72_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Mt b&aacute;t ph<br /></em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p>Nc d&ugrave;ng n&oacute;i chung c l&agrave;m bng vic hm xng b&ograve;, tht d&ugrave;ng cho m&oacute;n ph l&agrave; tht b&ograve; hoc g&agrave; v&agrave; gia v bao gm qu, hi, gng, tho qu, inh hng, ht m&ugrave;i... "B&aacute;nh ph", theo truyn thng, c l&agrave;m t bt go, tr&aacute;ng th&agrave;nh tm mng ri ct th&agrave;nh si.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cc79d3600b_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>a rau i k&egrave;m ph</em><em>.</em></p>', 'Wikipedia', '', '16:21:44', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (87, 27, 0, 'Hng Temple', '', 'hung-temple', '<p style="text-align: justify;">H&ugrave;ng temple, locating in Ngha Lnh mountain, C T&iacute;ch village, Hy Cng commune, Phong Ch&acirc;u district, Ph&uacute; Th province, is the most sacred Vietnamese national historic and cultural complex site.</p>', '<p style="text-align: justify;">H&ugrave;ng temple''s the place of worship H&ugrave;ng Vng- the first King of Vietnam and the first son of Lc Long Qu&acirc;n and &Acirc;u C. H&ugrave;ng temple''s also the place where was the center of the first capital of Vietnam- Phong Ch&acirc;u, according to ancient tales. It is the symbol of glorious &ocirc;ng Sn culture. This temple is the most significant political and social institution of Vietnam. For thousands of year, all Vietnamese dynasties and governments have always regarded it as "Nam Vit Triu T" (place of Vietnamese millionth-generation ancestor). All Vietnamese people are proud of it and remember to celebrate Gi T H&ugrave;ng Vng every 10th day of 3rd lunar month here.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2>Locating</h2>\r\n<p style="text-align: justify;">It told that once upon a time, H&ugrave;ng Vng The First was looking for a place to set up the capital. The King had gone to 99 places but he hadn''t been satisfactory. One day, he gone to Hy Cng village. Immediately, his horse whinny resoundingly. He stopped and gone to the highest mountain- Ngha Lnh, saw four directions. At last, he decided to choose this place.<br /><br />Ph&uacute; Th is fat and green midland area. In Fengshui opinion, Phong Ch&acirc;u is the land of "sn chu thy t" (place where mountains and rivers gather), "long chu h phc" (place where dragons and tigers attend upon), "phng bay nga chy" (place where phoenixes fly and horses run) and the land where''s abundant in "kh&iacute; thi&ecirc;ng s&ocirc;ng n&uacute;i" (sacred country''s qi- flow of energy). This place is full of obstacles and difficult of access that''s easy to protect, but also easy of access to welcome people come from other places. This is the confluence of 3 rivers: &agrave; river, red water- Thao river and clear blue water- L&ocirc; river. In front of Ngha Lnh mountain, it''s Vit Tr&igrave; three- way crossroads where there''re tens of turtle- shape low hill direct to H&ugrave;ng temple. Behind Ngha Lnh mountain, it''s Hy Cng village form a letter- bringing phoenix shape. At the right of this mountain, there''s Khang Ph hill like an attending tiger. At the left of this mountain, there''s An Th&aacute;i hill like an arrow- shooting army general for protecting this temple. At the foot of the mountain, there''s C T&iacute;ch village on the horse- shape hill and Thm Th&igrave;nh village where villagers stay awake throung a night to pound rice in order that Lang Li&ecirc;u make b&aacute;nh chng and b&aacute;nh d&agrave;y. From Thm Th&igrave;nh to Ph&uacute; Lc, there''re hills which''re incarnation of 100 elephants of which 99 elephants direct to H&ugrave;ng temple but 1 elephant didn''t. That''s why the God cut off its head and the hill of this elephant still overflow blood nowadays. Ngha Lnh mountain- 175m high like head of a dragon direct to the South and the body of this dragon are Trc mountain, Vn mountain and Pheo mountain. Ngha Lnh mountain, Vn mountain and Trc mountain''s regarded as "Tam Sn Cm a" (3 forbidden mountain).</p>', 'Wikipedia', '', '16:27:03', 1, 'admin');
