INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (91, 28, 21, 'L hi n Hng', '', 'le-hoi-den-hung', '<p style="text-align: justify;">H&ugrave;ng temple, locating in Ngha Lnh mountain, C T&iacute;ch village, Hy Cng commune, Phong Ch&acirc;u district, Ph&uacute; Th province, is the most sacred Vietnamese national historic and cultural complex site.</p>', '<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; D&ugrave; ai i ngc v xu&ocirc;i<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Nh ng&agrave;y gi t m&ugrave;ng mi th&aacute;ng ba<br /><br />L hi n H&ugrave;ng c&ograve;n gi l&agrave; Gi t H&ugrave;ng Vng l&agrave; mt l hi ln mang tm v&oacute;c quc gia  Vit Nam, tng nh v&agrave; t l&ograve;ng bit n c&ocirc;ng lao lp nc ca c&aacute;c vua H&ugrave;ng, nhng v vua u ti&ecirc;n ca d&acirc;n tc. L hi din ra v&agrave;o ng&agrave;y 10 th&aacute;ng 3 &acirc;m lch, tuy nhi&ecirc;n, l hi thc cht &atilde; din ra t h&agrave;ng tun trc &oacute; vi nhng phong tc nh &acirc;m ung (&aacute;nh trng ng) ca d&acirc;n tc Mng, h&agrave;nh hng tng nim c&aacute;c vua H&ugrave;ng, v&agrave; kt th&uacute;c v&agrave;o ng&agrave;y 10 th&aacute;ng 3 &acirc;m lch vi l rc kiu v&agrave; d&acirc;ng hng tr&ecirc;n n Thng. L hi n H&ugrave;ng hin c Nh&agrave; nc Cng h&ograve;a x&atilde; hi ch ngha Vit Nam n&acirc;ng l&ecirc;n th&agrave;nh quc gi t chc ln v&agrave;o nhng nm chn.<br /><br />C&oacute; 2 l c c h&agrave;nh c&ugrave;ng thi im ng&agrave;y ch&iacute;nh hi:<br /><br />L rc kiu vua: &aacute;m rc kiu vi m&agrave;u sc sc s ca bt ng&agrave;n c, hoa, lng, kiu, trang phc truyn thng xut ph&aacute;t t di ch&acirc;n n&uacute;i ri ln lt qua c&aacute;c n  ti n Thng, ni l&agrave;m l d&acirc;ng hng. &aacute;m rc nh mt con rng un ln tr&ecirc;n nhng bc &aacute; di nhng t&aacute;n l&aacute; c&acirc;y  ti nh n&uacute;i Thi&ecirc;ng.<br /><br />L d&acirc;ng hng: Ngi h&agrave;nh hng ti n H&ugrave;ng ch yu v&igrave; nhu cu ca i sng t&acirc;m linh. Mi ngi u thp l&ecirc;n v&agrave;i n&eacute;n hng khi ti t T  nh l&agrave;n kh&oacute;i thm n&oacute;i h nhng iu t&acirc;m nim ca m&igrave;nh vi t ti&ecirc;n. Trong t&acirc;m hn ngi Vit th&igrave; mi nm t, gc c&acirc;y ni &acirc;y u linh thi&ecirc;ng v&agrave; chng c&oacute; g&igrave; kh&oacute; hiu khi nh&igrave;n thy nhng gc c&acirc;y, hc &aacute; cm  nhng ch&acirc;n hng.<br /><br />Phn hi c&oacute; nhiu tr&ograve; chi d&acirc;n gian c sc. &oacute; l&agrave; nhng cuc thi h&aacute;t xoan (tc h&aacute;t gho), mt h&igrave;nh thc d&acirc;n ca c bit ca Vnh Ph&uacute;, nhng cuc thi vt, thi k&eacute;o co, hay thi bi tri  ng&atilde; ba s&ocirc;ng Bch Hc, ni c&aacute;c vua H&ugrave;ng luyn tp c&aacute;c o&agrave;n thy binh luyn chin.<br /><br />T nm 2007, gi t H&ugrave;ng Vng tr th&agrave;nh quc l. Ng&agrave;y 10/3 &acirc;m lch h&agrave;ng nm l&agrave; ng&agrave;y ngh l. L&ecirc; hi n H&ugrave;ng nhng nm l s do tnh Ph&uacute; Th ng ra t chc. C&aacute;c nm chn s c&oacute; quy m&ocirc;  c&aacute;c cp ln hn. L hi n H&ugrave;ng kh&ocirc;ng ch din ra  khu di t&iacute;ch lch s n H&ugrave;ng Ph&uacute; Th m&agrave; s din ra  nhiu a phng trong c nc nh th&agrave;nh ph H Ch&iacute; Minh, Cn Th, &agrave; Nng .v.v.<br /><br />Theo ngh nh 82/2001/N-CP v vic quy c l hi n H&ugrave;ng th&igrave;:<br /><br />&nbsp;&nbsp;&nbsp; * "Nm chn" l&agrave; s nm k nim c&oacute; ch s cui c&ugrave;ng l&agrave; "0"; B Vn ho&aacute; - Th&ocirc;ng tin v&agrave; y ban Nh&acirc;n d&acirc;n tnh Ph&uacute; Th t chc L hi; mi i din l&atilde;nh o ng, Nh&agrave; nc, Quc hi, Ch&iacute;nh ph, y ban Trung ng Mt trn T quc Vit Nam v&agrave; c&aacute;c o&agrave;n th d l d&acirc;ng hng.<br />&nbsp;&nbsp;&nbsp; * "Nm tr&ograve;n" l&agrave; s nm k nim c&oacute; ch s cui c&ugrave;ng l&agrave; "5 "; y ban Nh&acirc;n d&acirc;n tnh Ph&uacute; Th t chc l hi; mi i din l&atilde;nh o ng, Nh&agrave; nc, Quc hi, Ch&iacute;nh ph, y ban Trung ng Mt trn T quc Vit Nam v&agrave; c&aacute;c o&agrave;n th d l d&acirc;ng hng.<br />&nbsp;&nbsp;&nbsp; * "Nm l" l&agrave; s nm k nim c&oacute; c&aacute;c ch s cui c&ugrave;ng c&ograve;n li. y ban Nh&acirc;n d&acirc;n tnh Ph&uacute; Th t chc l hi; mi l&atilde;nh o B Vn ho&aacute; - Th&ocirc;ng tin d l d&acirc;ng hng v&agrave; t chc c&aacute;c hot ng trong l hi.<br /><br /></p>', 'Wikipedia', '', '16:32:10', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (92, 29, 21, 'L hi c  Hoa L', '', 'le-hoi-co-do-hoa-lu', '<p style="text-align: justify;">L hi c &ocirc; l&agrave; mt l hi truyn thng c m  suy t&ocirc;n c&ocirc;ng lao c&aacute;c anh h&ugrave;ng d&acirc;n tc &atilde; x&acirc;y dng kinh &ocirc; Hoa L, lp ra nh&agrave; nc i C Vit th k X m&agrave; ti&ecirc;u biu l&agrave; hai v vua inh Ti&ecirc;n Ho&agrave;ng v&agrave; L&ecirc; i H&agrave;nh.</p>', '<p style="text-align: justify;">L hi din ra ti qung trng trung t&acirc;m khu di t&iacute;ch c &ocirc; Hoa L v&agrave; c&aacute;c di t&iacute;ch. &acirc;y l&agrave; mt l hi c truyn hng v ci ngun dng nc v&agrave; gi nc ca d&acirc;n tc Vit Nam. L hi c&oacute; lch s t khi kinh &ocirc; Hoa L tr th&agrave;nh c &ocirc;. L hi c&ograve;n c&oacute; t&ecirc;n l&agrave; l hi c lau v&igrave; c&oacute; m&agrave;n din t&aacute;i hin cnh inh B Lnh vi tr&ograve; chi "C lau tp trn". L hi truyn thng c &ocirc; Hoa L c&oacute; quy m&ocirc; ln nht v&agrave;o nhng nm c&oacute; s h&agrave;ng n v l&agrave; 3, 5, 8, 0. L hi c m v&agrave;o m&ugrave;a xu&acirc;n, c&oacute; th v&agrave;o ng&agrave;y sinh ca inh B Lnh (15/2 &acirc;m lch), hoc u th&aacute;ng 3 bt u t 6/3 n 10/3 &acirc;m lch (Tng truyn, ng&agrave;y 10/3 l&agrave; ng&agrave;y inh B Lnh l&ecirc;n ng&ocirc;i Ho&agrave;ng , ng&agrave;y 8/3 l&agrave; ng&agrave;y mt ca vua L&ecirc; i H&agrave;nh).[28] Nhc n l hi Trng Y&ecirc;n d&acirc;n gian c&oacute; c&acirc;u: [29] "Ai l&agrave; con ch&aacute;u rng ti&ecirc;n - Th&aacute;ng hai m hi Trng Y&ecirc;n th&igrave; v".<br /><br />L rc nc: M u l&agrave; l rc nc, o&agrave;n ngi khi h&agrave;nh t n vua inh t 5-6 gi s&aacute;ng, n b&ecirc;n s&ocirc;ng Ho&agrave;ng Long th&igrave; dng li ly nc v&agrave;o ch&eacute; em v n. Cuc rc c chun b kh&aacute; c&ocirc;ng phu. Trc ng&agrave;y khai hi,  s&ocirc;ng Ho&agrave;ng Long chn mt c&acirc;y tre ln. Tr&ecirc;n ngn tre c&oacute; treo mt di phn m&agrave;u v&agrave;ng, ghi li ch&uacute;. Ni dung nhng li ch&uacute; i lc l&agrave; Thn d&acirc;n, con ch&aacute;u trm h lu&ocirc;n nh n rng v&agrave;ng  s&ocirc;ng n&agrave;y &atilde; cu gi&uacute;p v Ho&agrave;ng  nh&agrave; inh; Cu mong thn s&ocirc;ng gi cho d&ograve;ng nc m&aacute;t hin ho&agrave;, ph&ugrave; tr cho d&acirc;n tr&aacute;nh mi iu &aacute;c d&hellip;[30][31] o&agrave;n rc i theo th t dn u l&agrave; nhng ngi mang c ng sc c&aacute;c loi, i h&agrave;ng &ocirc;i. K tip l&agrave; phng nhc b&aacute;t &acirc;m, phng trng. Tip n l&agrave; mt kiu b&aacute;t cng ln c&oacute; hng &aacute;n do t&aacute;m nam thanh ni&ecirc;n trong trang phc l&iacute;nh tr&aacute;ng nh&agrave; inh xa kho mnh khi&ecirc;ng, tip theo l&agrave; c&aacute;c v quan kh&aacute;ch, i biu trung ng, a phng. Ri tip n l&agrave; nhng kiu b&aacute;t cng c&oacute; t&aacute;n do c&aacute;c thiu n mang l vt. o&agrave;n ngi i sau l&agrave; c&aacute;c b&ocirc; l&atilde;o, nhng i t n quan ca nhiu a phng xa gn, nh&acirc;n d&acirc;n v&agrave; du kh&aacute;ch.<br /><br />L t: Ngay sau khi o&agrave;n rc nc tr v s&acirc;n khu trung t&acirc;m l hi, sau nghi thc th rng bay th&igrave; khi im phn l t ti 2 n Vua inh, Vua L&ecirc;. Phn t c&oacute; s tham gia ca nhiu o&agrave;n theo lch ng k&yacute;. C&aacute;c o&agrave;n rc kiu v&agrave; ch&acirc;n nhang t c&aacute;c di t&iacute;ch th c&aacute;c danh nh&acirc;n thi inh - L&ecirc; s tham gia rc kiu v 2 n, hu ht c&aacute;c o&agrave;n  c ly xa phi rc tr&ecirc;n xe l hi tin v Hoa L. L t c tin h&agrave;nh sau &oacute; c ban ng&agrave;y v&agrave; ban &ecirc;m  c n vua inh v&agrave; vua L&ecirc; vi ni dung ca ngi c&ocirc;ng c ca hai v vua, sen k &oacute; du kh&aacute;ch v&agrave;o thp hng tng nim. Tham gia l t c&oacute; nhiu o&agrave;n trong v&ugrave;ng.<br /><br />M&agrave;n s&acirc;n khu h&oacute;a: &acirc;y l&agrave; m&agrave;n din s&acirc;n khu ng i  khai mc l hi v&agrave; truyn h&igrave;nh trc tip. Sau li din vn khai mc l&agrave; m&agrave;n trng hi Hoa L, c&aacute;c m&agrave;n din t&aacute;i hin lch s trng i din ra ti kinh &ocirc; Hoa L xa nh: s kin l&ecirc;n ng&ocirc;i ho&agrave;ng  ca inh B Lnh v&agrave; L&ecirc; Ho&agrave;n; &aacute;nh thng gic Tng; di &ocirc; v Thng Long v&agrave; kt th&uacute;c l&agrave; m&agrave;n th rng bay l&ecirc;n cho ti ht bui s&aacute;ng khai mc.</p>', 'Wikipedia', '', '16:42:26', 1, 'admin');
