INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (97, 31, 3, 'Lantern Festival', '', 'lantern-festival', '<p style="text-align: justify;">The Lantern Festival or Yuan Xiao Festival (simplified Chinese: ; traditional Chinese: ; pinyin: Yu&aacute;nxioji&eacute;) or Shang Yuan Festival (simplified Chinese: ; traditional Chinese: ; pinyin: Sh&agrave;ngyu&aacute;nji&eacute;) in China or Chap Goh Meh (Chinese: ; Peh-e-j: chap-g-m&ecirc;; literally "the fifteen night") Festival in Malaysia and Singapore or "Tt Thng Nguy&ecirc;n" or "Tt Nguy&ecirc;n Ti&ecirc;u"(Vietnamese: Tt Nguy&ecirc;n ti&ecirc;u) in Vietnam; is a festival celebrated on the fifteenth day of the first month in the lunar year in the Chinese calendar, the last day of the Lunar Chinese New Year celebration.</p>', '<p style="text-align: justify;">It is not to be confused with the Mid-Autumn Festival, which is sometimes also known as the "Lantern Festival" in locations such as Singapore and Malaysia. During the Lantern Festival, children go out at night to temples carrying paper lanterns (simplified Chinese: ; traditional Chinese: ; pinyin: t&ugrave;zidng) and solve riddles on the lanterns (simplified Chinese: ; traditional Chinese: ; pinyin: cidngm&iacute;). It officially ends the Chinese New Year celebrations.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cd59188688_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Lantern Festival celebration in Shijiazhuang.</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">In ancient times, the lanterns were fairly simple, for only the emperor and noblemen had large ornate ones; in modern times, lanterns have been embellished with many complex designs. For example, lanterns are now often made in shapes of animals.<br /><br />The Lantern Festival is also known as the Little New Year since it marks the end of the series of celebrations starting from the Chinese New Year.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cd59084397_crop.JPG" alt="" /></p>\r\n<p style="text-align: center;"><em>Year 2008 Taiwan Lantern Festival in Tainan, Taiwan.</em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: justify;">In some region and countries, this festival is also regarded as the Chinese version of St. Valentine''s Day, a day celebrating love and affection between lovers in Chinese tradition and culture.</p>', 'Wikipedia', '', '17:14:03', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (98, 32, 21, 'Tt Nguyn Tiu', '', 'tet-nguyen-tieu', '<p style="text-align: justify;">Tt Nguy&ecirc;n Ti&ecirc;u hay tt Thng Nguy&ecirc;n l&agrave; mt tt, l hi c truyn ca Trung Quc v&agrave;o ng&agrave;y 15 (ng&agrave;y rm) th&aacute;ng gi&ecirc;ng &Acirc;m lch.</p>', '<p style="text-align: justify;">Tt n&agrave;y phn ln t chc ti ch&ugrave;a, v&igrave; ng&agrave;y rm th&aacute;ng gi&ecirc;ng c&ograve;n l&agrave; ng&agrave;y v&iacute;a ca Pht t. V&agrave;o ng&agrave;y n&agrave;y, cng c&oacute; gia &igrave;nh Vit Nam t tp  nh&agrave; trng h hoc nh&agrave; th h.<br /><br />Theo s&aacute;ch Trung Hoa, l Thng Nguy&ecirc;n kh&ocirc;ng phi l&agrave; mt ng&agrave;y l Pht. Trc &acirc;y ch&iacute;nh l&agrave; Tt Trng Nguy&ecirc;n. Nh&acirc;n dp n&agrave;y, nh&agrave; vua hi hp c&aacute;c &ocirc;ng Trng  tht tic v&agrave; mi v&agrave;o vn Thng Uyn thm hoa, ngm cnh, l&agrave;m th.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cd59188688_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Hi hoa ng ti Thch Gia Trang</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<h2>Tp tc v&agrave; l hi</h2>\r\n<p style="text-align: justify;"> Trung Quc v&agrave; &agrave;i Loan hin nay, Tt Nguy&ecirc;n Ti&ecirc;u (rm th&aacute;ng gi&ecirc;ng, &ecirc;m rm u ti&ecirc;n ca nm mi) c coi l&agrave; ng&agrave;y l thi&ecirc;ng li&ecirc;ng nht u nm mi v&agrave; c&ograve;n c gi l&agrave; "L hi &egrave;n hoa" hoc "Hi hoa ng", c&oacute; th bt ngun t tc c&uacute;ng t thi H&aacute;n V , vi tp tc trng &egrave;n tr&ecirc;n c&acirc;y n&ecirc;u trc ca nh&agrave;, t &egrave;n, chi lng &egrave;n ng sc, c&oacute; th k&eacute;o t 13 n 17 th&aacute;ng gi&ecirc;ng. c y&ecirc;u chung l&agrave; nhng lng &egrave;n c&oacute; h&igrave;nh th&ugrave; rng, phng, mi hai con gi&aacute;p hoc nhng nh&acirc;n vt c trong truyn thuyt, c t&iacute;ch. Ngo&agrave;i ra c&ograve;n nhng tp tc kh&aacute;c nh : c&uacute;ng t cu an cu phc, n b&aacute;nh tr&ocirc;i (gi l&agrave; "thang vi&ecirc;n" - vi&ecirc;n tr&ograve;n trong nc), thi o&aacute;n h&igrave;nh th&ugrave; tr&ecirc;n lng &egrave;n, ng&acirc;m th. Ngi &agrave;i Loan c&ograve;n ghi nhng c&acirc;u c nguyn ca m&igrave;nh v&agrave;o &egrave;n lng v&agrave; th bay l&ecirc;n tri. Nhiu ngi c&ograve;n coi &acirc;y l&agrave; m&ugrave;a Valentine phng &ocirc;ng, tng t nh l Tht Tch. Th ng xa &atilde; vit: Nguy&ecirc;n ti&ecirc;u chi d hoa lng nguyt, m&ugrave;a trng tr&ograve;n lung linh sc m&agrave;u hoa ng rc r cng l&agrave; dp Ngu Lang Chc N hin i gp g se duy&ecirc;n.</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cd68c98531_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>L hi &egrave;n hoa nm 2007 ti Gia Ngha, &agrave;i Loan</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;"> Philippines, c&oacute; l hi diu h&agrave;nh truyn thng v&agrave;o ng&agrave;y rm th&aacute;ng gi&ecirc;ng, &aacute;nh du khi u nm mi.  Vit Nam, nhng ni c&oacute; &ocirc;ng cng ng ngi Hoa sinh sng nh Ch Ln, Hi An cng c&oacute; nhiu sinh hot c bit.</p>', 'Wikipedia', '', '17:17:34', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (99, 31, 3, 'Lantern Festival', '', 'lantern-festival', '<p style="text-align: justify;">The Lantern Festival or "Tt Thng Nguy&ecirc;n" or "Tt Nguy&ecirc;n Ti&ecirc;u"(Vietnamese: Tt Nguy&ecirc;n ti&ecirc;u) in Vietnam; is a festival celebrated on the fifteenth day of the first month in the lunar year in the Chinese calendar, the last day of the Lunar Chinese New Year celebration.</p>', '<p style="text-align: justify;">It is not to be confused with the Mid-Autumn Festival, which is sometimes also known as the "Lantern Festival" in locations such as Singapore and Malaysia. During the Lantern Festival, children go out at night to temples carrying paper lanterns and solve riddles on the lanterns. It officially ends the Chinese New Year celebrations.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cd59188688_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Lantern Festival celebration in Shijiazhuang.</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">In ancient times, the lanterns were fairly simple, for only the emperor and noblemen had large ornate ones; in modern times, lanterns have been embellished with many complex designs. For example, lanterns are now often made in shapes of animals.<br /><br />The Lantern Festival is also known as the Little New Year since it marks the end of the series of celebrations starting from the Chinese New Year.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cd59084397_crop.JPG" alt="" /></p>\r\n<p style="text-align: center;"><em>Year 2008 Taiwan Lantern Festival in Tainan, Taiwan.</em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: justify;">In some region and countries, this festival is also regarded as the Chinese version of St. Valentine''s Day, a day celebrating love and affection between lovers in Chinese tradition and culture.</p>', 'Wikipedia', '', '17:33:02', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (100, 33, 2, 'Canh chua', '', 'canh-chua', '<p>Canh chua (literally "sour soup") is a sour soup indigenous to the Mekong River region of southern Vietnam.</p>', '<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cd9c2e4dfa_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A bowl of <em>canh chua</em></em></p>\r\n<p>&nbsp;</p>\r\n<p>It is typically made with fish from the Mekong River, pineapple, tomatoes (and sometimes also other vegetables such as okra or bc h&agrave;), and bean sprouts, in a tamarind-flavored broth. It is garnished with the lemony-scented herb ng&ograve; &ocirc;m (Limnophila aromatica), caramelized garlic, and chopped scallions, as well as other herbs, according to the specific variety of canh chua; these other herbs may include rau rm (Vietnamese coriander), ng&ograve; gai (long coriander), and rau qu (Thai basil).<br /><br />The sour taste of the soup comes from tamarind, which is mixed with a small amount of hot water; the mixture is then stirred for a few moments to release all the essence, and the liquid (minus the tamarind seeds and other solids, which are discarded) is then added to the soup.<br /><br />When made in style of a hot pot, canh chua is called lu canh chua.</p>', 'Wikipedia', '', '17:33:55', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (101, 34, 17, 'Canh chua', '', 'canh-chua', '<p style="text-align: justify;">Canh chua l&agrave; t&ecirc;n gi ca nhng m&oacute;n n nhiu nc v&agrave; c&oacute; v chua do c nu bng c&aacute;c nguy&ecirc;n liu phi trn vi gia v to chua.</p>', '<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cd9c2e4dfa_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Mt b&aacute;t <em>canh chua</em></em></p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: justify;">Canh chua thng c d&ugrave;ng  nhng v&ugrave;ng n&oacute;ng nh min Nam, min Trung, hoc nhng l&uacute;c n&oacute;ng nc ca m&ugrave;a h&egrave; min Bc Vit Nam, m&oacute;n canh chua n&agrave;y cng c bit ph bin trong ngh thut m thc H&agrave; Ni.<br /><br />Nguy&ecirc;n liu ch&iacute;nh  nu canh chua l&agrave; loi rau c qu, c&aacute;c loi tht hay thy sn (c&aacute;, t&ocirc;m, cua, c, hn, trai, s&ograve;) kh&aacute;c nhau, trong &oacute; thng d&ugrave;ng mt loi rau chua hoc mt gia v chua  to v chua thm ngon cho nc canh. Rt ph bin c&aacute;c m&oacute;n canh chua s dng nguy&ecirc;n liu ch&iacute;nh l&agrave; mt loi thy sn, hi sn, do cht to chua c&oacute; t&aacute;c dng kh m&ugrave;i v tanh ca nguy&ecirc;n liu n&agrave;y. Cng kh&ocirc;ng him khi canh chua ch l&agrave; mt b&aacute;t nc luc rau cho th&ecirc;m ch&uacute;t nc ct chanh, ch&uacute;t l&aacute; me hay v&agrave;i qu su xanh.</p>\r\n<h2>c im</h2>\r\n<p style="text-align: justify;">Mt c im rt quan trng &atilde; c d&acirc;n gian &uacute;c kt bao i l&agrave;  nu ngon c&aacute;c m&oacute;n canh chua, t&ugrave;y theo nguy&ecirc;n liu ch&iacute;nh l&agrave; g&igrave;, s c s dng cht to chua v&agrave; liu lng chua kh&aacute;c nhau. C&aacute;c loi canh c&aacute; nu chua kh&ocirc;ng bao gi d&ugrave;ng qu su hay chanh m&agrave; bt buc phi d&ugrave;ng bng ru, dc, da v.v. Nc rau mung luc  ngui vt nc ct chanh, hoc cho qu su hay v&agrave;i l&aacute;t tai chua khi nc c&ograve;n n&oacute;ng. Canh ri&ecirc;u cua nu dm bng hoc qu dc nng ch&iacute;n b&oacute;c v. Canh ri&ecirc;u hn, trai, tr&ugrave;ng trc phi nu vi me. S phi trn gia v to chua v&agrave; nguy&ecirc;n liu ch&iacute;nh c&oacute; th linh ng, thng da tr&ecirc;n kinh nghim m thc ca ngi ni tr.<br /><br />Th&ecirc;m v&agrave;o &oacute;, c&aacute;c loi rau gia v (nh h&agrave;nh, th&igrave; l&agrave;, l&aacute; gc, rau rm v.v.) c gia v&agrave;o m&oacute;n canh chua phi t&ugrave;y theo loi, nh canh ri&ecirc;u cua kh&ocirc;ng th thiu h&agrave;nh, canh c&aacute; nu chua phi d&ugrave;ng th&igrave; l&agrave;, canh hn d&ugrave;ng rau rm v.v. C&aacute;c loi rau gia v kh&ocirc;ng c gia v&agrave;o ni canh trong l&uacute;c nu, thm ch&iacute; kh&ocirc;ng n&ecirc;n  di &aacute;y b&aacute;t m&uacute;c canh l&ecirc;n v&igrave; di t&aacute;c dng ca v chua rau s v&agrave;ng, tr&ocirc;ng mt ngon. Thng thng ngi ta hay m&uacute;c canh ra b&aacute;t v&agrave; rc rau gia v l&ecirc;n tr&ecirc;n.</p>', 'Wikipedia', '', '17:38:36', 1, 'admin');
