INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (104, 35, 1, 'Bnh c', '', 'banh-duc', '<p>B&aacute;nh &uacute;c is a term used to refer to two different varieties of Vietnamese b&aacute;nh, or cake.</p>', '<h2>Northern Vietnamese version</h2>\r\n<p style="text-align: justify;">In northern Vietnam, b&aacute;nh &uacute;c is a cake made from either non-glutinous rice flour or corn flour. It is white in color and has a soft texture and mild flavour.photo It is typically garnished with savory ingredients such as ground pork, t&ocirc;m chy (grilled ground shrimp),photo fried onions, sesame seeds, salt, peanuts, lime juice, and soy sauce or fish sauce.photo Although it may be eaten on its own, it may also be served hot, accompanied by steamed meat or mushrooms.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cdd066d361_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A bowl of northern Vietnamese b&aacute;nh &uacute;c tht (b&aacute;nh &uacute;c with ground pork and fish sauce)</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">B&aacute;nh &uacute;c is available at small stalls and is eaten throughout the day.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Southern_Vietnamese_version" class="mw-headline">Southern Vietnamese version</span></h2>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A plate of southern Vietnamese b&aacute;nh &uacute;c colored with Pandanus amaryllifolius leaf extract, and topped with sweetened coconut milk and sesame seeds</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">In southern Vietnam, b&aacute;nh &uacute;c is a dessert made from non-glutinous rice flour. It takes the form of gelatinous blocks that are often colored green by the addition of Pandanus amaryllifolius leaf extract. It is cooked by boiling the ingredients and allowing them to cool, solidifying into a jelly-like sheetphoto that is then cut into blocks.</p>', 'Wikipedia', '', '17:45:31', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (105, 35, 1, 'Bnh c', '', 'banh-duc', '<p>B&aacute;nh &uacute;c is a term used to refer to two different varieties of Vietnamese b&aacute;nh, or cake.</p>', '<h2>Northern Vietnamese version</h2>\r\n<p style="text-align: justify;">In northern Vietnam, b&aacute;nh &uacute;c is a cake made from either non-glutinous rice flour or corn flour. It is white in color and has a soft texture and mild flavour.photo It is typically garnished with savory ingredients such as ground pork, t&ocirc;m chy (grilled ground shrimp),photo fried onions, sesame seeds, salt, peanuts, lime juice, and soy sauce or fish sauce.photo Although it may be eaten on its own, it may also be served hot, accompanied by steamed meat or mushrooms.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cdd066d361_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A bowl of northern Vietnamese b&aacute;nh &uacute;c tht (b&aacute;nh &uacute;c with ground pork and fish sauce)</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">B&aacute;nh &uacute;c is available at small stalls and is eaten throughout the day.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Southern_Vietnamese_version" class="mw-headline">Southern Vietnamese version</span></h2>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A plate of southern Vietnamese b&aacute;nh &uacute;c colored with Pandanus amaryllifolius leaf extract, and topped with sweetened coconut milk and sesame seeds</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">In southern Vietnam, b&aacute;nh &uacute;c is a dessert made from non-glutinous rice flour. It takes the form of gelatinous blocks that are often colored green by the addition of Pandanus amaryllifolius leaf extract. It is cooked by boiling the ingredients and allowing them to cool, solidifying into a jelly-like sheetphoto that is then cut into blocks.</p>', 'Wikipedia', '', '17:46:35', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (106, 36, 17, 'Bnh c', '', 'banh-duc', '<p style="text-align: justify;">B&aacute;nh &uacute;c l&agrave; mt loi b&aacute;nh ca Vit Nam, thng c l&agrave;m bng bt go (ti min Bc v&agrave; min Trung) hoc bt nng (min Nam) vi mt s gia v. B&aacute;nh c l&agrave;m th&agrave;nh tm to, khi n th&igrave; ct nh th&agrave;nh ming t&ugrave;y th&iacute;ch.</p>', '<p style="text-align: justify;">L&agrave; m&oacute;n n d&acirc;n d&atilde; thnh h&agrave;nh khp ba min, b&aacute;nh &uacute;c n gi&ograve;n, m&aacute;t, mn, no bng m&agrave; li d ti&ecirc;u d l&agrave;m v&agrave; gi&aacute; th&agrave;nh cng rt r. Kh&ocirc;ng ch c n nh mt thc qu&agrave; qu&ecirc;, ba n s&aacute;ng m&agrave; in h&igrave;nh l&agrave; b&aacute;nh &uacute;c chm tng, b&aacute;nh &uacute;c cng c&oacute; th n k&egrave;m vi canh ri&ecirc;u cua, rau thm, mm t&ocirc;m, mt ong, mt m&iacute;a, mt tr&aacute;i c&acirc;y v&agrave; thm ch&iacute; c c&aacute; kho, tht kho t&ugrave;y th&iacute;ch. T loi b&aacute;nh &uacute;c thun t&uacute;y ch c bng bt go pha vi nc v&ocirc;i trong ngai ng&aacute;i m&ugrave;i v&ocirc;i, b&aacute;nh &uacute;c cng &atilde; c&oacute; nhiu bin tu nh b&aacute;nh &uacute;c cm thch, b&aacute;nh &uacute;c lc, b&aacute;nh &uacute;c da, b&aacute;nh &uacute;c ng&ocirc; v.v.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cdd066d361_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>B&aacute;nh &uacute;c tht kiu min Bc</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">B&aacute;nh &uacute;c is available at small stalls and is eaten throughout the day.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Quy_tr.C3.ACnh_th.E1.BB.B1c_hi.E1.BB.87n" class="mw-headline">Quy tr&igrave;nh thc hin</span></h2>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>B&aacute;nh &uacute;c kiu min Nam vi nc ct da, m&egrave;, nhum m&agrave;u bng l&aacute; da thm</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">Quy tr&igrave;nh thc hin b&aacute;nh &uacute;c, d&ugrave; vi nhiu bin th, v c bn bao gm 3 c&ocirc;ng on: l&agrave;m nc ng&acirc;m go, chun b bt v&agrave; un b&aacute;nh. Di &acirc;y ch tr&igrave;nh b&agrave;y m&oacute;n b&aacute;nh &uacute;c bt go, l&agrave; m&oacute;n c bn v&agrave; ph bin nht trong h b&aacute;nh &uacute;c.<br /><br />Nc ng&acirc;m go c l&agrave;m bng nc v&ocirc;i trong hoc nc tro. V&ocirc;i t&ocirc;i (loi v&ocirc;i mm nhuyn  trn va x&acirc;y nh&agrave;, v&agrave; c&aacute;c c  Vit Nam ng&agrave;y xa thng d&ugrave;ng n tru) c pha vi nc, thng vi t l 1 l&iacute;t nc 10g v&ocirc;i. Khuy u  lng v&agrave; cht ly nc v&ocirc;i trong. Vi nc tro, ngi ta thng ly tro bp, loi tro un ci hoc l&aacute; c&acirc;y kh&ocirc;ng ln tp cht ca c&aacute;c loi nhi&ecirc;n liu kh&aacute;c, s&agrave;ng xy tht k v&agrave; h&ograve;a v&agrave;o nc ng&acirc;m go. Mt s ni cu k c&ograve;n s dng c&aacute;c loi c&agrave;nh xoan, c&agrave;nh bi, l&aacute; si, d&acirc;y tm gi v.v.  t th&agrave;nh tro v&agrave; ly tro &oacute; h&ograve;a nc.<br /><br />Chn loi go ngon, t&ugrave;y loi go s quyt nh cht lng b&aacute;nh ( dai, gi&ograve;n, n; m&agrave;u sc; hng v). Go c vo, &atilde;i sch v&agrave; ng&acirc;m trong nc v&ocirc;i trong hoc nc tro &atilde; n&oacute;i  tr&ecirc;n trong mt thi hn nht nh c&oacute; th mi ting ng h hoc v&agrave;i ng&agrave;y (t&ugrave;y a phng, t&ugrave;y loi go) cho n khi tay b&oacute;p ht go thy b ti. Sau &oacute; go c em xay ln vi nc cho ra bt nc. Bt li xay i xay li v&agrave;i ln cho n khi tht nhuyn mn v&agrave; sau &oacute; c&ograve;n c lc qua r&acirc;y cho sch tp cht.<br /><br /> to  dai gi&ograve;n ca b&aacute;nh c&oacute; khi ngi ta c&ograve;n d&ugrave;ng c h&agrave;n the h&ograve;a ln v&agrave;o bt d&ugrave; rng &acirc;y l&agrave; ph gia kh&ocirc;ng m bo v sinh an to&agrave;n thc phm.<br /><br />Ni c l&aacute;ng mt ch&uacute;t m nc  bt kh&ocirc;ng d&iacute;nh &aacute;y,  bt v&agrave;o v&agrave; bc l&ecirc;n bp un nh la, khuy tht u tay v&agrave; li&ecirc;n tc sao cho bt kh&ocirc;ng v&oacute;n, kh&ocirc;ng kh&ecirc;, kh&ocirc;ng s&aacute;t ni, cho n khi bt trong ni c st li, tr n&ecirc;n trong mt, ht mt a bt l&ecirc;n thy bt chy xung kh&ocirc;ng d&iacute;nh a, th&igrave; tt bp. Rt thng thy b&aacute;nh &uacute;c bt go n&agrave;y c&ograve;n kt hp vi lc nh&acirc;n luc ch&iacute;n b&oacute;c v v&agrave; cm da xt vn. Khi &oacute; lc v&agrave; da c th&ecirc;m v&agrave;o ni bt trc thi im nhc ni xung.<br /><br />D&ugrave;ng du n, nc m ln hoc ch&uacute;t &oacute;c ln t&aacute;n nhuyn thoa u l&ecirc;n l&ograve;ng mt c&aacute;i a to, hoc c&aacute;c khu&ocirc;n b&aacute;nh.  bt v&agrave;o vi  d&agrave;y khong 3-5cm.  ngui th&agrave;nh phm s &ocirc;ng cng li v&agrave; c&oacute; th xt ming  n k&egrave;m c&aacute;c thc phm kh&aacute;c (tng, mt, c&aacute; kho, canh cua, u ph r&aacute;n, tht ln luc v.v.).</p>', 'Wikipedia', '', '17:49:50', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (107, 35, 2, 'Bnh c', '', 'banh-duc', '<p>B&aacute;nh &uacute;c is a term used to refer to two different varieties of Vietnamese b&aacute;nh, or cake.</p>', '<h2>Northern Vietnamese version</h2>\r\n<p style="text-align: justify;">In northern Vietnam, b&aacute;nh &uacute;c is a cake made from either non-glutinous rice flour or corn flour. It is white in color and has a soft texture and mild flavour.photo It is typically garnished with savory ingredients such as ground pork, t&ocirc;m chy (grilled ground shrimp),photo fried onions, sesame seeds, salt, peanuts, lime juice, and soy sauce or fish sauce.photo Although it may be eaten on its own, it may also be served hot, accompanied by steamed meat or mushrooms.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cdd066d361_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A bowl of northern Vietnamese b&aacute;nh &uacute;c tht (b&aacute;nh &uacute;c with ground pork and fish sauce)</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">B&aacute;nh &uacute;c is available at small stalls and is eaten throughout the day.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Southern_Vietnamese_version" class="mw-headline">Southern Vietnamese version</span></h2>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A plate of southern Vietnamese b&aacute;nh &uacute;c colored with Pandanus amaryllifolius leaf extract, and topped with sweetened coconut milk and sesame seeds</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">In southern Vietnam, b&aacute;nh &uacute;c is a dessert made from non-glutinous rice flour. It takes the form of gelatinous blocks that are often colored green by the addition of Pandanus amaryllifolius leaf extract. It is cooked by boiling the ingredients and allowing them to cool, solidifying into a jelly-like sheetphoto that is then cut into blocks.</p>', 'Wikipedia', '', '17:51:45', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (108, 35, 2, 'Bnh c', '', 'banh-duc', '<p>B&aacute;nh &uacute;c is a term used to refer to two different varieties of Vietnamese b&aacute;nh, or cake.</p>', '<h2>Northern Vietnamese version</h2>\r\n<p style="text-align: justify;">In northern Vietnam, b&aacute;nh &uacute;c is a cake made from either non-glutinous rice flour or corn flour. It is white in color and has a soft texture and mild flavour.photo It is typically garnished with savory ingredients such as ground pork, t&ocirc;m chy (grilled ground shrimp),photo fried onions, sesame seeds, salt, peanuts, lime juice, and soy sauce or fish sauce.photo Although it may be eaten on its own, it may also be served hot, accompanied by steamed meat or mushrooms.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cdd066d361_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A bowl of northern Vietnamese b&aacute;nh &uacute;c tht (b&aacute;nh &uacute;c with ground pork and fish sauce)</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">B&aacute;nh &uacute;c is available at small stalls and is eaten throughout the day.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Southern_Vietnamese_version" class="mw-headline">Southern Vietnamese version</span></h2>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A plate of southern Vietnamese b&aacute;nh &uacute;c colored with Pandanus amaryllifolius leaf extract, and topped with sweetened coconut milk and sesame seeds</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">In southern Vietnam, b&aacute;nh &uacute;c is a dessert made from non-glutinous rice flour. It takes the form of gelatinous blocks that are often colored green by the addition of Pandanus amaryllifolius leaf extract. It is cooked by boiling the ingredients and allowing them to cool, solidifying into a jelly-like sheetphoto that is then cut into blocks.</p>', 'Wikipedia', '', '17:51:45', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (109, 37, 6, 'Bn Thnh Market', '', 'ben-thanh-market', '<p style="text-align: justify;">Ben Thanh Market (from Vietnamese Ben meaning "wharf", and Quy Thanh meaning "turtle citadel") is a big marketplace in the downtown area of Ho Chi Minh City, Vietnam, in District 1. The market is one of the earliest surviving structures in Saigon and today is considered one of symbols of Ho Chi Minh City, popular with tourists seeking local handicrafts, textiles, &aacute;o d&agrave;i, and souvenirs, as well as local cuisine.</p>', '<h2 style="text-align: justify;"><span id="History" class="mw-headline">History</span></h2>\r\n<p style="text-align: justify;">The market developed from informal markets created by early 17th century street vendors gathering together near the Saigon River. The market was formally established by the French colonial powers after taking over the Gia Dinh citadel in 1859 (see Citadel of Saigon). This market was destroyed by fire in 1870 and rebuilt to become Saigon''s largest market. In 1912 the market was moved to a new building and called the New Ben Thanh Market to distinguish over its predecessor. The building was renovated in 1985.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce02254cc8_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Bn Th&agrave;nh Market</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2 style="text-align: justify;"><span id="Transport" class="mw-headline">Transport</span></h2>\r\n<p style="text-align: justify;">Apart from being a major hub for the network of city buses serving Ho Chi Minh City, Ben Thanh Market is planned to be a hub for several lines of the planned Ho Chi Minh City Metro; Line 1, already under construction, would connect Ben Thanh with Suoi Tien Park and Long Binh in District 9. Other lines would connect Ben Thanh with Tham Luong in District 12, and with Cholon and Binh Tan District.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce021481af_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Ben Thanh Market</em></p>', 'Wikipedia', '', '17:58:41', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (110, 38, 12, 'Mid-Autumn Festival', '', 'mid-autumn-festival', '<p>The Mid-Autumn Festival, also known as the Moon Festival, Zhongqiu Festival, or in Chinese, Zhongqiujie, or in Vietnamese "Tt Trung Thu", is a popular harvest festival celebrated by Chinese and Vietnamese people, dating back over 3,000 years to moon worship in China''s Shang Dynasty.</p>', '<p>The Mid-Autumn Festival is held on the 15th day of the eighth month in the Chinese calendar, which is usually around late September or early October in the Gregorian calendar. It is a date that parallels the autumnal equinox of the solar calendar, when the moon is supposedly at its fullest and roundest. The traditional food of this festival is the mooncake, of which there are many different varieties.</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce033ae1f6_medium.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Mid-Autumn Festival at the Botanical Garden, Montreal</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p>The Mid-Autumn Festival is one of the few most important holidays in the Chinese calendar, the others being Chinese New Year and Winter Solstice, and is a legal holiday in several countries. Farmers celebrate the end of the summer harvesting season on this date. Traditionally on this day, Chinese family members and friends will gather to admire the bright mid-autumn harvest moon, and eat moon cakes and pomelos under the moon together.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce032b90af_medium.JPG" alt="" /></p>\r\n<p style="text-align: center;"><em>Mid-Autumn Festival celebrations in Victoria Park, Hong Kong</em></p>', 'Wikipedia', '', '18:00:14', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (111, 39, 16, 'Ch Bn Thnh', '', 'cho-ben-thanh', '<p style="text-align: justify;">Ch Bn Th&agrave;nh l&agrave; mt trong nhng a im ti&ecirc;u biu ca Th&agrave;nh ph H Ch&iacute; Minh. Trong nhiu trng hp, h&igrave;nh nh khu ca nam ng&ocirc;i ch n&agrave;y c xem l&agrave; biu tng ca th&agrave;nh ph.</p>', '<h2><span id="Ngu.E1.BB.93n_g.E1.BB.91c_v.C3.A0_xu.E1.BA.A5t_x.E1.BB.A9_t.C3.AAn_g.E1.BB.8Di" class="mw-headline">Ngun gc v&agrave; xut x t&ecirc;n gi</span></h2>\r\n<p style="text-align: justify;">Nguy&ecirc;n thy, ch Bn Th&agrave;nh &atilde; c&oacute; t trc khi ngi Ph&aacute;p x&acirc;m chim Gia nh. Ban u, v tr&iacute; ca ch nm b&ecirc;n b s&ocirc;ng Bn Ngh&eacute;, cnh mt bn s&ocirc;ng gn th&agrave;nh Gia nh (by gi l&agrave; th&agrave;nh Quy, c&ograve;n gi l&agrave; th&agrave;nh B&aacute;t Qu&aacute;i). Bn n&agrave;y d&ugrave;ng  cho h&agrave;nh kh&aacute;ch v&atilde;ng lai v&agrave; qu&acirc;n nh&acirc;n v&agrave;o th&agrave;nh, v&igrave; vy mi c&oacute; t&ecirc;n gi l&agrave; Bn Th&agrave;nh, v&agrave; khu ch cng c&oacute; t&ecirc;n gi l&agrave; ch Bn Th&agrave;nh.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce02254cc8_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;">Cng ch&iacute;nh v&agrave;o dp Tt 2003</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Ch.E1.BB.A3_M.E1.BB.9Bi" class="mw-headline">Ch Mi</span></h2>\r\n<p style="text-align: justify;">Khu vc x&acirc;y ch, vn l&agrave; mt c&aacute;i ao s&igrave;nh ly c, gi l&agrave; ao B Rt (Marais Boresse), c ngi Ph&aacute;p cho lp i. Khu&ocirc;n vi&ecirc;n ch quy hoch bn mt bi bn con ng. Mt tin l&agrave; Place Cuniac, t&ecirc;n t theo vi&ecirc;n X&atilde; T&acirc;y (y vi&ecirc;n Hi ng) Cuniac, ngi &atilde;  ra c&ocirc;ng vic lp ao. Ngi Vit th&igrave; quen gi &oacute; l&agrave; B&ugrave;ng binh Ch Bn Th&agrave;nh cho d&ugrave; t&ecirc;n ch&iacute;nh thc &atilde; tng i l&agrave; "C&ocirc;ng trng Cng H&ograve;a", "C&ocirc;ng trng Di&ecirc;n Hng", ri "Qung trng Qu&aacute;ch Th Trang". Mt bc ch l&agrave; Rue d''Espagne, ph&iacute;a &ocirc;ng l&agrave; rue Vi&eacute;not, v&agrave; ph&iacute;a t&acirc;y l&agrave; rue Schroeder. Nm 1955 thi  nht Cng h&ograve;a Vit Nam, bn con ng n&agrave;y i t&ecirc;n th&agrave;nh ng L&ecirc; Th&aacute;nh T&ocirc;n, ng Phan Bi Ch&acirc;u v&agrave; ng Phan Ch&acirc;u Trinh.<br /><br />Ng&ocirc;i ch mi do h&atilde;ng thu Brossard et Maupin khi c&ocirc;ng x&acirc;y dng t nm 1912 n cui th&aacute;ng 3 nm 1914 th&igrave; ho&agrave;n tt. L n mng ch kh&aacute;nh th&agrave;nh c b&aacute;o ch&iacute; thi &oacute; gi l&agrave; "T&acirc;n Vng Hi", do c din ra trong ba ng&agrave;y 28, 29 v&agrave; 30 th&aacute;ng 3 nm 1914, vi ph&aacute;o b&ocirc;ng, xe hoa v&agrave; hn 100.000 ngi tham d, k c t c&aacute;c tnh  v.<br /><br />Khu ch mi n&agrave;y vn mang t&ecirc;n gi Bn Th&agrave;nh; tuy nhi&ecirc;n cho n trc nm 1975 t&ecirc;n gi ch Bn Th&agrave;nh n&agrave;y thng ch hin din trong s&aacute;ch v, c&ograve;n ngi d&acirc;n th&igrave; thng gi l&agrave; ch S&agrave;i G&ograve;n hay ch Mi,  ph&acirc;n bit ch C ti im c, vn ch c&ograve;n li gian h&agrave;ng tht. Phn c&ograve;n li b ph&aacute; i v&agrave; c ngi Ph&aacute;p x&acirc;y dng th&agrave;nh c quan Ng&acirc;n kh. Hai con ng b&ecirc;n h&ocirc;ng ch m&atilde;i n nm 1940 c&ograve;n l&agrave; bn xe &ograve; min &ocirc;ng v&agrave; min T&acirc;y. V sau, bn xe n&agrave;y mi c di i ch kh&aacute;c.<br /><br />Ch Bn Th&agrave;nh hot ng li&ecirc;n tc trong 70 nm. T ng&agrave;y 1 th&aacute;ng 7 n 15 th&aacute;ng 8 nm 1985, ch Bn Th&agrave;nh c ci to v&agrave; sa cha ln.<br /><br />&nbsp;&nbsp;&nbsp; * a ch: Ca Nam (nm gia c&aacute;c ng Phan Bi Ch&acirc;u - Phan Chu Trinh - L&ecirc; Th&aacute;nh T&ocirc;n - C&ocirc;ng trng Qu&aacute;ch Th Trang) - Phng Bn Th&agrave;nh - Qun 1<br />&nbsp;&nbsp;&nbsp; * in thoi: +84.8.8292096<br />&nbsp;&nbsp;&nbsp; * Din t&iacute;ch: 13.056 m&sup2;<br />&nbsp;&nbsp;&nbsp; * Ng&agrave;nh h&agrave;ng kinh doanh ch yu: Qun &aacute;o, vi si, giy d&eacute;p, thi trang, h&agrave;ng th c&ocirc;ng m ngh, thc phm ti sng, tr&aacute;i c&acirc;y, hoa ti.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce021481af_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Ch Bn Th&agrave;nh<br /></em></p>', 'Wikipedia', '', '18:01:51', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (112, 40, 24, 'Tt Trung thu', '', 'tet-trung-thu', '<p>Tt Trung Thu theo &acirc;m lch l&agrave; ng&agrave;y rm th&aacute;ng 8 hng nm. &acirc;y l&agrave; ng&agrave;y tt ca tr em, c&ograve;n c gi l&agrave; "Tt tr&ocirc;ng Trng" hay Tt Thiu nhi, Tt Nhi ng.</p>', '<p>Tr em rt mong i c &oacute;n tt n&agrave;y v&igrave; thng c ngi ln tng  chi, thng l&agrave; &egrave;n &ocirc;ng sao, mt n, &egrave;n k&eacute;o qu&acirc;n, s&uacute;ng phun nc... ri b&aacute;nh nng, b&aacute;nh do. V&agrave;o ng&agrave;y tt n&agrave;y, ngi ta t chc b&agrave;y c, tr&ocirc;ng trng. Thi im trng l&ecirc;n cao, tr em s va m&uacute;a h&aacute;t va ngm trng ph&aacute; c.  mt s ni ngi ta c&ograve;n t chc m&uacute;a l&acirc;n, m&uacute;a s t, m&uacute;a rng  c&aacute;c em vui chi tho th&iacute;ch.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce110b10d9_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>B&aacute;nh nng ng&agrave;y tt Trung thu</em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p>Tt Trung Thu c&oacute; ngun gc vn minh n&ocirc;ng nghip ca d&acirc;n tc Vit. Thi im n&agrave;y kh&iacute; tri m&aacute;t m, m&ugrave;a m&agrave;ng ang ch thu hoch. N&ocirc;ng d&acirc;n ngh ngi v&agrave; vui chi sau mt v m&ugrave;a. Sau n&agrave;y ngi Trung Hoa tip nhn t vn h&oacute;a Vit v&agrave; tip &oacute; ph bin th&agrave;nh ng&agrave;y l truyn thng ca d&acirc;n tc &ocirc;ng &Aacute; nh H&agrave;n Quc, Nht Bn.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce10ebf455_crop.JPG" alt="" /></p>\r\n<p style="text-align: center;"><em>C&aacute;c loi &egrave;n lng</em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p>Theo phong tc ngi Vit, v&agrave;o dp Tt Trung Thu, cha m b&agrave;y c cho c&aacute;c con  mng trung thu, mua v&agrave; l&agrave;m  th lng &egrave;n thp bng nn  treo trong nh&agrave; v&agrave;  c&aacute;c con rc &egrave;n. C mng trung thu gm b&aacute;nh Trung Thu, ko, m&iacute;a, bi v&agrave; c&aacute;c th hoa qu kh&aacute;c na. &acirc;y l&agrave; dp  cha m t&ugrave;y theo kh nng kinh t gia &igrave;nh th hin t&igrave;nh thng y&ecirc;u con c&aacute;i mt c&aacute;ch c th. V&igrave; th, t&igrave;nh y&ecirc;u gia &igrave;nh li c&agrave;ng khng kh&iacute;t th&ecirc;m. Cng trong dp n&agrave;y ngi ta mua b&aacute;nh trung thu, tr&agrave;, ru  c&uacute;ng t ti&ecirc;n, biu &ocirc;ng b&agrave;, cha m, thy c&ocirc;, bn b&egrave;, h h&agrave;ng v&agrave; c&aacute;c &acirc;n nh&acirc;n kh&aacute;c.</p>', 'Wikipedia', '', '18:03:29', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (113, 41, 8, 'Cm tm', '', 'com-tam', '<p style="text-align: justify;">Cm tm in Vietnamese is cooked rice from fractured rice grains. Tm refers to the broken rice grains while cm refers to cooked rice.<br /><br /></p>', '<p style="text-align: justify;">It is usually served with grilled pork (either ribs or shredded) plus a Vietnamese dish called b&igrave; (thinly shredded pork mixed with cooked and thinly shredded pork skin) over broken rice. The rice and meat are served with various greens and pickled vegetables, along with a prawn paste cake, trng hp (baked egg), and grilled prawns. Typically restaurants will serve this popular combination rice plate with a small bowl of nc chm, as well as a small bowl of soup broth (canh) with garlic chives (to cleanse the throat).</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce1fb72ae2_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Cm tm with grilled pork, shredded pork, pork skin, fried egg, pork meatloaf, pickled carrot, and sliced cucumber</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Broken_Rice" class="mw-headline">Broken Rice</span></h2>\r\n<p style="text-align: justify;">Some rice grains break in the transport and processing of the rice from the field to the pot. Machinery is available to separate the broken grains from the whole grains. Broken rice may or may not have lower fiber and nutrient content, but generally has a similar energy content to intact rice.<br /><br />The broken varieties are often less expensive, so are preferred by poorer consumers or used as raw material (such as in beer brewing). ue to the different size and shape of the grains, broken rice has a different texture from "unbroken" rice. Some chefs and consumers may prefer the qualities of broken rice for certain dishes.</p>', 'Wikipedia', '', '18:07:28', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (114, 42, 19, 'Cm tm', '', 'com-tam', '<p style="text-align: justify;">Cm tm l&agrave; m&oacute;n c sn ca min Nam Vit Nam, n&oacute; l&agrave; mt trong nhng m&oacute;n n s&aacute;ng c a chung nht ca ngi min Nam, nht l&agrave; S&agrave;i G&ograve;n. Hin nay loi cm l&agrave;m t ht go b n&agrave;y &atilde; c&oacute; mt  mt s ni thuc min Trung, min Bc v&agrave; hi ngoi.</p>', '<p style="text-align: justify;">Da cm tm vi sn, b&igrave;, ch, trng thng c d&ugrave;ng l&agrave;m qu&agrave; s&aacute;ng, nhng nay cm tm &atilde; c&oacute;  mt s qu&aacute;n n tra, chiu hoc ti vi nhiu loi thc n k&egrave;m.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce1fb72ae2_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Mt da cm tm vi y  sn, b&igrave;, ch v&agrave; trng p-la</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Th.C3.A0nh_ph.E1.BA.A7n" class="mw-headline">Th&agrave;nh phn</span></h2>\r\n<p style="text-align: justify;"><strong>- Cm</strong><br /><br />Cm tm c nu t ht go tm, tc ht go b b. Go n&agrave;y xa l&agrave; loi go th phm, rt v&atilde;i sau khi s&agrave;ng, thng d&ugrave;ng cho g&agrave; n hoc cho ngi n l&uacute;c qu&aacute; t&uacute;ng thiu. Nay th&igrave; cm tm &atilde; th&agrave;nh m&oacute;n n quen thuc do &oacute; go tm cng c n&acirc;ng gi&aacute; cao hn.</p>\r\n<p style="text-align: justify;"><br />- <strong>Nc mm</strong><br /><br />n cm tm phi c&oacute; nc mm ngt, l&agrave; nc mm pha vi nc lc v&agrave; th&ecirc;m ng. T&ugrave;y theo c&aacute;ch ch bin v&agrave; khu v ca ngi n, nc mm c&oacute; th ngt nhiu hoc ngt &iacute;t (mn), c&oacute; th th&ecirc;m chanh.</p>\r\n<p style="text-align: justify;"><br />- <strong>M&oacute;n n k&egrave;m</strong><br /><br />Cm tm c&oacute; th n k&egrave;m vi nhiu th, nhng nhiu nht l&agrave; Sn nng, ch trng, trng p-la v&agrave; b&igrave;.</p>\r\n<p style="text-align: justify;"><br />- <strong>Sn, b&igrave;, ch, trng</strong><br /><br />Mt da cm tm c&oacute; th gm c sn, b&igrave;, ch, trng hoc kh&ocirc;ng gm y  c&aacute;c m&oacute;n tr&ecirc;n, nhng &acirc;y l&agrave; 4 thc n th&ocirc;ng dng nht v&agrave; ngi ta thng xp t&ecirc;n ch&uacute;ng b&ecirc;n nhau tr&ecirc;n c&aacute;c bin hiu cm tm:<br /><br />&nbsp;&nbsp;&nbsp; * Sn: Sn n vi cm tm l&agrave; loi sn heo c tm p gia v chua ngt, sau &oacute; em nng. Thng c&aacute;c qu&aacute;n cm tm nng sn ngay trc ca tim, kh&oacute;i hng bc ra mang theo m&ugrave;i v c trng ca sn nng v&agrave; nhiu ngi nhn ra ngay l&agrave; h va i ngang qua qu&aacute;n cm tm.<br />&nbsp;&nbsp;&nbsp; * Ch: Gi l&agrave; ch hoc ch trng, c l&agrave;m t trng, cua, tht xay, nm m&egrave;o v&agrave; b&uacute;n t&agrave;u. Ch trng c chng sn th&agrave;nh mt c&aacute;i b&aacute;nh h&igrave;nh tr&ograve;n hoc ch nht, khi n xt l&aacute;t.<br />&nbsp;&nbsp;&nbsp; * Trng: Trng  &acirc;y thng l&agrave; trng g&agrave; p la.<br />&nbsp;&nbsp;&nbsp; * B&igrave;: l&agrave; hn hp nhiu th, thng gm tht heo ct si, da heo ct si trn vi th&iacute;nh v&agrave; gia v.<br /><br />- <strong>M h&agrave;nh</strong><br /><br />M h&agrave;nh cng rt quan trng trong da cm tm. N&oacute; l&agrave; hn hp lng c l&agrave;m t h&agrave;nh l&aacute;, phi du hoc m, &ocirc;i khi trn vi t&oacute;p m chi&ecirc;n. M h&agrave;nh gi&uacute;p cm tm c&oacute;  b&eacute;o c trng, tuy nhi&ecirc;n mt s ngi kh&ocirc;ng n v&igrave; nhiu l&yacute; do.</p>\r\n<p style="text-align: justify;"><br />- <strong> chua</strong><br /><br /> chua gi&uacute;p cm tm c&oacute; v chua ngt. Thng l&agrave;m t u , c&agrave; rt, c ci, da leo, &ocirc;i khi l&agrave; c&agrave; chua.</p>\r\n<p style="text-align: justify;"><br />- <strong>C&aacute;c thc kh&aacute;c</strong><br /><br />&ocirc;i khi cm tm c n vi tht kho, u h nhi tht, c&aacute; chi&ecirc;n, g&agrave;, rau,  x&agrave;o... ging nh cm go nguy&ecirc;n ht. Kiu n n&agrave;y thng thy  c&aacute;c qu&aacute;n cm tm &ecirc;m.</p>', 'Wikipedia', '', '18:12:07', 1, 'admin');
