INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (116, 39, 16, 'Ch Bn Thnh', '', 'cho-ben-thanh', '<p style="text-align: justify;">Ch Bn Th&agrave;nh l&agrave; mt trong nhng a im ti&ecirc;u biu ca Th&agrave;nh ph H Ch&iacute; Minh. Trong nhiu trng hp, h&igrave;nh nh khu ca nam ng&ocirc;i ch n&agrave;y c xem l&agrave; biu tng ca th&agrave;nh ph.</p>', '<h2><span id="Ngu.E1.BB.93n_g.E1.BB.91c_v.C3.A0_xu.E1.BA.A5t_x.E1.BB.A9_t.C3.AAn_g.E1.BB.8Di" class="mw-headline">Ngun gc v&agrave; xut x t&ecirc;n gi</span></h2>\r\n<p style="text-align: justify;">Nguy&ecirc;n thy, ch Bn Th&agrave;nh &atilde; c&oacute; t trc khi ngi Ph&aacute;p x&acirc;m chim Gia nh. Ban u, v tr&iacute; ca ch nm b&ecirc;n b s&ocirc;ng Bn Ngh&eacute;, cnh mt bn s&ocirc;ng gn th&agrave;nh Gia nh (by gi l&agrave; th&agrave;nh Quy, c&ograve;n gi l&agrave; th&agrave;nh B&aacute;t Qu&aacute;i). Bn n&agrave;y d&ugrave;ng  cho h&agrave;nh kh&aacute;ch v&atilde;ng lai v&agrave; qu&acirc;n nh&acirc;n v&agrave;o th&agrave;nh, v&igrave; vy mi c&oacute; t&ecirc;n gi l&agrave; Bn Th&agrave;nh, v&agrave; khu ch cng c&oacute; t&ecirc;n gi l&agrave; ch Bn Th&agrave;nh.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce02254cc8_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;">Cng ch&iacute;nh v&agrave;o dp Tt 2003</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Ch.E1.BB.A3_M.E1.BB.9Bi" class="mw-headline">Ch Mi</span></h2>\r\n<p style="text-align: justify;">Khu vc x&acirc;y ch, vn l&agrave; mt c&aacute;i ao s&igrave;nh ly c, gi l&agrave; ao B Rt (Marais Boresse), c ngi Ph&aacute;p cho lp i. Khu&ocirc;n vi&ecirc;n ch quy hoch bn mt bi bn con ng. Mt tin l&agrave; Place Cuniac, t&ecirc;n t theo vi&ecirc;n X&atilde; T&acirc;y (y vi&ecirc;n Hi ng) Cuniac, ngi &atilde;  ra c&ocirc;ng vic lp ao. Ngi Vit th&igrave; quen gi &oacute; l&agrave; B&ugrave;ng binh Ch Bn Th&agrave;nh cho d&ugrave; t&ecirc;n ch&iacute;nh thc &atilde; tng i l&agrave; "C&ocirc;ng trng Cng H&ograve;a", "C&ocirc;ng trng Di&ecirc;n Hng", ri "Qung trng Qu&aacute;ch Th Trang". Mt bc ch l&agrave; Rue d''Espagne, ph&iacute;a &ocirc;ng l&agrave; rue Vi&eacute;not, v&agrave; ph&iacute;a t&acirc;y l&agrave; rue Schroeder. Nm 1955 thi  nht Cng h&ograve;a Vit Nam, bn con ng n&agrave;y i t&ecirc;n th&agrave;nh ng L&ecirc; Th&aacute;nh T&ocirc;n, ng Phan Bi Ch&acirc;u v&agrave; ng Phan Ch&acirc;u Trinh.<br /><br />Ng&ocirc;i ch mi do h&atilde;ng thu Brossard et Maupin khi c&ocirc;ng x&acirc;y dng t nm 1912 n cui th&aacute;ng 3 nm 1914 th&igrave; ho&agrave;n tt. L n mng ch kh&aacute;nh th&agrave;nh c b&aacute;o ch&iacute; thi &oacute; gi l&agrave; "T&acirc;n Vng Hi", do c din ra trong ba ng&agrave;y 28, 29 v&agrave; 30 th&aacute;ng 3 nm 1914, vi ph&aacute;o b&ocirc;ng, xe hoa v&agrave; hn 100.000 ngi tham d, k c t c&aacute;c tnh  v.<br /><br />Khu ch mi n&agrave;y vn mang t&ecirc;n gi Bn Th&agrave;nh; tuy nhi&ecirc;n cho n trc nm 1975 t&ecirc;n gi ch Bn Th&agrave;nh n&agrave;y thng ch hin din trong s&aacute;ch v, c&ograve;n ngi d&acirc;n th&igrave; thng gi l&agrave; ch S&agrave;i G&ograve;n hay ch Mi,  ph&acirc;n bit ch C ti im c, vn ch c&ograve;n li gian h&agrave;ng tht. Phn c&ograve;n li b ph&aacute; i v&agrave; c ngi Ph&aacute;p x&acirc;y dng th&agrave;nh c quan Ng&acirc;n kh. Hai con ng b&ecirc;n h&ocirc;ng ch m&atilde;i n nm 1940 c&ograve;n l&agrave; bn xe &ograve; min &ocirc;ng v&agrave; min T&acirc;y. V sau, bn xe n&agrave;y mi c di i ch kh&aacute;c.<br /><br />Ch Bn Th&agrave;nh hot ng li&ecirc;n tc trong 70 nm. T ng&agrave;y 1 th&aacute;ng 7 n 15 th&aacute;ng 8 nm 1985, ch Bn Th&agrave;nh c ci to v&agrave; sa cha ln.<br /><br />&nbsp;&nbsp;&nbsp; * a ch: Ca Nam (nm gia c&aacute;c ng Phan Bi Ch&acirc;u - Phan Chu Trinh - L&ecirc; Th&aacute;nh T&ocirc;n - C&ocirc;ng trng Qu&aacute;ch Th Trang) - Phng Bn Th&agrave;nh - Qun 1<br />&nbsp;&nbsp;&nbsp; * in thoi: +84.8.8292096<br />&nbsp;&nbsp;&nbsp; * Din t&iacute;ch: 13.056 m&sup2;<br />&nbsp;&nbsp;&nbsp; * Ng&agrave;nh h&agrave;ng kinh doanh ch yu: Qun &aacute;o, vi si, giy d&eacute;p, thi trang, h&agrave;ng th c&ocirc;ng m ngh, thc phm ti sng, tr&aacute;i c&acirc;y, hoa ti.</p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7ce021481af_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Ch Bn Th&agrave;nh<br /></em></p>', 'Wikipedia', '', '18:17:33', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (117, 35, 2, 'Bnh c', '', 'banh-duc', '<p>B&aacute;nh &uacute;c is a term used to refer to two different varieties of Vietnamese b&aacute;nh, or cake.</p>', '<h2>Northern Vietnamese version</h2>\r\n<p style="text-align: justify;">In northern Vietnam, b&aacute;nh &uacute;c is a cake made from either non-glutinous rice flour or corn flour. It is white in color and has a soft texture and mild flavour.photo It is typically garnished with savory ingredients such as ground pork, t&ocirc;m chy (grilled ground shrimp),photo fried onions, sesame seeds, salt, peanuts, lime juice, and soy sauce or fish sauce.photo Although it may be eaten on its own, it may also be served hot, accompanied by steamed meat or mushrooms.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cdd066d361_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A bowl of northern Vietnamese b&aacute;nh &uacute;c tht (b&aacute;nh &uacute;c with ground pork and fish sauce)</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">B&aacute;nh &uacute;c is available at small stalls and is eaten throughout the day.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Southern_Vietnamese_version" class="mw-headline">Southern Vietnamese version</span></h2>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/tomatocms/upload/news/admin/2010/08/4c7cdc71e1d42_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A plate of southern Vietnamese b&aacute;nh &uacute;c colored with Pandanus amaryllifolius leaf extract, and topped with sweetened coconut milk and sesame seeds</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">In southern Vietnam, b&aacute;nh &uacute;c is a dessert made from non-glutinous rice flour. It takes the form of gelatinous blocks that are often colored green by the addition of Pandanus amaryllifolius leaf extract. It is cooked by boiling the ingredients and allowing them to cool, solidifying into a jelly-like sheetphoto that is then cut into blocks.</p>', 'Wikipedia', '', '18:20:23', 1, 'admin');
